insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1692768322,'上海交通大学第三学期免修大学英语的2017级学籍学生名单','各位同学： 本学期临近期末，为方便大家复习迎考，学校从2018年6月4日至2018年6月29日（除2018年6月15、16外），开放下列通宵教室供同学们自习使用： 东区：东上院1-2层所有教室；       东下院105、115  西区：中院1-2层所有教室； 在保证考试和上课使用后，同学们可以在这些教室内自习，请同学们注意劳逸结合，提高复习效率。同时，请保持室内环境干净、整洁，请勿将复习资料、贵重物品放置教室内，以免遗失，保洁人员将每天清洁教室。 另：因6月16日四六级考试， 因此，原东上、东下院、中院1-2层的自修教室将于考试当天暂时全部关闭，期间开放下院1-3层所有教室作为自习使用，开放时间2018年6月15日22:00至次日18：00。 请同学们留意以上变化。同时，提醒各位同学自习结束后勿将书籍等物品放置室内，以免遗失。  教务处教学运行管理办公室 2018年5月29日 相关附件：  ','2018-05-31 09:08','2018-05-31 09:08','','','教学信息服务网','2018-05-31 09:08:00.0');
