insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1304442729,'2018年“上海交通大学学生创业训练计划项目”公示','         上海交通大学评选2017年度教务管理工作优秀奖的通知 教务处通知[2018]29号 各学院（系）： 为进一步提升我校本科教学管理工作水平，促进工作交流，表彰工作先进，学校决定结合年度考核，评选2017年度“上海交通大学教务管理工作优秀个人”和“上海交通大学教务管理工作优秀集体”。现将有关事项通知如下： 一、评选范围 “上海交通大学教务管理工作优秀个人”的评选范围是承担本科教学管理工作一年以上的人员。“上海交通大学教务管理工作优秀集体”的评选对象是学院（系）教务办公室（本科教务管理人员不少于2人）。 医学院2017年度教务管理工作优秀个人奖评选由医学院自行组织。如进行评选，须报教务处备案。 二、评选条件 “上海交通大学教务管理工作优秀个人”评选条件： 1. 爱岗敬业，工作责任感强，认真履行岗位职责。 2. 胜任本职工作，熟悉各项教务管理的常规工作。工作有计划，能及时、出色地完成学院和教务处交给的工作，且工作中没有明显失误或违规现象。 3. 工作踏实勤奋、积极主动，细致准确、认真负责、无差错，办事效率高。 4. 服务意识强，工作中任劳任怨，积极宣传学校有关教务管理工作规范，能为教师、学生做好有关教务管理和服务工作。 5. 创新精神强，能认真总结教学与教务管理工作规律，积极主动地思考和研究工作中出现的新情况，撰写教务管理方面的研究论文；积极向教务处等有关部门提出工作建议和意见，促进学校教学管理改革。 “上海交通大学教务管理工作优秀集体”评选条件： 1. 工作人员均爱岗敬业，工作积极主动，认真负责。 2. 工作人员均能够胜任本职工作，工作细致准确无差错，完成任务好，有较强的事业心和工作责任感。 3. 办公室各岗位职责明确，工作人员均有较强的集体荣誉感，合作能力强，配合默契，整体工作气氛融洽。 4. 同事间团结友爱，互相帮助，能开展批评与自我批评，自觉维护教务管理工作集体荣誉。 5. 工作人员服务意识强，宣传教务管理工作规定，主动为教师和学生服务。 6. 工作人员能够结合工作实际开展调查研究，积极向学院领导和教务处提出建议和意见；积极撰写教务管理工作的论文，或及时认真总结教务工作经验。 三、评选程序 凡申报2017年度“教务管理工作优秀个人”的人员必须填写《上海交通大学教务管理工作优秀个人申报表》，申报“上海交通大学教务管理工作优秀集体”的单位必须填写《上海交通大学教务管理工作优秀集体申报表》（表格见附件','2018-04-25 14:47','2018-04-25 14:47','','','教学信息服务网','2018-04-25 14:47:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1469182440,'2018年本科毕业生报名学校英语水平考试认证通知','                            上海交通大学2018年本科课程教学改革研究项目结项验收通知                                                                     教务处通知〔2018〕28号 各院(系）:                我校于2016年立项61个本科课程教改项目，根据进度安排，现启动结项验收工作。本次验收工作采用专家网上评审的形式，有关事宜通知如下： 1、 验收对象         2016年经学校批准立项的校级教学改革研究项目必须参加本次结题验收。 2、材料要求： （1）《上海交通大学本科课程教学改革研究项目结题验收表》， 需项目负责人签字并经所在单位审核。 （2）《上海交通大学本科课程教学改革研究项目总结报告》，需项目负责人签字。 3、时间安排         2018年6月8日前，请各学院提交验收材料，电子版（PDF格式）发至教务处教学改革与质量办公室郝彬卉老师（邮箱：binhuihao@sjtu.edu.cn），一份纸质版（可双面打印）交至教务处教改办（新行政楼B323）          请各院（系）做好传达工作。最终将根据专家评审成绩评出优秀、合格和不合格项目。对于不合格的项目，我们将减拨经费。 附件1：《2016-2017学年本科课程教学改革研究项目公布名单》 附件2：《上海交通大学本科课程教学改革研究项目结题验收表》 附件3：《上海交通大学本科课程教学改革研究项目总结报告》                                                                                                                                                       教务处                                                                                                                                                    2018年4月 相关附件：   附件1、 附件1 2016-2017学年本科课程教学改革研究项目公布名单.doc 附件2、 附件2','2018-04-25 13:37','2018-04-25 13:37','','','教学信息服务网','2018-04-25 13:37:00.0');
