insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1577041027,'部分夏季学期课程认可通识核心课程学分的通知','上海交通大学 关于2018年度夏季学期课程抢选的通知 各位同学：  2018年度夏季学期课程海选的调整工作已经结束，学校将于4月8日下午13：00开放海选调整结果，请同学们及时登录教学信息服务网查询。  2018年度夏季学期课程抢选时间为：4月9日下午1：00--4月16日上午8：30。 1.选课门数 抢选阶段每位同学最多可选3门通识核心课程（含海选阶段被确定的），2017级同学可选一门新生研讨课（含海选阶段被确定的）。 2.特别提醒 (1)在抢选阶段，学校将于4月12日下午13:30-16:00暂时关闭选课系统，取消选课人数低于学校规定的最低开课人数的课程。16：00之后学校重新开放选课系统，请同学及时登录教学信息服务网查询和改选。 (2) 需要补修培养计划内开在19周及以后的课程，可在抢选和第三轮选课阶段通过任选课模块进入到相应年级的开课单位进行选、退课。 选课期间有任何相关问题，请致电34206405－106，或至教务处（新行政楼B112室）进行。                         教务处                    2018年4月8日 相关附件：  ','2018-04-09 17:00','2018-04-09 17:00','','','教学信息服务网','2018-04-09 17:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-632405506,'上海交通大学通识教育名家讲堂第71讲:文化史视野中的盗墓现象','上海交通大学关于“上海高校示范性全英语课程” 2018年项目申报和2015年立项项目验收结果的公示 教务处通知【2018】23号 根据《上海市教育委员会关于做好上海高校示范性全英语课程2018年项目申报和2015年立项项目验收工作的通知》（沪教委高〔2018〕22号），我校于4月上旬积极组织上海市高校示范型全英语课程的立项申报和验收评审工作。 现将评审结果进行公示，凡对其有异议者，请与教务处教学研究办公室联系。联系电话：34206415-26。 公示期自本通知发布之日起一周。                                                                                 教务处 二零一八年四月九日 上海高校示范性全英语课程2018年申报项目公示 应市教委文件要求，上海高校示范性全英语课程建设需以专业建设为目标，三年内每个学校只支持一个专业，2017年我校电子信息与电气工程学院信息工程专业入选。根据当年提交并获批的《全英语课程三年（2017－2019年）建设规划》，此次课程立项仍由电子信息与电气工程学院负责申报。拟申报课程如下： 序号 课程名称（中文） 课程名称（英文） 课程负责人 1 视频编码与通信 Video Coding and Communications 张文军 2 微波与天线、 微波技术 Microwave and Antennas、 Microwave Technology 吴林晟 3 数字信号处理 Digital Signal Processing 王军锋 上海高校示范性全英语课程2015年立项项目验收结果公示 序号 课程名称（中文） 课程名称（英文） 负责人 验收结果 1 分析力学 Analytical Mechanics 张骏 通过 2 热力学与统计物理 Thermodynamics and Statistical Physics 王顺/洪亮 通过 3 计算物理 Computational Physics 顾卫华 通过 相关附件：  ','2018-04-09 16:45','2018-04-09 16:45','','','教学信息服务网','2018-04-09 16:45:00.0');
