insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(548062876,'关于2016级学生第二轮报名第二学科的说明','       2017年12月全国大学英语四、六级考试考生须知（重点提醒） 各位考生：         根据上海市教育考试院《关于做好上海地区2017年下半年全国大学英语四、六级考试考务工作的通知》，明确规定：严禁考生将各种通讯工具（如寻呼机、移动电话等）、电子存储记忆录放设备（如mp3等）以及涂改液、修正带、草稿纸等带入考场。现重点提醒我校参加2017年12月全国大学英语四、六级考试的考生如下信息： 1.禁止携带手机等通讯设备进入考场； 2.入场时必须主动出示准考证、学生证和身份证（均原件）。证件不齐备者不得进入考场。                                                                                        教务处教学运行管理办公室                                    2017年12月12日 相关附件：  ','2017-12-05 12:39','2017-12-05 12:39','','','教学信息服务网','2017-12-05 12:39:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(896550556,'关于发布2017-2018学年秋季学期期末考试安排的通知','关于本周日（12月10日）自习教室暂时调整的通知 各位同学： 由于本周日（12月10日）学校将举行重要考试，使用东上院、东中院和东下院教室作为考场，届时将对东上院、东中院和东下院三栋教学楼进行封楼。 因此，原东上院1-2层和东下院115和105的自修教室将于考试当天暂时全部关闭，期间开放下院1-2层所有教室作为自习使用，开放时间2017年12月10日7:30-22:30。 请同学们留意以上变化。同时，提醒各位同学自习结束后勿将书籍等物品放置室内，以免遗失。                                                      教务处教学运行管理办公室                                                                 2017年12月5日 相关附件：  ','2017-12-03 10:13','2017-12-03 10:13','','','教学信息服务网','2017-12-03 10:13:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-577566258,'关于2017年12月全国大学英语四、六级考试对比考报名的通知','上海交通大学2018年致远科学营报名通知 你想一睹两院院士的大师风采，共同探讨科学世界的奥秘吗？你想与世界名奖得主坐而论道，笑谈科学前沿风云吗？你想走进国家重点实验室，亲自动手实践非凡创意吗？上海交通大学“致远科学营”将助你实现梦想！在巍巍交大的校园里，你将和名家大师一起挥斥方遒，拥有最深刻的名校体验，让源自童年的创意和设计走进现实，用两天的时间汇成你青春里最生动的片段！ 刻苦求学的你、天马行空的你、热爱科学的你、钟情创意的你、从小有着交大梦的你……上海交通大学“致远科学营”欢迎你！ 一、报名条件 被推荐的高三学生应同时满足下述条件： 1. 高中阶段获得以下奖项之一： 全国中学生奥林匹克竞赛（数学、物理、化学、信息学）省赛区一等奖（获奖名单须在教育部阳光平台公示）；青少年科技创新大赛、“明天小小科学家”全国一、二等奖（获奖名单须在教育部阳光平台公示）。 2. 高中阶段综合成绩优秀，品质优秀、社会责任感强、素质全面且具有创新潜质、较强发展后劲的优异学生。 3. 申请者必须由获得邀请的中学推荐。 二、报名方式 请申请者登录上海交通大学网上报名系统(http://202.120.1.38)进行报名（报名指南详见通知下方附件），并按要求在报名系统中提交相关材料（无需邮寄纸质材料）。 报名时间：2017年12月1日―2017年12月11日。 三、报名审核 专家组将对符合条件并按规定报名的学生材料进行评审，确定营员名单。申请学生可于2017年12月19日后登录报名系统查询初审结果，获得资格的学生可从网上打印《报到通知书》，并凭身份证和《报到通知书》报到。 四、日程安排 12月29日下午17:00前报到。 12月31日中午：闭营。 具体安排以《报到通知书》为准。 五、评奖 根据营员的入营表现择优评定优秀营员并推荐给学校招生办公室。 联系我们 邮编地址：上海市闵行区东川路800号包玉刚图书馆607，200240 联系电话：021-54745823 电子邮箱：zhiyuan@sjtu.edu.cn 上海交通大学致远学院 2017年11月 相关附件：   附件1、 附件：2018年致远科学营报名指南.pdf','2017-12-01 13:44','2017-12-01 13:44','','','教学信息服务网','2017-12-01 13:44:00.0');
