insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(739271713,'上海交通大学2016级本科生修读“第二学科学士学位“报名通知””','上海交通大学第十五期校级大学生创新实践计划项目学期检查情况通报 教务处通知〔2017〕 67号 根据《上海交通大学大学生创新实践计划项目管理办法》要求，为加强项目过程控制，学校于2017年7月25日至10月31日组织了第十五期“上海交通大学大学生创新实践计划”项目学期检查工作，分别按项目组自查、院（系）检查和学校审核三级完成。各院（系）大学生创新计划工作组按照学校要求组织中期检查答辩，并向学校提供中期检查情况汇总表。现根据各院（系）中期检查汇总表，将有关情况通报如下： 全校165个项目参加了此次学期检查，船舶海洋与建筑工程学院丁时述项目组暂缓通过；船舶海洋与建筑工程学院苏梦颖项目组，安泰经济与管理学院吴亦晗项目组中止；密西根学院周天宇项目组评定为不通过；其余161个项目均通过学期检查。希望各项目组认真汲取各方意见和建议，明确分工、加大投入，指导教师加强指导，院（系）工作组加强监督，完成项目后续工作。 教务处 2017年11月20日 附表：第十五期校级大学生创新实践计划项目学期检查情况 项目编号 项目名称 立项人 指导教师 所在院系 中期检查 结果 IPP15001 智能井道探测机器蛇 王欣然 宋元斌 船舶海洋与建筑工程学院 通过 IPP15002 桥梁裂缝光电自动巡检系统设计与应用 郑佳庆 周拥军 船舶海洋与建筑工程学院 通过 IPP15003 钢筋混凝土小型课堂实验加载装置的设计与制造 吴辰晗 宋晓冰 船舶海洋与建筑工程学院 通过 IPP15004 混凝土中骨料形态特征对离子传输影响的数值模拟研究 刘燕 刘清风 船舶海洋与建筑工程学院 通过 IPP15005 盆地地貌和沙漠气候下新疆特色清真寺结构风压风载和风场分析 苏梦颖 周岱 船舶海洋与建筑工程学院 项目中止 IPP15006 非线性波浪能发电装置的模型试验与研究 邓博贤 肖龙飞 船舶海洋与建筑工程学院 通过 IPP15007 深海采矿水力输送系统的研究与开发 吴泽仁 吕海宁 船舶海洋与建筑工程学院 通过 IPP15008 深度学习在螺旋桨识别和建模中的应用 陈影 余龙 船舶海洋与建筑工程学院 通过 IPP15009 基于多相流的管道流固耦合分析 刘经京 余龙 船舶海洋与建筑工程学院 通过 IPP15010 建筑业生产要素配置扭曲对行业TFP的影响研究 贾特 徐峰 船舶海洋与建筑工程学院 通过 IPP15011 大型轨道交通换乘枢纽站的人流安全性评价方法研究','2017-11-20 10:48','2017-11-20 10:48','','','教学信息服务网','2017-11-20 10:48:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-81391194,'第十六期“上海交通大学大学生创新实践计划”项目公示','上海交通大学创业学院第八期“宣怀”创业班 招生简章 上海交通大学创业学院成立于2010年6月12日，旨在培养全校学生创新思维和创业家精神，为有创业意愿的同学搭建创业教育和实践的平台，提供创业指导、训练和服务。创业学院建立伊始便同步设立了“宣怀班”，在这里凝聚了具有强烈创业意愿、愿以学院为依托进行创业探索与实践的在校生和校友。“宣怀班”为创业“种子选手”提供系统的创新创业课程、丰富的创业活动和实践、精彩的创业大咖交流，培养具有国际化视野，掌握创新创业理论和方法，并具有创业能力的创业者。 第八期“宣怀班”在前七期办学经验的基础上，致力于进一步为创业者打造专业性、高质量的精细化学习体系。第八期“宣怀班”培养体系设为“雄鹰计划”与“雏鹰计划”。“雄鹰计划”定位为培养创业精英，招收创新创业项目的主要创始人或合伙人；“雏鹰计划”定位为培养创新创业新力量，招收目前暂无创新创业项目但对科技创业有浓厚兴趣的同学，“雏鹰计划”培养周期内表现优秀者可自动成为下一期“雄鹰计划”学员。创业学院将为学员提供全方位的创新创业指导，助力学员实现创业梦想。 一、申请条件 1、上海交通大学在校本、硕、博学生或毕业五年内校友； 2、具有强烈创业意愿，积极参与创业实践，期待今后以创业作为生涯选择与人生梦想； “雄鹰计划”：创业项目的主要创始人或合伙人； “雏鹰计划”：对科技创业有浓厚兴趣、暂时无创业项目的同学。 二、申请流程 1、网上报名 请扫描文末二维码关注创业学院官方微信公众号“上海交大创业JIA”，回复“宣怀班”获取报名通道。通道开放时间：2017年11月20日至12月4日。 2、提交材料 “雄鹰计划”申请材料包括但不限于：个人简历、项目商业计划书（不超过1500字）以及其它能证明个人创新创业能力的材料。 “雏鹰计划”申请材料包括：个人简历、申请书（简要描述对创新创业的认识、构想及特长，不超过1500字）。 材料须于2017年11月30日下午16点前发送至chuangye@sjtu.edu.cn，邮件主题请标明“第八期宣怀班XX计划申请材料+姓名”。（注：XX计划注明为雄鹰计划/雏鹰计划） 3、面试 学院将组织评审委员会对申请材料进行综合评价确定面试名单，具体面试时间和地点另行通知。 4、录取 学院根据申请者的情况择优录取，总录取人数为40-50人。 三、培养体系 1.“雄鹰计划”：设宣怀课堂、宣怀下午茶及宣怀游学三大模块 宣怀课堂：提供初创企业','2017-11-17 14:28','2017-11-17 14:28','','','教学信息服务网','2017-11-17 14:28:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1153896158,'2017届本科毕业生英语水平考证书领取通知','上海交通大学通识教育“名家讲堂”第69讲 人脑孕育人性：从人脑探索人性的秘密 主讲嘉宾：朱锐 主办单位：上海交通大学文科与通识教育委员会  教务处 人文学院 讲座时间：2017年11月22日（周三），13：30―15：30 讲座地点：闵行校区  东中院1-108 主讲嘉宾简介： 朱锐，哲学博士，美国森林湖学院哲学系终身教授，神经学系兼职教授，亚洲研究中心主任，曾任希腊与土耳其文化中心指导教授。历任教职于德州州立大学（1997-2002）、北京大学（1991-1992）等。研究专长为心灵哲学、中西比较哲学、古希腊哲学。熟知英语、古希腊语和拉丁语。在国际一流学术期刊发表论文30余篇。代表性论文有：Love as Communication: A Short, Redacted Argument from the Phaedrus；Identity, Legitimacy, and Chaste Widows；Love in the Euthyphro；Distinguishing the Public from the Private: Aristotle’s Solution to Plato’s Paradox;译著《个体的价值与命运》、《语词和对象》等。 讲座内容简介： 神经科学的发展，逐渐从哲学上改变人的深度自我认识。传统哲学对理性主义的追求、对情感主义的排斥，在一些关键问题上却显示了对人性的生命基础――人脑――的无知。情感和理性是不可完全分割的――特别是在对问题的决策方面。本讲座将围绕“决策”这个概念从神经学的角度展开对人性的重新思考：包括人性的道德基础、身体对认知的意义、主体间性以及暴力倾向的神经学原理等。 相关附件：   附件1、 海报.png','2017-11-14 15:10','2017-11-14 15:10','','','教学信息服务网','2017-11-14 15:10:00.0');
