insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1551272457,'上海交通大学通识教育“名家讲堂”第67讲----文化的相遇：俄罗斯文学与中国','关于组织上海交通大学大学生创新计划项目结题验收的通知 教务处通知〔2017〕51号 各学院（系）、项目负责人： 我校第十四期校级“大学生创新实践计划”项目于2016年10月正式启动。按项目总体进程安排，已进入结题验收阶段（包括已升级为上海市级、国家级项目和前期未结题的国家级、上海市级、校级创新项目）。为规范大学生创新计划项目结题验收管理工作，现将有关事项通知如下： 一、 验收安排 项目结题验收采用答辩方式，由各院系组织实施，时间为10月30日至11月6日之间，具体答辩安排由院系安排和通知。不能按期参加结题答辩的项目组请于10月27日之前向学校提出中止或延期申请（申请表在创新网上下载）。所有项目必须在项目负责人毕业之前结题验收。 二、 结题要求 结题前，项目组必须认真填写《项目结题报告》，完成《项目研究论文》、《项目研究综述》和《项目成果表》，项目组各成员分别撰写《个人总结》、网上撰写项目日志。 1.《项目研究论文》是项目组对项目研究内容、方法及研究成果等方面的详尽论述，字数要求10000字左右。（具体格式要求请参照《创新项目学生研究论文撰写规范》）。 2.《项目研究综述》是项目组对《项目研究论文》的整合和凝炼，重点在于阐述项目创新点、特色和应用及推广前景等，字数2000字左右。 3. 《项目成果表》是项目组取得的成果统计。并请将公开发表的论文、申请的专利的复印件和项目成果实物A4打印照片等材料以及《经费使用单》作为项目结题报告附件。 4.《个人总结》（题目自拟）是项目组各成员对参与工作的总结，主要介绍个人在项目研究中承担的工作，发挥的作用，以及在能力培养和素质提高特别是在创新思维和创新实践方面的体验和收获。可配以照片记录项目研究过程中难忘的瞬间（不超过5张），字数要求2500―3000字，每个成员一份。 5.上述电子材料请项目负责人登入大学生创新实践网“提交项目结题材料”时，作为附件打包上传，并请指导教师在10月27日之前登入创新网进行评审。 三、纸质材料提交 1.结题纸制材料请于10月27日之前交各院（系）工作组相关负责老师，纸质资料包括《项目结题报告》、《项目研究论文》、《项目研究综述》、《个人总结》（每个成员一份）。 2.请项目负责人积极督促所有项目成员认真完成材料，凡未按时递交纸质、电子材料,递交材料不全或准备材料不认真的项目，取消项目组答辩资格；既不提交延期、中止申请也不参加答辩的项目，项目所有成员成绩','2017-09-18 15:29','2017-09-18 15:29','','','教学信息服务网','2017-09-18 15:29:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1661138334,'大四以上、本学期复学及结业学生特殊情况申请及毕业二专补修选课通知','关于2016年度上海交通大学 “教务管理工作优秀奖”评审结果的通知 教务处[2017]51号 各学院（系）： 为了总结教务管理工作经验，表彰先进，进一步提升本科教学管理工作水平，学校组织了2016年度“教务管理工作优秀奖”的评选。经评审委员会评比，共评选出“教务管理工作优秀集体”一等奖1个，二等奖3个；“教务管理工作优秀个人”一等奖9名，二等奖13名，三等奖35名(其中医学院、继续教育学院单独评审)。现决定给予获奖集体和个人表彰，具体名单见附件。 希望全体教务管理工作人员学习先进集体和先进个人的经验，以他们为榜样，在教务管理工作中再创佳绩。 附件：上海交通大学2016年度“教务管理工作优秀奖”获奖名单                                 上海交通大学教务处 2017年9月16日 相关附件：   附件1、 关于2016年度上海交通大学“教务管理工作优秀奖”评审结果的通知（附件）.doc','2017-09-15 14:53','2017-09-15 14:53','','','教学信息服务网','2017-09-15 14:53:00.0');
