insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1994086631,'上海交通大学第30、31期PRP项目结题验收的通知','          2017-2018学年第一学期工程训练中心实习注意事项 着装要求：长裤（建议穿军训服装），不准穿凉鞋、拖鞋、高跟鞋，女生带好帽子。 上课地点及实训项目：请登陆工程训练中心网站http://engtc.sjtu.edu.cn，在首页的“用户登录”，以学生学号为用户名和密码登陆查询学生的实习项目及其实习地点。请事先在网上查找好当天的实习项目及上课地点。 考勤制度：依据工程训练中心《教学实习手册》，迟到及早退扣当天成绩，事假及病假需到工程训练中心B楼303室出示相关证明。 联系人：陶老师、许老师 办公地点：工程训练中心B楼303室 电话：54742932-824         54742927 相关附件：  ','2017-09-08 09:23','2017-09-08 09:23','','','教学信息服务网','2017-09-08 09:23:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(634548115,'教务处注册与学务中心助管招聘公告','          2017年12月全国大学英语四六级考试报名通知        2017年12月全国大学英语四、六级考试报名工作已经开始，考试的项目包括大学英语四级和大学英语六级。具体通知如下： 一、考试时间 大学英语四级： 2017年12月16日上午9：00； 大学英语六级： 2017年12月16日下午15：00。 二、报名资格 1.上海交通大学全日制专科、本科、研究生在校生； 2.各类全日制成人高等院校本科、专科在校生； 3.大一年级春季学期考生方可首次报名CET4； 4.大学英语四级考试成绩达到425分以上（含425分）者可报考CET6； 5.从2012年6月开始，每位考生在大学阶段只允许报名三次CET6考试（包括因故或无故缺考）（低于425分除外）； 6.每次英语考试只能报一个类型，不能同时报考英语四、六级考试； 7.医学院大二（含大二年级）以上（重庆南路校区）的学生请到医学院考点报名考试。   注：一经发现不符合上述报名条件者，取消报名，报名费不退，谢谢配合。 三、报名方式       上海交通大学闵行校区本科生在2017年9月11日上午9：00---9月22日下午16：00之间使用jaccount账号登录等级考试报名系统（网址：http://ks.sjtu.edu.cn/）进行网上报名。 报名时请仔细核对本人基本信息（特别是身份证号和照片）： （1）每学期首次登录请务必先确认本人的基本信息，确认无误之后点击“确认”按钮，方可进行报名；如有问题请登入教学信息服务网，选择“我的个人设定”-----“个人信息”，修改错误信息。修改完成后重新登录等级考试系统查看个人信息是否已更正，无误后点击“确认”按钮。 （2）如没有照片则无法报名，请先按照页面要求上传头像照片；      继续教育学院、研究生院学生报名请咨询所在学院；医学院已回重庆南路校区的学生请去医学院考点报名考试。 四、考试报名收费     本次大学英语四级、大学英语六级报名费：25元/科。     考试报名收费方式为等级考试网报名后直接网上付费，网上付费成功视为报名成功，请同学不要重复付费。请各位同学相互转告。 注意事项： 1、 本次开始报名流程改革，取消报名信息纸质版签字确认环节，全部改为线上报名信息核对确认，请考生务必于2017年9月11日--9月22日报名期间登录等级考试网核对报名信息，并点击确认按钮，报名信息数据重要, 出错将导致考生无法参','2017-09-07 14:57','2017-09-07 14:57','','','教学信息服务网','2017-09-07 14:57:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1801907786,'2017-2018秋季学期本科老生注册报到通知','                                       关于2017-2018学年秋季学期本科生研究生课程互选的通知 为推进实施本科硕士博士课程贯通建设，鼓励学有余力的同学进行课程互选，现将有关事宜通知如下： 第一部分：本科同学选修研究生课程注意事项 1、适用对象 （1）我校本科应届毕业同学； （2）部分学院针对选课有特殊规定，在选课之前，请先与各院系研究生教务办老师联系咨询，各院系联系方式：http://yzb.sjtu.edu.cn/information/master/lianxi.htm 2、选课方式及时间 请登录研究生院选课网址（链接：http://www.yjs.sjtu.edu.cn/ssfw/login.jsp）后，选择JAccount登录方式登录-----选课管理-----本科生选课 选课时间：9月19日下午14:00---9月25日上午8:30 3、注意事项 选课时间过后，将不能退出课程，请审慎选择是否选修研究生课程，以免影响上课资源，同时给自己造成不必要的麻烦。建议各位同学在选修具体课程前，与课程所在学院教务办公室老师联系咨询。 咨询电话：研究生院337办公室 姜老师 电话 ：34206360 第二部分：研究生同学选修本科课程注意事项 1、教务处向研究生同学开放未选满课程（特殊班级课程不开放，如经管类-体育班、ACM试点班等），有意向选课的同学最好经过导师同意。 2、选课方式及时间 登录http://electsys.sjtu.edu.cn，通过学校Jaccount进行登录，先到先得，选满为止。 选课时间：9月19日 14:00―9月21日 8:30 3、注意事项 请同学慎重选课，逾期将不办理退课和补选。 咨询电话：教务处教学运行管理办公室 李老师 34206465-26 注意： 1、学生互选课程不影响个人本身的修读计划。本科生选修研究生课程并获得成绩的，研究生院开具成绩证明，如学生进入本校研究生阶段学习，可申请转换成绩进入培养方案中； 成绩转换流程：春季和秋季学期开学后第二周起，将个人信息（姓名、本科学号、联系方式等）汇总至研究生阶段所在学院研究生教务办公室负责老师，由研究生教务办老师统一至研究生院办理。 2、研究生修读本科生课程并获得成绩的，由教务处开具成绩证明。 相关附件：  ','2017-09-07 11:03','2017-09-07 11:03','','','教学信息服务网','2017-09-07 11:03:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-748082985,'2017-2018学年秋季学期重修免修考考试安排发布通知','                                                  上海交通大学2017-2018学年秋季学期第三轮选课通知 各位同学： 2017-2018学年秋季学期的第三轮选课即将开始，现将相关事宜通知如下： 一、选课时间 第三轮选课时间为：2017年9月11日(第一周周一)13:30―2017年9月19日(第二周周二)8:30。 二、注意事项 1.第三轮选课期间，2017年9月11日（第一周周一）13：30―2017年9月18日（第二周周一）8：30，同学可进行选课和退课；2017年9月18日（第二周周一）8：30会暂停选课，13：30起重新开放选课，2017年9月18日（第二周周一）13：30―2017年9月19日（第二周周二） 8：30则只可进行选课、不可退课。 2.抢选结束后，各开课院（系）对选课人数较少的课程进行了调整，对达不到规定人数的必修课程、专业限选课程、通识核心课程或通选课程等进行了合并或取消，请同学在上课前及时登录教学信息服务网查询确认自己的课程表。 3.2017-2018学年秋季学期本硕博课程互选时间为2017年9月19日(第二周周二)13:30―2017年9月21日(第二周周四)8:30，具体请关注本科生研究生课程互选通知。 4.需要重修2017级新生大学英语（1）和体育(1)的学生请注意，由于新生名单是在新生入学后第一周导入，所以大学英语（1）和体育(1)课程在第一周周五下午1：30后才能开放，请重修同学注意。 5.2017级除大学英语(1)和体育(1)课程从第二周周一开始上课外，其余课程均从第一周周三开始上课，请需要重修选课或需要与2017级合上的同学注意上课时间。 选课期间有任何选课问题，请致电34206403-107/105，或至新行政楼B112室咨询。                                                                                                                                                                                                                       教务处教学运行管理办公室                        ','2017-09-07 10:52','2017-09-07 10:52','','','教学信息服务网','2017-09-07 10:52:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1556090704,'关于组织第十五期“上海交通大学大学生创新实践计划”项目学期检查的通知','关于“2017年暑期本科生科研见习岗位”结项的通知 教务处通知〔2017〕49号 各院（系）、科研院所、实验室（中心）： 根据学校实践教学工作安排，“2017年暑期本科生科研见习岗位”工作已进入结项阶段。现将有关工作通知如下： 一、结项时间 2017年9月11日-2017年10月13日。 二、结项要求 1.参与学生 参与学生须对自己在岗期间的工作情况和收获体会进行全面总结，认真填写《暑期科研见习岗位总结报告》（字数要求：2500-3000字），于9月22日前登录上海交通大学大学生创新实践网（http://uitp.sjtu.edu.cn/）填写结项材料，并将《暑期科研见习岗位总结报告》作为附件上传，同时在9月22日之前将《总结报告》纸质材料（一式二份）交于指导教师签署评价意见（请参与暑期科研见习岗位的同学按时提交《总结报告》，否则没有成绩）。 2.指导教师 指导教师在2017年9月30日之前登录大学生创新实践网，对学生在岗期间的工作表现进行考核并给予评价。凡认真完成科研见习岗位工作并提交总结报告的学生，一般可获得1个学分，所有学生成绩均计为P。学生获得的该类学分可作为培养方案中的个性化学分记入学业档案。 指导教师须在学生的纸质《暑期科研见习岗位总结报告》上签署评价意见，给予学分认定，于9月30日前将相关材料交至相关院（系）教务办。 3.院（系）审核 各院（系）负责人认真审核并签署意见，于10月13日前将签字和盖章后的《总结报告》一份（另一份留院系存档）和申请2个学分的学生提供的相关佐证材料交教务处实践办。 三、网上操作流程 参与学生和指导教师登录“上海交通大学大学生创新实践网”（http://uitp.sjtu.edu.cn/，点击页面左方的“创新实践活动平台登录”）。进入系统后，点击左上方的“结题”进行相关输入操作。 联 系 人：田夏 联系电话：34206468-106 电子信箱：prp@sjtu.edu.cn 教务处 2017年9月7日 相关附件：   附件1、 暑期科研见习岗总结报告.doc','2017-09-07 10:37','2017-09-07 10:37','','','教学信息服务网','2017-09-07 10:37:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(328913775,'教务处关于2017年暑假工作安排的通知','关于开放2017-2018学年秋季学期自习教室的通知 各位同学: 新学期好！ 为给同学们提供一个良好的学习环境，同时保障学校正常教学秩序，学校决定在教学优先使用的前提下，开放以下教室供同学们自习使用： 开放教室： 西区：中院1至2层除205、206和207外的其它所有教室； 东区：东上院1至2层除204外的所有教室；       东下院115和105； 开放时间： 2017年9月11日至2017年12月29日（含节假日）7：30―22：30（正常教学安排时间除外）（其中中院114、115和东下院115、105四间教室为24小时通宵自习教室）。 如遇大型考试等临时借用教室的情况，学校会另行通知； 请同学们自习后，勿将书籍等物品放置室内，以免遗失；请自觉维护室内环境卫生。                                 教务处教学运行管理办公室                                    2017年9月7日 相关附件：  ','2017-09-07 10:34','2017-09-07 10:34','','','教学信息服务网','2017-09-07 10:34:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1101609604,'关于2016-2017学年上海交通大学“唐立新教学名师奖”获奖人选的公示','                                                上海交通大学第30、31期PRP项目结题验收的通知                                                             教务处通知〔2017〕48号     根据工作安排， 第30期（执行时间一年）和第31期（执行时间半年）PRP项目结题验收时间为9月11日―10月13日。具体工作由各部门PRP工作组统一组织实施。现将有关安排通知如下： 一、结题要求 1.参与学生 项目参与学生须对项目工作做全面总结，各自独立撰写研究论文（字数要求：不少于5000字），并将论文交由指导教师审阅。在9月20日前登录上海交通大学大学生创新实践网（http://202.120.35.20/ppa/）网上提交结题材料，认真填写“参与心得”，并将《PRP项目研究论文》最终版作为附件上传。研究论文格式按标准模板格式（http://www.jwc.sjtu.edu.cn/web/sjtu/198152-1980000000285.htm）撰写，文件大小不超过10M，页面不超过10页，文件格式为word。项目组全体成员必须参加结题答辩。凡不参加答辩者，成绩以“F”计（海外游学同学请按各院系工作组要求正常提交答辩材料或提前办理退出手续，否则按不参加答辩处理，成绩记为F）。 2. 指导教师 指导教师须认真审阅学生提交的结题材料，于9月27日前在网上填写结题表，并点击 “提交结题申请”。 对完成较好且取得一定成果的项目可申请优秀PRP项目，请填写《PRP优秀项目评审表》，交部门PRP工作组。 3.部门PRP工作组 （1）验收准备工作 梳理本部门第30、31期应验收项目，对尚未进入“准备结项检查”的项目，需逐个完成其前期各个状态的工作，直至进入“准备结项检查”状态。 （2）答辩组织工作 各部门PRP工作组须组织专家对项目进行答辩验收。 （3）成绩评定 各部门应制定统一的答辩要求和评分标准，严把质量关。项目验收按优秀、合格和不合格三档评价，优秀项目不超过15%；学生成绩采用A、B、C、D、F（11级）记分方式，A级（含A+，A，A-）比例控制在25%以内。 （4）成绩录入 请各部门务必在10月13日前完成结题验收审核，并在网上录入PRP成绩。对未进行中期检查或结题验收的项目，其最终的项目成绩记为“不合格”，学生','2017-09-06 09:14','2017-09-06 09:14','','','教学信息服务网','2017-09-06 09:14:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(178798981,'2017-2018学年秋季学期重修班选课通知','                                            教务处注册与学务中心助管招聘公告   因工作需要，教务处注册与学务中心拟在全校范围内招聘助管数名。 工作内容： 1. 办理本科阶段出国成绩单、在读证明、学历学位认证、火车票优惠卡等； 2. 协助收取学生各类申请材料； 3. 协助解答学生各类常见问题的咨询； 4. 协助处理日常事务工作。 招聘要求： 1. 三、四年级在校本科生及在校研究生； 2. 勤奋踏实，细致、有责任心，有一定的沟通协调能力和较强的服务意识，为人热忱； 3. 能够熟练使用办公软件； 4. 学有余力有一定的空余时间，能长期稳定工作（至少协助工作一学期以上）。 工作时间：上午8:30-11:30，下午13:30-17:00，一周值班2-4个半天 工作地点：上海交通大学闵行校区新行政楼B108 / B112办公室 工作待遇：120元/天 报名链接：请在线填写报名问卷，截止时间2017年9月17日。面试及培训时间请留意个人邮箱与短信通知。 电脑端：https://ding.fanqier.com/f/zctnjg 手机端：（请扫描下方二维码）   联系方式：孙老师 kenshbshx017@163.com 教务处注册与学务中心 2017年9月6日 相关附件：  ','2017-07-21 15:27','2017-07-21 15:27','','','教学信息服务网','2017-07-21 15:27:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1382382449,'关于2017年上海交通大学优秀教师奖、烛光奖获奖人选的公示','各院（系）：     上海交通大学本科学生报到、注册日定于2017年9月10日（星期日），请各院（系）通知学生按时到校注册、报到，不允许他人代为注册、报到。具体安排如下： 注册/报到时间： 2017年9月10日（星期日） 上午 8：00―下午17：00 注册/报到地点： 各学院注册报到点（见附录地址，具体以学院通知为准） 注意事项： 1.学生本人凭校园卡或学生证注册、报到 2. 因病或其它原因不能按时返校的同学，应提前与学院（系）联系请假事宜。请假情况由学院（系）汇总后，于9月10日下午16：30前将请假学生名单导入报到注册系统中 3.申请复学的学生，先办理复学手续，补缴学费后再凭校园卡办理注册 4.2017-2018学年秋季学期出国交流的学生不需要办理报到手续 5.非闵行校区的医学院学生请至重庆南路校区注册、报到 6.闵行校区的留学生请至留学生办公室注册、报到 特别提醒： 1.根据教育部令第41号令及2017版学生手册的规定，无故逾期不办理注册手续的学生将以退学处理 2.注册手续包含报到与学费缴纳                                                       二�一七年七月二十一日                                                                  教务处 相关附件：   附件1、 2017-2018秋季学期注册报到地点.xls','2017-07-21 09:04','2017-07-21 09:04','','','教学信息服务网','2017-07-21 09:04:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1227251021,'教务处关于组织本学期重修免修考试(补考)的通知','2017-2018学年秋季学期重修免修考考试安排发布通知 各位同学： 本学期重修免修考（补考）考试安排在第0周四（9月7日）周五（9月8日）：上午二，下午一，下午二；第1周二（9月12日）：下午一，下午二进行。所有考试安排已经发布，请大家登陆自己的教学信息服务网查询详细的考试信息，并做好考试准备。                                          教务处教学运行管理办公室                                                2017年7月21日 相关附件：  ','2017-07-20 13:52','2017-07-20 13:52','','','教学信息服务网','2017-07-20 13:52:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(843332260,'2016-2017学年春季学期尚未提交成绩的课程列表','关于组织第十五期“上海交通大学大学生创新实践计划” 项目学期检查的通知 教务处通知〔2017〕47号 根据《上海交通大学“大学生创新实践计划”项目管理办法》的规定，为加强项目过程管理，学校将对第十五期“上海交通大学大学生创新实践计划”项目进展情况进行学期检查。现将有关事项通知如下： 一、时间安排 7月25日-10月9日为项目组自查阶段，请各项目组充分利用这段时间，积极准备，合理安排，做好项目自查工作，认真填写《上海交通大学IPP项目学期检查报告》，同时登录大学生创新实践网填写学期检查报告。各项目组须在10月9日前将经指导教师审核并签署意见的纸质报告交至所在院（系）工作组负责老师。10月10日-10月23日为院（系）组织学期检查时间，要求院（系）工作组对各项目统一进行学期检查答辩评审。 二、检查内容与要求 1、检查项目组是否认真填写《学期检查报告》，提交中期检查材料。（系统操作详见附件1） 2、检查项目是否按计划进度组织实施，指导教师是否认真予以指导，项目是否取得初步成效等。 3、要求项目组及时发现项目实施过程中存在的问题，在指导教师的帮助下分析原因，总结经验，提出解决思路，并进一步明确下一阶段的任务。 4、要求项目组及时总结在项目实施过程中的心得与体会，特别是在创新思维和创新实践方面的体验和收获。 5、项目组成员若发生变更，请及时填写项目变更申请表，经指导教师、院系工作组签署意见并盖章后，交至工程训练中心B104。中期检查（10月23日）之后，不允许任何形式的项目成员添加。 注：第十五期大学生创新计划由院系作为管理主体对各项目进行管理，如院系大学生创新计划组有高于学校的管理要求参照院系管理细则执行，具体实施细则请咨询院系工作组 三、检查形式 1、各院（系）工作组要求采取统一答辩的形式进行学期检查。根据检查情况，在《学期检查报告》中“院（系）工作组意见”一栏签署审核意见和院（系）内排序建议，同时请登录大学生创新实践网录入评语。 2、各院系将于10月10日-10月23日组织专家对各项目进行公开答辩评审，所有项目和全体成员均需认真准备，同时准备好7分钟的PPT演示材料。具体答辩安排由各院系工作组另行通知。 3、各院系请在答辩进行前一周将具体时间地点上报工程训练中心。 4、各院系于10月26日前将《大学生创新计划学期检查评审汇总表》电子版与打印文稿交工程训练中心B104。 四、检查电子材料 1、项目立项人请登录大学生创新','2017-07-17 10:56','2017-07-17 10:56','','','教学信息服务网','2017-07-17 10:56:00.0');
