insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-165105475,'关于增设“统计学”等三个本科专业的公示','关于2016-2017学年上海交通大学 “唐立新教学名师奖”获奖人选的公示 教务处通知[2017]45号 根据《上海交通大学“唐立新教学名师奖”评选办法（暂行）》以及有关规定，经全校学生投票和专家评选，拟推荐杨培中等10名教师为我校2016-2017学年“唐立新教学名师奖”获得者。 特此公示。如有异议，请与教务处教学改革与质量办公室联系，公示期自公示之日起一周。 联系方式：zhaoliu@sjtu.edu.cn               教务处                                       二�一七年七月十三日 附件：2016-2017学年“唐立新教学名师奖”获奖名单 相关附件：   附件1、 20170713-2017“唐立新教学名师奖”获奖名单- 附件.doc','2017-07-10 16:01','2017-07-10 16:01','','','教学信息服务网','2017-07-10 16:01:00.0');
