insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(785064858,'关于组织申报2018年上海市本科重点教学改革项目的通知','上海交通大学大学生创新计划项目结题成绩公告 教务处通知 [2017]  40号 2017年5月，各院系组织专家对第十三期校级“大学生创新实践计划”（包括已升级为“国家级大学生创新创业训练计划”创新训练项目、 “上海大学生创新活动计划”创新训练项目和前期未结题的校级创新项目）项目进行了结题答辩验收。经项目组申请，学院审核，全校共有139个项目取得学校答辩验收资格（不含医学院），其中“国家级大学生创新创业训练计划”创新训练项目14项，“上海大学生创新活动计划”项目33项，“上海交通大学大学生创新实践计划”项目92项。 经院系专家评审，学校领导组终审，136项项目通过验收，其中39项项目成绩评定为“优秀”，项目优秀率为28.1%；427名学生取得大学生创新实践计划成绩和学分，其中136名学生成绩优秀，占获得成绩学生总数的31.9%。按照《上海交通大学大学生创新计划项目管理办法》有关规定，所有项目学生成绩和学分记入《上海交通大学学生学习成绩大表》。 工程训练中心 教务处 2017年6月6日 附表：上海交通大学大学生创新计划项目结题成绩（2017年5月） 序号 项目编号 项目名称 立项人 立项人所在院系 项目成绩 1 IPP13001 上海国际旅游度假区停车场收费模式的研究 石雯婷 船舶海洋与建筑工程学院 优秀 2 IPP13003 硅藻精土改性方法及改性硅藻精土吸附性能研究 杨昆 船舶海洋与建筑工程学院 合格 3 IPP13004 面向普通用户的BIM虚拟装修系统研发 黄煌 船舶海洋与建筑工程学院 优秀 4 IPP13005 无人风帆船自动循迹研究 岳伟韬 船舶海洋与建筑工程学院 合格 5 IPP13006 可潜水四旋翼设计 吴华坚 船舶海洋与建筑工程学院 合格 6 IPP13007 上海交通大学闵行校区认知地图研究 何雅欣 船舶海洋与建筑工程学院 合格 7 IPP13008 基于网络的船用螺旋桨快速建模技术开发及应用 曾祥帅 船舶海洋与建筑工程学院 合格 8 IPP13009 软土小应变刚度与微观结构的相关性分析 翟信哲 船舶海洋与建筑工程学院 合格 9 IPP13010 平面浮雕雕刻机 马晓也 船舶海洋与建筑工程学院 合格 10 IPP12034 外部冲击条件下脑动脉瘤内血流特征及其破裂风险研究 蔡汉青 船舶海洋与建筑工程学院 合格 11 201610248012 FSC电子涡轮的设计与研发 黄雨晗 机械与动力工程学','2017-06-06 10:56','2017-06-06 10:56','','','教学信息服务网','2017-06-06 10:56:00.0');
