insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(265591447,'关于第32期PRP选项报名通知','根据依照《九所首批“985工程”建设高等学校人才培养合作与交流协议书》的工作安排，南京大学将继续组织举办2017年C9高校暑期学校。具体通知如下： 一、主题 为激发大学生关注国防的热情，增加大学生对国际形势的了解，本届暑期学校将继续以“当前国际形势与中国周边安全”为主题，通过一系讲座、参观和讨论，发挥南京在中国近代史上特殊地位的作用，让学生深入了解我国面临的机遇和挑战，理性看待当前国际国内局势。 二、开班时间 2017年7月9日- 7月16日 1.报到：7月9日下午 2.学习及参观活动：7月10日-7月16日 3.离校：7月16日下午 三、学生及人数 学生专业不限，每校本科生10人。 四、教学内容及授课方式 日期 时间 课程活动 7月9日 13:00-17:00 报到、领取学习资料 参观：熟悉南大校园环境 18:30-20:30 破冰活动 7月10日 9:00-9:45 开班典礼和全体学员合影 10:00-11:30 讲座 15:00-17:00 讲座 7月11日 9:00-11:00 讲座 15:00-17:00 讲座 7月12日 9:00-11:00 讲座 15:00-17:00 讲座 7月13日 9:00-11:00 讲座 15:00-17:00 参观：南京大屠杀遇难同胞纪念馆 7月14日 9:00-11:00 讲座 15:00-17:00 讲座 7月15日 9:00-11:00 讲座 14:00-17:00 参观：南京历史文化考察：中山陵、中国近现代史博物馆（总统府）、太平天国博物馆（瞻园）、夫子庙 7月16日 8:30-12:00 报告会：学生分组汇报，专家点评 下午 学生离校 注：各项教学活动的时间可能会调整，以报到时教学安排为准。 五、费用 1、学生自理往返交通费和餐费，自费充值一卡通后在食堂用餐，每天约20－30元。 2、在南大上课期间的学费、住宿费和参观费用由南京大学承担。 六、 报名时间及方式 请有意参加该暑期项目的同学填写附件1：上海交通大学2017年C9高校暑期学校报名表并于6月7日中午12：00前将签字盖章完整的纸质版表格交至新行政楼B329教务处教学运行管理办公室；同时填写附件2：2017年南京大学C9高校暑期学校报名回执，以电子版表格形式发送至邮箱sjtu_c9_exchange@126.com。3：学生电子证件照（照片文件名请改为学生姓名）发送到上述邮箱。本次南京大学暑期教学活动的','2017-06-01 09:51','2017-06-01 09:51','','','教学信息服务网','2017-06-01 09:51:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-161892511,'2017年本科课程教学改革研究项目中期检查通知','关于2017届“上海交通大学优异学士学位论文”评选的通知 教务处通知〔2017〕035号 毕业设计（论文）是本科人才培养的重要环节，是实现教学、科研与社会实践相结合的重要结合点，对于提高学生的综合能力和全面素质具有重要意义。为进一步提升我校本科生毕业设计（论文）质量，更好地调动广大师生的积极性，学校从2012届起，开展“上海交通大学优异学士学位论文” （TOP 1%）评选活动，取得了良好的效果。现将2017届“上海交通大学优异学士学位论文”评选活动有关事项通知如下： 一、评选范围 参加本次评选的毕业论文，必须是成绩为“A+”的2017届本科生毕业论文（不含密西根和巴黎高科学院）。各院(系)按应届生总人数的1%左右推荐参评，具体推荐名额见附件1。 二、评选条件 1.选题的性质、难度、工作量等符合专业教学要求，力求选题与科学研究、技术开发、经济建设和社会发展紧密结合。 2.学生的毕业设计（论文）撰写规范，资料齐全，由学生本人完成，学生在毕业设计（论文）过程中获得完整的系统性和综合性训练。 3.应有明确的研究结论或实验结果，并具有一定的创新性，或具有一定的学术水平和独到见解，或具有一定的实用（参考）价值。 4.学生须填写“上海交通大学优异学士学位论文申报表”（见附件2）。 三、申报材料及程序 1.申报材料为优异学士学位论文申报表和研究论文纸质版各一份及其电子版（word格式）。请各院（系）在6月16日（十七周）前将上述纸质材料交至教务处教改办，电子版发至PRP@sjtu.edu.cn。 2.教务处将组织专家于6月21日下午进行答辩评选，择优选拔38篇“上海交通大学优异学士学位论文”。具体答辩安排另行通知。 四、奖励 1.学校将对入选“上海交通大学优异学士学位论文”的学生和指导教师颁发证书并进行表彰。 2.学校将制作2017届优异学士学位论文获奖人“电子荣誉榜”进行风采展示。（http://sjcg.jwc.sjtu.edu.cn/）                                                 教务处                                             2017年6月1日 相关附件：   附件1、 附件1：上海交通大学2017届优异学士学位论文各院（系）推荐名额.xls 附件2、 附件2：上海交通大学优异学士学位论文推荐表.doc','2017-05-31 17:33','2017-05-31 17:33','','','教学信息服务网','2017-05-31 17:33:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1472951008,'关于2016-2017学年春季学期期末开放通宵教室的通知','各位同学：     哈尔滨工业大学（简称哈工大）始建于1920年，是首批进入国家“211工程”和“985工程”建设的若干所大学之一，也是C9联盟成员之一。秉承“规格严格，功夫到家”的校训，哈工大坚持个性化培养与柔性化管理相结合的人才培养方法，遵循课程学习与项目学习结合、理论学习与社会实践结合、课内学习与课外学习结合、教师教与学生学结合等原则，注重对学生能力和素质的培养，造就了大批工程实践能力强、具有团结协作精神的创新型人才。     哈工大坐落于素有“东方小巴黎”和“东方莫斯科”之称的冰城夏都哈尔滨市。哈尔滨地处东北亚中心地带，被誉为欧亚大陆桥的明珠，是第一条欧亚大陆桥和空中走廊的重要枢纽，是中国历史文化名城、热点旅游城市和国际冰雪文化名城，也是国家战略定位的“沿边开发开放中心城市”、“东北亚区域中心城市”及“对俄合作中心城市”。    为加强C9学校之间的合作与交流，哈尔滨工业大学定于2017年夏季学期继续开办面向C9高校的暑期课程。相关事宜如下： 一、主题 哈尔滨工业大学本届暑期课程的主题有3个：计算机科学与技术前沿―感知世界 智化生活”、“智能机器人”、“印记哈尔滨 城市循踪―建筑设计工作营”。 “印记哈尔滨 城市循踪―建筑设计工作营”课程因包括设计内容，请参营同学自行准备笔记本电脑。      详细介绍可参考附件。 二、开班时间及课程内容 1、“计算机科学与技术前沿―感知世界 智化生活” 暑期学校的时间为7月2日-16日（2日报到，16日离校）， 日程安排见下表： 时间 环节安排 具体内容 负责教师 学时 学分 第一周 课程讲授 物联网与信息物理系统前沿 美国圣路易斯华盛顿大学Chenyang Lu教授 24 1.5 计算思维与科学前沿 微软亚洲研究院 林钦佑研究员 16 1 学术讲堂 专题讲座与报告       第二周 课程讲授 快速原型系统设计 美国卡内基梅隆大学 Asim Smailagic教授 24 1.5 学术讲堂 专题讲座与报告       第一周 第二周 项目实践 课程相关项目实践特色项目实践   20 1 2、“智能机器人”暑期学校的时间为2017年7月9日-29日（9日报到，29日离校），  具体日程安排见下表： 时间 课程 课程内容 主讲人 学时 学分 第一周 讲座 专题讲座与报告 蔡鹤皋院士等 8 0.5 授课 工业4.0与信息物理系统 Jörn Mehne 教授 16 1 第二周 ','2017-05-31 13:34','2017-05-31 13:34','','','教学信息服务网','2017-05-31 13:34:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-299321376,'关于端午节放假期间通宵自习教室临时调整的通知','关于组织申报2018年上海市本科重点教学改革项目的通知 教务处2017[037]号 各院（系）:     为贯彻落实国家和上海市中长期教育改革和发展规划纲要精神，深化本科教育教学改革，培育优秀教学成果，根据《上海市教育委员会关于开展2018年上海高校本科重点教学改革项目申报工作的通知》指导精神，我校将继续组织开展上海市本科重点教学改革项目的申报和推荐工作。现将有关事项通知如下： 一.申报条件 1.申报项目倡导项目与培育教学成果相结合、研究与改革实践相结合，以科学研究指导改革实践，争取产生具有创新性和示范性的教育教学成果和经验；鼓励跨学科、多层次合作开展教育教学的综合研究和改革，通过研究项目促进上海高等教育教学水平的整体提高。 2.各院（系）可结合自身实际情况，围绕转变教育思想观念、改革人才培养机制、创新人才培养模式、加强教学质量保障、推进优质教育资源共享、推动教学管理机制改革、优化学科专业结构、改进教学内容方法、强化实践育人环节、深入推进创新创业教育改革等方面申报立项。 3.申报项目应遵循高等教育教学规律，突出教育教学改革的创新性和实践性，对提高教学水平和教育质量产生明显效果。申报项目应具备一定的教学改革基础和相应条件，有推广价值，能在高校教学改革中起示范和指导作用。 二．申报名额与申报流程 1. 我校总推荐项目不超过5项，各院（系）限推荐一项。 2. 各院（系）于2017年6月15日前将项目申报书（附件1）的电子文档发送至丛峰老师：cfier@sjtu.edu.cn，申报表纸质文档（一式三份，双面打印）交教务处教学改革与质量办公室。 3.学校组织专家对申报项目进行评审遴选后报上海市教育委员会。 三．其他 工作组织中有任何疑问，请联系教学改革与质量办公室： 地址：闵行校区新行政楼B323，电话：34206426转8001。 教务处 2017年5月31日 相关附件：   附件1、 附件1-2018年上海高校本科重点教学改革项目申报书.doc','2017-05-31 10:11','2017-05-31 10:11','','','教学信息服务网','2017-05-31 10:11:00.0');
