insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-919152497,'2017夏季学期海外学者通识教育核心课程选课手册','教务处通知[2017]23号 根据《上海市教育委员会关于做好上海高校示范性全英语课程2017年项目申报和2014年立项项目验收工作的通知》（沪教委高〔2017〕22号）及《上海高校示范性全英语教学课程建设管理办法》（沪教委高[2009]51号），学校对2014年立项的上海高校示范性全英语教学课程建设项目开展了验收工作。通过课程建设负责人自评、专家评审等环节，拟给予我校本部2门2014年上海高校示范性全英语教学课程建设项目“验收通过”的结论。名单请见附件一。 同时，根据上述文件要求，学校动员各院系进行2017-2019年建设专业及课程的申报，通过组织专业自评、专家评审，全面考察了各申报专业的教学理念、建设基础、教学团队和教材情况等相关内容，拟推荐1个示范性全英语专业及该专业下3门示范性全英语课程。名单请见附件二。 现将结果予以公示，公示有效期自公示之日起至4月14日。如有异议，请与教务处教学研究办公室联系。 联系人：曹筱旭，34206415-27，闵行校区新行政楼B321室cxxathk@sjtu.edu.cn                                                        教学研究办公室 二�一七年四月七日 附件一：2014年上海高校示范性全英语教学课程建设项目验收结果 序号 课程名称（中文） 课程名称（英文） 课程负责人 验收结果 1 电动力学 Classical Electrodynamics 陈民 通过 2 量子力学 II Quantum Mechanics II 孙扬 通过 附件二：2017年上海高校示范性全英语教学课程立项建设推荐名单 序号 专业名称 课程名称（中文） 课程名称（英文） 课程负责人 1 信息工程 信号与系统 Signals and Systems 熊红凯 2 信息工程 无线通信原理与移动网络 Principles Wireless Communication and Mobile Networks 王新兵 3 信息工程 通信原理与实验 Principles and Experiments of Communications 陶梅霞 相关附件：  ','2017-04-06 15:14','2017-04-06 15:14','','','教学信息服务网','2017-04-06 15:14:00.0');
