insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-130260087,'关于2017年度上海交通大学教学成果奖、校长教学奖评选工作的通知','关于组织上海市语言文字水平测试中心2017年调研项目及2018年科研课题申报的通知 教务处通知〔2017〕022号 各相关学院（系）： 为推动上海市语言文字水平测试工作规范、科学、健康发展，引导和鼓励全市语言文字工作者积极参与多层次、宽领域的国家通用语言文字培训测试等相关工作研究，上海市语言文字水平测试中心（以下简称“市语测中心”）启动了2017年度调研项目及2018年科研课题立项申报、评审工作（参见通知附件）。 我校语言文字相关教师、研究人员有申报意向者，请按照附件通知要求填写项目申请书。申 请 书 （ 样 张 ） 可 从 市 语 测 中 心 网 站（http://www.shysc.edu.sh.cn）的“下载中心”栏目中下载。 请各申请者于2017年4月14日之前将申请书（一式三份）交至闵行校区新行政楼B325室，同时将电子版发送至zhangsm@sjtu.edu.cn。 教务处提请分管校领导审核同意后报送市语测中心。 联 系 人：张老师 联系电话：34206429 电子信箱：zhangsm@sjtu.edu.cn 联系地址：新行政楼B325室 附件：上海市语言文字水平测试中心关于申报2017 年调研项目及 2018 年科研课题的通知                                                       教务处                                                        2017年4月1日 相关附件：   附件1、 3-申报2017年调研项目2018年课题的通知.pdf','2017-04-01 08:57','2017-04-01 08:57','','','教学信息服务网','2017-04-01 08:57:00.0');
