insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1746087671,'关于2016-2017学年第二学期C9高校联盟交流项目预录取名单公示','关于组织第十四期“上海交通大学大学生创新实践计划”项目学期检查的通知 教务处通知〔2017〕02号 根据《上海交通大学“大学生创新实践计划”项目管理办法》的规定，为加强项目过程管理，学校将对第十四期“上海交通大学大学生创新实践计划”项目进展情况进行学期检查。现将有关事项通知如下： 一、时间安排 1月16日-3月10日为项目组自查阶段，请各项目组充分利用这段时间，积极准备，合理安排，做好项目自查工作，认真填写《上海交通大学IPP项目学期检查报告》，同时登录大学生创新实践网填写学期检查报告。各项目组须在3月10日前将经指导教师审核并签署意见的纸质报告交至所在院（系）工作组负责老师。3月10日-3月31日为院（系）组织学期检查时间，要求院（系）工作组对各项目统一进行学期检查答辩评审。 二、检查内容与要求 1、检查项目组是否认真填写《学期检查报告》，提交中期检查材料。（系统操作详见附件1） 2、检查项目是否按计划进度组织实施，指导教师是否认真予以指导，项目是否取得初步成效等。 3、要求项目组及时发现项目实施过程中存在的问题，在指导教师的帮助下分析原因，总结经验，提出解决思路，并进一步明确下一阶段的任务。 4、要求项目组及时总结在项目实施过程中的心得与体会，特别是在创新思维和创新实践方面的体验和收获。 5、项目组成员若发生变更，请及时填写项目变更申请表，经指导教师、院系工作组签署意见并盖章后，交至工程训练中心B104。中期检查（3月10日）之后，不允许任何形式的项目成员添加。 注：第十四期大学生创新计划由院系作为管理主体对各项目进行管理，如院系大学生创新计划组有高于学校的管理要求参照院系管理细则执行，具体实施细则请咨询院系工作组 三、检查形式 1、各院（系）工作组要求采取统一答辩的形式进行学期检查。根据检查情况，在《学期检查报告》中“院（系）工作组意见”一栏签署审核意见和院（系）内排序建议，同时请登录大学生创新实践网录入评语。 2、各院系将于3月10日-3月31日组织专家对各项目进行公开答辩评审，所有项目和全体成员均需认真准备，同时准备好PPT演示材料。具体答辩安排由各院系工作组另行通知。 3、各院系请在答辩进行前一周将具体时间地点上报。 4、各院系于3月31日前将《大学生创新计划学期检查评审汇总表》电子版与打印文稿交工程训练中心B104。 四、检查电子材料 1、项目立项人请登录大学生创新实践网，于3月10日之前网上填写学','2017-01-13 14:18','2017-01-13 14:18','','','教学信息服务网','2017-01-13 14:18:00.0');
