insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1155836301,'创新与创业大讲堂第76期','上海交通大学第十三期校级大学生创新实践计划项目学期检查情况通报 教务处通知〔2016〕69号 根据《上海交通大学大学生创新实践计划项目管理办法》要求，为加强项目过程控制，于2016年7月25日至10月21日组织了第十三期“上海交通大学大学生创新实践计划”项目学期检查工作，分别按项目组自查、院（系）检查和学校审核三级完成。各院（系）大学生创新计划工作组按照学校要求组织中期检查答辩，并向学校提供中期检查情况汇总表。现根据各院（系）中期检查汇总表，将有关情况通报如下： 全校147个项目参加了此次学期检查，船舶海洋与建筑工程学院侯耀晟树项目组，电子信息与电气工程学院何佳倪、陈俊润项目组，材料科学与工程学院李文项目组，化学化工学院聂誉、穆振宇项目组以上6个项目组项目申请中止；密西根学院符晓�肯钅孔楸蛔�家评定为暂缓通过，要求项目按照专家组意见进行整改（具体整改安排另行通知）；其余140个项目通过学期检查。希望各项目组认真汲取各方意见和建议，明确分工、加大投入，指导教师加强指导，院（系）工作组加强监督，完成项目后续工作。 工程训练中心 教务处 2016年11月17日 附表：第十三期校级大学生创新实践计划项目学期检查情况 项目编号 项目名称 立项人 指导教师 所在院系 中期检查结果 IPP13001 上海国际旅游度假区停车场收费模式的研究 石雯婷 胡昊 船舶海洋与建筑工程学院 通过 IPP13002 风力发电实验造风系统研究与实践 侯耀晟树 余龙 船舶海洋与建筑工程学院 项目中止 IPP13003 硅藻精土改性方法及改性硅藻精土吸附性能研究 杨昆 杜守继 船舶海洋与建筑工程学院 通过 IPP13004 面向普通用户的BIM虚拟装修系统研发 黄煌 宋元斌 船舶海洋与建筑工程学院 通过 IPP13005 无人风帆船自动循迹研究 岳伟韬 许劲松 船舶海洋与建筑工程学院 通过 IPP13006 可潜水四旋翼设计 吴华坚 曾铮 船舶海洋与建筑工程学院 通过 IPP13007 上海交通大学闵行校区认知地图研究 何雅欣 秦丹妮 船舶海洋与建筑工程学院 通过 IPP13008 基于网络的船用螺旋桨快速建模技术开发及应用 曾祥帅 余龙 船舶海洋与建筑工程学院 通过 IPP13009 软土小应变刚度与微观结构的相关性分析 翟信哲 叶冠林 船舶海洋与建筑工程学院 通过 IPP13010 平面浮雕雕刻机 马晓也 宋元斌 船舶海洋与建筑工程学院 通过 ','2016-11-17 15:02','2016-11-17 15:02','','','教学信息服务网','2016-11-17 15:02:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1835979855,'2016-2017学年秋季学期二专学费缴费通知','关于2016年12月全国大学英语四、六级考试对比考报名的通知 各位同学： 现定于2016年12月7日（周三）晚上18：00――19：15（考试时间75分钟）在闵行校区进行全国大学英语四、六级对比考试，该考试题型只有听力和阅读（不考作文、翻译和完形填空）。本次考试成绩高于正式考试成绩的考生将增发一张取得较高分数的成绩单。请已经报名参加12月全国大学英语四、六级考试的本科生踊跃报名，四级限额30名，六级限额450名，先到先得，报完截止。 报名时间：2016年11月21日（周一）中午12：30  ―  2016年11月23日（周三）上午9：00，报完截止。 报名网址：http://ks.sjtu.edu.cn 考试科目：CET4C、CET6C 考试时间：2016年12月7日（周三）18：00--19：15 考试地点：下院（闵行校区） 注： 1.考试请务必带好准考证、学生证、身份证（均原件），三证缺一不可； 2.由于名额有限，报名同学请务必参加对比考试，若报名又无故缺考者，取消下次四六级考试资格； 3.本考试为对比考试，考试成绩需与正式考试成绩对比参考，成绩单发放时间较正式考试成绩单发放晚（一般在下一次对比考试前期），故无法作为下次正式考试报名成绩依据，特此申明； 4.凡在2016年11月21日（周一）中午12:30以后进行报名的，为有效报名结果，在此时间之前的报名均为测试阶段，按无效计。请同学们把握好报名时间。                        教务处教学运行管理办公室                           2016年11月17日 相关附件：  ','2016-11-15 15:10','2016-11-15 15:10','','','教学信息服务网','2016-11-15 15:10:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-729749260,'2016-2017学年秋季学期重新学习学费缴费通知','上海交通大学关于2017年美国MCM/ICM报名参赛通知 美国数学建模竞赛（MCM）和交叉学科建模竞赛（ICM）由美国工业与应用数学学会主办，每年举办一次，2017年竞赛通知详见附件1。 MCM/ICM的官方网站为http://www.comap.com/undergraduate/contests/。请在报名和开赛前必须认真阅读和理解《2017_MCM-ICM_Flyer》（附件1，可参考中文翻译(附件2）)和《2017_MCM-ICM_Instructions》（附件3，可参考中文翻译(附件4））。 注意: 1.       以下所有时间节点以附件1、3为准。美国东部时间（冬令时）比北京时间晚13小时； 2.       附件5为2016年美赛官网报名步骤，2017年可能与之有所出入； 3.       各类通知、证明、奖励等事项均以交大数学类竞赛官网上登记信息为准，请务必准确填写，并注意查收邮件（包括垃圾邮件）。 4.       不再要求邮寄论文和签好名的控制页； 5.       请保存所有有关文件。 一、    参赛对象 上海交通大学在校本科生。 二、   参赛方式 三名本校本科生组成一个参赛队、官网等网站公布试题、参赛队独立完成赛题的方式进行。请参赛同学自己组队并确定和联系指导教师，参赛同学和指导教师必须认真阅读和理解《2017_MCM-ICM_Flyer》（附件1、2）和《2017_MCM-ICM_Instructions》（附件3、4）。报名费为100美元/队，约680人民币/队。 三、   报名和缴费方法 报名时间从即日起至美国东部时间2017年1月19日下午3时（北京时间2017年1月20日上午4时），采用网上报名方式，可参考附件5报名指南。组队后有两种报名缴费方式： 方式一（推荐）：队员有可网上支付美元的VISA或MasterCard信用卡，则个人自行缴费并完成报名： 1.       确定导师：参赛队自己联系指导教师（必须为我校教师）。 2.       美赛官网报名：参考附件5中步骤，按附件3、4要求在美赛官网： http://www.comap.com/undergraduate/contests/mcm/register.php 上报名缴费并获得控制号（control number），请务必记住自己的控制号。注意报名截止日期为美国东部时间2017年1月19日下午3时（北京时间201','2016-11-15 12:21','2016-11-15 12:21','','','教学信息服务网','2016-11-15 12:21:00.0');
