insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1009905178,'关于发布2016届毕业生调查问卷抽奖结果的通知','各位同学： 2016-2017学年秋季学期的第三轮选课即将开始，现将相关事宜通知如下： 一、选课时间 第三轮选课时间为：2016年9月12日(第一周周一)13:30―2016年9月20日(第二周周二)8:30。 二、注意事项 1.第三轮选课期间，2016年9月12日（第一周周一）13：30―2016年9月19日（第二周周一）8：30，同学可进行选课和退课；2016年9月19日（第二周周一）8：30会暂停选课，13：30起重新开放选课，2016年9月19日（第二周周一）13：30―2016年9月20日（第二周周二） 8：30则只可进行选课、不可退课。 2.抢选结束后，各开课院（系）对选课人数较少的课程进行了调整，对达不到规定人数的必修课程、专业限选课程、通识核心课程或通选课程等进行了合并或取消，请同学在上课前及时登录教学信息服务网查询确认自己的课程表。 3.2016级新生的大学基础英语（1）、体育（1）因分班需要将在9月18日13：30后在系统中开放，有补选、重修需求的同学请关注节点，注意选课。 4.2016-2017学年秋季学期本硕博课程互选时间为2016年9月20日(第二周周二)13:30―2016年9月22日(第二周周四)8:30，具体请关注本科生研究生课程互选通知。 选课期间有任何选课问题，请致电34206403-105/106，或至新行政楼B112室咨询。                                                            教务处教学运行管理办公室                      2016年9月8日 相关附件：  ','2016-09-08 13:46','2016-09-08 13:46','','','教学信息服务网','2016-09-08 13:46:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(358981761,'关于本周末（7月2、3日）加班受理2016届本科毕业生出国成绩单与学历学位认证办理申请的通知','                         2016年12月全国大学英语四六级考试报名通知        2016年12月全国大学英语四、六级考试报名工作已经开始，考试的项目包括大学英语四级和大学英语六级。具体通知如下： 一、考试时间     大学英语四级：     2016年12月17日上午9：00―11：20；     大学英语六级：     2016年12月17日下午15：00―17：25。 二、考试报名资格     1.上海交通大学全日制专科、本科、研究生在校生；     2.各类全日制成人高等院校本科、专科在校生；     3. 大一年级春季学期考生方可首次报名CET4；     4.大学英语四级考试成绩达到425分以上（含425分）者可报考CET6。     5.从2012年6月开始，每位考生在大学阶段只允许报名三次CET6考试（包括因故或无故缺考）（低于425分除外）。     6.每次英语考试只能报一个类型，不能同时报考英语四、六级考试。一经发现，取消考试资格，报名费不退，责任自负。     7.医学院大二（含大二年级）以上（重庆南路校区）的学生请到医学院考点报名考试。     注：一经发现不符合上述报名条件者，取消报名，报名费不退，谢谢配合。 三、考试报名方式：        上海交通大学闵行校区本科生在2016年9月12日上午9：00---9月22日下午16：00之间登入等级考试报名系统（网址：http://ks.sjtu.edu.cn/）进行网上报名，用户名和密码为学号和选课网密码。报名时请仔细核对本人信息（特别是手机号码），如有问题请登入教学信息服务网，选择“我的个人设定”-----“个人信息”，修改错误信息。修改完成后重新登入等级考试系统查看个人信息是否已更正。 网络学院、成人教育学院、研究生院学生报名请咨询所在学院；医学院已回重庆南路校区的学生请去医学院考点报名考试。 四、考试报名收费     本次大学英语四级、大学英语六级报名费：25元/科。     考试报名收费方式为等级考试网报名后直接网上付费的方式，网上付费成功视为报名成功，请同学不要重复付费。请各位同学相互转告（付费详细流程见等级考试网相关通知）。 注意事项： 1、 请学生务必于2016年9月23日--9月30日去院系教务办核对报名信息，并签字确认，报名信息数据重要, 出错将导致考生无法参加考试,证书信息错误等情况,请各','2016-09-08 08:47','2016-09-08 08:47','','','教学信息服务网','2016-09-08 08:47:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1690531779,'夏季学期工程训练中心实习注意事项','上海交通大学关于 “第八届全国大学生数学竞赛暨第八届上海市大学生数学竞赛” 通知 各位同学： 为了培养人才、服务教学、促进高等学校数学课程的改革和建设，增加大学生学习数学的兴趣，培养分析、解决问题的能力，发现和选拔数学创新人才，为青年学子提供一个展示基础知识和思维能力的舞台，经中国数学会批准，第八届全国大学生数学竞赛将由北京科技大学数理学院承办。 竞赛分初赛和决赛两个阶段，上海市的参赛者初赛在上海赛区进行。上海赛区组织工作由上海市数学会负责。上海市数学会决定，上海赛区的初赛同时作为第八届上海市大学生数学竞赛，竞赛由复旦大学承办，华东政法大学协办。 现将竞赛的具体事宜通知如下： 一、参赛对象： 大学本科二年级或二年级以上的在校大学生。竞赛分为非数学专业组和数学专业组(含数学与应用数学、信息与计算科学专业的学生)。数学专业学生不得参加非数学专业组的竞赛。 二、竞赛时间： 本届比赛初赛于2016年10月22日（星期六）上午9：00―11：30举行，决赛于2017年3月18日（周六）上午在北京科技大学举行。 三、竞赛内容： （1）非数学专业组： 初赛考试内容：高等数学。 决赛考试内容：高等数学、线性代数（所占总分的比例分别为80%、20%左右）。 （2）数学专业组： 初赛考试内容：数学分析、高等代数、解析几何（所占总分的比例分别为50%、35%及15%左右）。 决赛试卷分为两类： 大二学生：在预赛所考内容的基础上增加常微分方程（所占总分的比例约为15%）。大三及以上年级学生：在大二学生考试内容的基础上，增加实变函数、复变函数、抽象代数、数值分析、微分几何、概率论等内容，由考生选做其中三门课程的考题。新增内容所占总分的比例不超过50%。以上考题所涉及的各科内容，均不超出数学专业本科或理工科本科相应课程教学大纲规定的教学内容。 四、报名和成绩查询： 1.  报名：直接用Jaccount账号登录上海交通大学数学类竞赛网站http://mcm.sjtu.edu.cn（以下简称“竞赛网站”），点击“全国大学生数学竞赛 2016年报名”，按要求正确填写信息（数学专业学生必须选“数学组”）。报名截止时间：2016年9月26日24:00，过时系统将关闭 2.  缴费：报名成功后每个参赛者须交报名费60元。请通过支付宝（账号：xmwang@sjtu.edu.cn）交报名费60元给王晓敏老师，请务必备注内写明“大学生数学竞赛+姓名”，否则将','2016-07-22 14:25','2016-07-22 14:25','','','教学信息服务网','2016-07-22 14:25:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1943613737,'2015-2016-3夏季学期《工程技术探究》选课与上课通知','各院（系）：      上海交通大学本科学生报到、注册日定于2016年9月11日（星期日），请各院（系）通知学生按时到校报到、注册，不允许他人代为报到、注册。具体安排如下： 报到/注册时间： 2016年9月11日（星期日） 上午 8：00―下午17：00 报到/注册地点： 各学院注册报到点（见附录地址，具体以学院通知为准） 注意事项： 1.学生本人凭校园卡或学生证注册、报到。 2. 因病或其它原因不能按时返校的同学，应提前与学院（系）联系请假事宜。请假情况由学院（系）汇总后，于9月11日下午16：30前将请假学生名单导入报到注册系统中。 3.申请复学的学生，先办理复学手续，补缴学费后再凭校园卡办理注册。 4.2016-2017学年秋季学期出国交流的学生不需要办理报到、注册手续。 5.非闵行校区的医学院学生请至重庆南路校区报到、注册。 6.闵行校区的留学生请至留学生办公室报到、注册。 报到、注册日学院（系）教务办须安排教师值班，处理学生诸如请假等特殊事宜，并安排班主任到学生宿舍了解学生假期思想动态。                                                        二�一六年七月二十二日                                                                  教务处 相关附件：   附件1、 2016-2017秋季学期老生报到注册地点.xls','2016-07-20 14:31','2016-07-20 14:31','','','教学信息服务网','2016-07-20 14:31:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(210628670,'2015-2016-3夏季学期《工程认知实践》上课通知','关于组织第十三期“上海交通大学大学生创新实践计划”项目学期检查的通知 教务处通知〔2016〕56号 根据《上海交通大学“大学生创新实践计划”项目管理办法》的规定，为加强项目过程管理，学校将对第十三期“上海交通大学大学生创新实践计划”项目进展情况进行学期检查。现将有关事项通知如下： 一、时间安排         7月25日-9月28日为项目组自查阶段，请各项目组充分利用这段时间，积极准备，合理安排，做好项目自查工作，认真填写《上海交通大学IPP项目学期检查报告》，同时登录大学生创新实践网填写学期检查报告。各项目组须在9月28日前将经指导教师审核并签署意见的纸质报告交至所在院（系）工作组负责老师。9月29日-10月21日为院（系）组织学期检查时间，要求院（系）工作组对各项目统一进行学期检查答辩评审。 二、检查内容与要求 1、检查项目组是否认真填写《学期检查报告》，提交中期检查材料。（系统操作详见附件1） 2、检查项目是否按计划进度组织实施，指导教师是否认真予以指导，项目是否取得初步成效等。 3、要求项目组及时发现项目实施过程中存在的问题，在指导教师的帮助下分析原因，总结经验，提出解决思路，并进一步明确下一阶段的任务。 4、要求项目组及时总结在项目实施过程中的心得与体会，特别是在创新思维和创新实践方面的体验和收获。 5、项目组成员若发生变更，请及时填写项目变更申请表，经指导教师、院系工作组签署意见并盖章后，交至工程训练中心B104。中期检查（10月21日）之后，不允许任何形式的项目成员添加。 注：第十三期大学生创新计划由院系作为管理主体对各项目进行管理，如院系大学生创新计划组有高于学校的管理要求参照院系管理细则执行，具体实施细则请咨询院系工作组 三、检查形式 1、各院（系）工作组要求采取统一答辩的形式进行学期检查。根据检查情况，在《学期检查报告》中“院（系）工作组意见”一栏签署审核意见和院（系）内排序建议，同时请登录大学生创新实践网录入评语。 2、各院系将于9月30日-10月21日组织专家对各项目进行公开答辩评审，所有项目和全体成员均需认真准备，同时准备好PPT演示材料。具体答辩安排由各院系工作组另行通知。 3、各院系请在答辩进行前一周将具体时间地点上报。 4、各院系于10月26日前将《大学生创新计划学期检查评审汇总表》电子版与打印文稿交工程训练中心B104。 四、检查电子材料 1、项目立项人请登录大学生创新实践网，于','2016-07-20 08:52','2016-07-20 08:52','','','教学信息服务网','2016-07-20 08:52:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1616548437,'第二季“未来人才培养――创新企业家校园行”暨上海交通大学“创新与创业大讲堂第74期”','上海交通大学第30期PRP（夏季学期专项）项目结题验收的通知 教务处通知〔2016〕55 号 根据工作安排， 第30期PRP（夏季学期专项）项目结题验收时间为9月12日―10月14日。具体工作由各部门PRP工作组统一组织实施。现将有关安排通知如下： 一、结题要求 1.参与学生 项目参与学生须对项目工作做全面总结，各自独立撰写研究论文（字数要求：不少于5000字），并将论文交由指导教师审阅。在9月23日前登录上海交通大学大学生创新实践网（http://202.120.35.20/ppa/）网上提交结题材料，认真填写“参与心得”，并将《PRP项目研究论文》最终版作为附件上传。研究论文格式按标准模板格式（http://www.jwc.sjtu.edu.cn/web/sjtu/198152-1980000000285.htm）撰写，文件大小不超过10M，页面不超过10页，文件格式为word。项目组全体成员必须参加结题答辩，凡不参加答辩者，成绩以“F”计（海外游学同学请按各院系工作组要求正常提交答辩材料或提前办理退出手续，否则按不参加答辩处理，成绩记为F）。 2. 指导教师 指导教师须认真审阅学生提交的结题材料，于9月28日前在网上填写结题表，并点击 “提交结题申请”。 对完成较好且取得一定成果的项目可申请优秀PRP项目，请填写《PRP优秀项目评审表》，交部门PRP工作组。 3.部门PRP工作组 （1）验收准备工作 梳理本部门第30期PRP（夏季学期专项）应验收项目，对指导教师已经确认学生的“学校通过待报名”的项目，调整状态至“准备结项检查”。 （2）答辩组织工作 各部门PRP工作组须组织专家对项目进行答辩验收。 （3）成绩评定 各部门应制定统一的答辩要求和评分标准，严把质量关。项目验收按优秀、合格和不合格三档评价，优秀项目不超过15%；学生成绩采用A、B、C、D、F（11级）记分方式，A级（含A+，A，A-）比例控制在25%以内。 （4）成绩录入 请各部门务必在10月14日前完成结题验收审核，并在网上录入PRP成绩。对未进行结题验收的项目，其最终的项目成绩记为“不合格”，学生个人成绩记为“F”。 （5）资料递交 各部门于10月21日前将《PRP（夏季学期）项目成绩单》、《PRP（夏季学期）优秀项目评审表》、《结题验收评审表》的打印文稿交教务处(新行政楼B311）。 二、其他事项 1.延期项目 PRP夏季学期项目执行时间较','2016-07-20 08:50','2016-07-20 08:50','','','教学信息服务网','2016-07-20 08:50:00.0');
