insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-894321495,'关于南京大学2016年C9高校暑期学校的通知','关于端午节放假期间通宵自习教室临时调整的通知 各位同学： 由于6月11日（端午节放假期间）学校将举行重要考试，使用东上院教室作为考场，届时将对东上院进行封楼。 因此，原东上院1-2楼的通宵自习教室将于端午节放假期间调整至东下院1-2楼所有教室，东下院通宵自习教室开放时间：2016年6月8日晚上18:00-6月12日上午7:00（东下院100、110不受此时间限制）。 请同学们留意以上变化。同时，提醒各位同学自习结束后勿将书籍等物品放置室内，以免遗失。                                教务处教学运行管理办公室                                    2016年5月31日 相关附件：  ','2016-05-30 16:58','2016-05-30 16:58','','','教学信息服务网','2016-05-30 16:58:00.0');
