insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1015718887,'上海交通大学第十二期校级大学生创新实践计划项目学期检查情况通报','                关于开放通宵自习教室的通知 各位同学：         应部分同学要求，学校决定自5月20日起开放闵行校区中院100、200和东下院100、110四间教室作为通宵自习教室。         5月30日（第15周周一）至6月24日（第18周周五），中院1至2层、东上院1至2层的所有教室也将开放为通宵自习教室。         特此通知。         通宵自习教室使用注意事项：         1.请同学们在每日早上6：30前离开自习教室并带走所有个人物品，以确保日间教学安排正常运行。         2.教室内的视频监控系统将保持24小时开通，请同学们注意文明自习，并自觉维护室内环境卫生。         3.如遇大型考试等须临时借用自习教室的情况，学校将另行通知。         教务处温馨提醒：建议同学们合理安排作息时间，尽量避免通宵学习。                                                上海交通大学教务处                                                   2016年5月20日 相关附件：  ','2016-05-19 16:42','2016-05-19 16:42','','','教学信息服务网','2016-05-19 16:42:00.0');
