insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2001899428,'上海交通大学关于2018年度夏季学期选课的通知','关于2015-2016 PRP(夏季学期专项)的立项申报通知 教务处通知〔2016〕026号 各学院（系）、直属单位： 为培养学生科学精神与创新能力，丰富2015-2016年夏季学期课程，加强夏季学期实践教学活动，现启动PRP(夏季学期专项)的立项申报工作。 实施PRP计划的目的，是使本科生有组织、有计划地参加科学研究工作，接受科学研究基础训练，培养学生的协作能力与团队精神，培养学生的独立思考能力和交流能力，提升创新精神和实践能力，从而提高学生的综合素质。PRP计划是我校“三位一体”本科创新人才培养体系中不可缺少的重要工作，现已纳入到我校“985工程”三期本科创新人才培养建设方案总体规划之中。 为了更好地开展PRP(夏季学期专项)的立项申报工作，现将有关事项通知如下： 一、立项申请条件 1. 项目申请人为本校具有中级及以上专业技术职称的教师。 2. 原则上每位教师每期最多申报一项夏季学期专项PRP项目（与正常PRP项目、暑期科研见习岗不冲突），每个项目参加学生不多于5名。 3. 项目申请人应敬业负责、教风学风端正，对本科生研究计划和申请的研究课题有强烈的兴趣和较深的认识，具有较强的组织与协作能力。 4. 项目选题要内容合适，难度适中，研究思路清晰、目标明确，具有创新性和可行性，研究成果具有可考核性，学生可在夏季学期内完成。 5. 项目执行时间为夏季学期（四周），学分为1学分。 6. 每个项目资助经费参照各部门制定的管理细则执行，学校会对正常结项验收的夏季学期PRP项目进行一定程度的倾斜补贴支持。 二、立项申请程序 1．立项申报时间：2016年4月6日 ―4月29日 2. 申报方式：登录上海交通大学大学生创新实践网进行申报操作（打开http://uitp.sjtu.edu.cn/→（左上方）校级PRP → 登录 （统一身份认证账号登陆）→ （创新实践管理平台）项目信息 → 项目查询/新的立项申请→第30期PRP（夏季学期）。 三、立项评审及审核 1. 部门评审时间：2016年5月2日 ―5月13日。 2. 部门评审内容：各部门组织专家对项目进行立项评审，部门负责人终审。 3. 部门审核要求：应避免与历期雷同的项目及与创新实践计划雷同的项目，避免项目执行期间因指导教师出国、不在岗位无法指导学生的情况出现。 （部门审核操作请参考附件――“PRP部门立项审核申请流程”、PRP专家立项评审流程） 四、学生选项时间 学生选项','2016-04-06 10:06','2016-04-06 10:06','','','教学信息服务网','2016-04-06 10:06:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(237337132,'关于发放 2017-2018 学年第二学期本科教学研究生助教（岗）额度的通知','关于第30期PRP的立项申报通知 教务处通知〔2016〕025号 各学院（系）、直属单位： 根据学校2015-2016学年第二学期教学工作安排，现启动第30期PRP立项申报工作。 实施PRP计划的目的，是使本科生有组织、有计划地参加科学研究工作，接受科学研究基础训练，培养学生的协作能力与团队精神，培养学生的独立思考能力和交流能力，提升创新精神和实践能力，从而提高学生的综合素质。PRP计划是我校“三位一体”本科创新人才培养体系中不可缺少的重要工作，现已纳入到我校“985工程”三期本科创新人才培养建设方案总体规划之中。 为了更好地开展第30期PRP项目的立项申报工作，现将有关事项通知如下： 一、立项申请条件 1. 项目申请人为本校具有中级及以上专业技术职称的教师。 2. 原则上每位教师每期最多申报两项，同时指导的项目一般不超过两项，每个项目参加学生不多于5名。 3. 项目申请人应敬业负责、教风学风端正，对本科生研究计划和申请的研究课题有强烈的兴趣和较深的认识，具有较强的组织与协作能力。 4. 项目选题要内容合适，难度适中，研究思路清晰、目标明确，具有创新性和可行性，研究成果具有可考核性。 5. 项目执行时间一般为一学期，少数大型、综合性项目可执行一学年。 6. 每个项目资助经费参照各部门制定的管理细则执行。鼓励社会团体和企业对申请项目进行经费资助。 二、立项申请程序 1．立项申报时间：2016年4月6日 ―4月29日。 2. 申报方式：登录上海交通大学大学生创新实践网进行申报操作（打开http://uitp.sjtu.edu.cn/→（左上方）校级PRP → 登录 （统一身份认证账号登陆）→ （创新实践管理平台）项目信息 → 项目查询/新的立项申请）。 （申请流程操作请参考附件――“PRP立项流程图”、“PRP申请流程”） 三、立项评审及审核 1. 部门评审时间：2016年5月2日 ―5月13日。 2. 部门评审内容：各部门组织专家对项目进行立项评审，部门负责人终审。 3. 部门审核要求：应避免与历期雷同的项目及与创新实践计划雷同的项目，避免项目执行期间因指导教师出国、生育等不在岗位无法指导学生的情况出现。 （部门审核操作请参考附件――“PRP部门立项审核申请流程”、PRP专家立项评审流程） 四、学生选项时间 第一轮学生选项时间：2016年5月30日 ― 2016年6月10日。 第二轮学生选项时间：2016年9月12','2016-04-05 14:04','2016-04-05 14:04','','','教学信息服务网','2016-04-05 14:04:00.0');
