insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1654216582,'关于2014年度上海交通大学“教务管理工作优秀奖”评审结果的通知','上海交通大学关于2015年节假日及课程调整安排的通知 各院（系）： 根据上海交通大学关于2015年节假日放假的通知我校2015年节假日放假调休及调课安排具体如下： 　一、元旦：1月1日至3日放假调休，共3天，1月4日（星期日）上班，教学安排按2014-2015学年秋季学期第16周周五课表执行。原安排在1月2日的考试等其他教学活动调至1月4日进行。 二、清明节：4月5日放假，4月6日（星期一）补休。 三、劳动节：5月1日放假。 四、端午节：6月20日放假，6月22日（星期一）补休。 五、中秋节：9月27日放假。 六、国庆节：10月1日至7日放假调休，共7天，10月10日（星期六）上班，教学安排按2015-2016学年秋季学期第3周周五课表执行。 第二学科教学按上述安排执行。 请各院（系）及时通知任课教师和学生，并协助做好相关教学安排的组织、协调工作。 上海交通大学教务处 2015年9月8日 相关附件：  ','2015-09-17 09:51','2015-09-17 09:51','','','教学信息服务网','2015-09-17 09:51:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1333425360,'转发：留学生发展中心关于2016年本科生中法4+4双学位项目学生选拔通知','上海交通大学医学院2016级八年制(4+4)报考指南        为培养综合素质高、科研潜力强，具备良好的思想品德和职业道德，广泛的人文社会科学知识，较宽厚的自然科学基础，较扎实的医学知识，以及较强的创新意识和创新能力的高级复合型、应用型医学人才，以促进医学教育更好地适应社会发展的需要,更好地适应科学技术发展的需要,更好地适应我国卫生事业发展的需要,学校已于2002年起,每年从重点综合性大学选拔招收部分优秀本科毕业生攻读医学博士。     上海交通大学医学院正敞开胸怀,热烈地期待着你的到来,欢迎你加入到献身祖国医学事业的行列。详细请见附件 相关附件：   附件1、 2016级八年制(4+4)报考指南20150911.docx','2015-09-17 09:32','2015-09-17 09:32','','','教学信息服务网','2015-09-17 09:32:00.0');
