insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1294670025,'上海交通大学关于2015年“万人计划”教学名师推荐名单的公示','关于“2015年暑期本科生科研见习岗位”结项的通知 各院（系）、科研院所、实验室（中心）： 根据学校实践教学工作安排，“2015年暑期本科生科研见习岗位”工作已进入结项阶段。现将有关工作通知如下： 一、 结项时间 2015年9月14日-2015年10月9日。 二、 结项要求 1. 参与学生 参与学生须对自己在岗期间的工作情况和收获体会进行全面总结，认真填写《暑期科研见习岗位总结报告》（字数要求：2500-3000字），于9月25日前登录上海交通大学大学生创新实践网（http://uitp.sjtu.edu.cn/）填写结项材料，并将《暑期科研见习岗位总结报告》作为附件上传，同时在9月25日之前将《总结报告》纸质材料（一式二份）交于指导教师签署评价意见（请参与暑期科研见习岗位的同学按时提交《总结报告》，否则没有成绩）。申请2个学分的学生须同时提供相关佐证材料，与《总结报告》一并交指导教师审核。 2. 指导教师 指导教师在2015年9月30日之前登录大学生创新实践网，对学生在岗期间的工作表现进行考核并给予评价。凡认真完成科研见习岗位工作并提交总结报告的学生，一般可获得1个学分。对于在见习过程中通过相对独立的工作取得了有一定显示度的成果的学生，学分数可酌情增加，但一般不超过2个学分（申请2个学分的学生须同时提供相关佐证材料）。所有学生成绩均计为P。学生获得的该类学分可作为培养方案中的个性化学分或任意选修课学分记入学业档案。 指导教师须在学生的纸质《暑期科研见习岗位总结报告》上签署评价意见，给予学分认定，于9月30日前将相关材料交至相关院（系）教务办。 3. 院（系）审核 各院（系）负责人认真审核并签署意见，于10月9日前将签字和盖章后的《总结报告》一份（另一份留院系存档）和申请2个学分的学生提供的相关佐证材料交教务处实践办。 三、 网上操作流程 参与学生和指导教师登录“上海交通大学大学生创新实践网”（http://uitp.sjtu.edu.cn/，点击页面左方的“创新实践活动平台登录”）。进入系统后，点击左上方的“结题”进行相关输入操作。 联 系 人：田夏 联系电话：34206429-8003 电子信箱：tianxiabest@sjtu.edu.cn 教务处 2015年9月10日 附件：上海交通大学暑期科研见习岗位总结报告 相关附件：   附件1、 上海交通大学暑期科研见习岗位总结报告.docx','2015-09-10 15:03','2015-09-10 15:03','','','教学信息服务网','2015-09-10 15:03:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1429131941,'上海交通大学2014-2015学年夏季学期第三轮选课通知','       关于开放2015-2016学年秋季学期自修教室的通知 各位同学:     新学期好！     为给同学们提供一个良好的学习环境，同时保障学校正常教学秩序，学校决定在教学优先使用的前提下，开放以下教室供同学们自习使用：     开放教室：     西区：中院1至2层所有教室；     东区：东上院1至2层所有教室；     开放时间：     2015年9月14日至2016年1月3日（含节假日）7：30―22：30（正常教学安排时间除外）。         如遇大型考试等临时占用教室的情况，学校会另行通知；     请同学们自习后，勿将书籍等物品放置室内，以免遗失；请自觉维护室内环境卫生。                                                         教务处教学运行管理办公室                                                              2015年9月10日 相关附件：  ','2015-09-10 14:49','2015-09-10 14:49','','','教学信息服务网','2015-09-10 14:49:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-481881743,'上海交通大学关于2015年全国大学生数学建模竞赛报名参赛通知','第二十八期PRP项目进入执行中（第二轮报名开始） 各项目立项人、参与同学： 第二十八期PRP项目的执行期是2015-2016学年第一学期，凡已有学生报名并已被参与人确认的项目，即日起项目进入执行中。 目前尚有一些项目有多余名额，项目处在“学校通过待报名”状态，有意参与第二十八期PRP项目的同学，请在第1-2周内尽快登陆学校创新实践网在线选项报名，并提请项目立项人确认，使项目及早执行。 为便于同学们在新学期学习计划的调整， 在PRP中期检查之前，各项目可以提出新增或退出人员的申请。 新增：由项目立项人提出并填写《PRP中期检查信息变更表》，中期检查时交由各部门PRP工作组作添加处理。PRP中期检查后不接受添加申请。 退出：本期项目报名且已被确认的同学，确因有困难本学期不能参与的，经指导教师同意，可以办理退出。退出手续：填写《PRP项目组成员退出申请表》（http://uitp.sjtu.edu.cn文档下载），向指导教师提交PRP退出申请表，指导教师签字后交各部门PRP工作组审批，由各部门PRP工作组做退出处理。PRP中期检查后不接受退出申请。 友情提醒： 1. 已报名还未确认的学生提醒立项人尽快确认； 2. 项目立项人还未确认报名学生的请尽快确认并及早执行项目。 网站开放时间2015年9月 14日-9月25日，请相互转告。 联系我们： 34206429-8003   prp@sjtu.edu.cn 新行政楼B325室 教务处 2015年9月10日 相关附件：  ','2015-09-10 10:41','2015-09-10 10:41','','','教学信息服务网','2015-09-10 10:41:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1609946394,'关于2015届本科毕业生在线调查发布的通知','                                   2015年12月全国大学英语四六级考试报名通知     2015年12月全国大学英语四、六级考试报名工作已经开始，考试的项目包括大学英语四级和大学英语六级。具体通知如下： 一、考试时间     大学英语四级：     2015年12月19日上午9：00―11：25；     大学英语六级：     2015年12月19日下午15：00―17：25。 二、考试报名资格 1.上海交通大学全日制专科、本科、研究生在校生； 2.各类全日制成人高等院校本科、专科在校生； 3. 大一年级春季学期考生方可首次报名CET4； 4.大学英语四级考试成绩达到425分以上（含425分）或2005年6月以前已获得大学四级证书的在校学生，修完大学英语六级课程可报考CET6。 5.从2012年6月开始，每位考生在大学阶段只允许报名三次CET6考试（包括因故或无故缺考）（低于425分除外）。 6.每次考试只能报一个类型，不能同时报考英语四、六级考试。一经发现，取消考试资格，报名费不退，责任自负。 7.医学院大二（含大二年级）以上（重庆南路校区）的学生请到医学院考点报名考试。 注：一经发现不符合上述报名条件者，取消报名，报名费不退，谢谢配合。 三、考试报名方式：     上海交通大学闵行校区本科生在2015年9月14日上午9：00---9月23日下午16：00之间登入等级考试报名系统（网址：http://ks.sjtu.edu.cn/）进行网上报名，用户名和密码为学号和选课网密码。报名时请仔细核对本人信息（特别是手机号码），如有问题请登入教学信息服务网，选择“我的个人设定”-----“个人信息”，修改错误信息。修改完成后重新登入等级考试系统查看个人信息是否已更正。如有问题请至新行政楼B112咨询解决。     网络学院、成人教育学院、研究生院学生报名请咨询所在学院；非本校在校学生不能报名，医学院已回重庆南路校区的学生请去医学院考点报名考试。 四、考试报名收费     本次大学英语四级、大学英语六级报名费：25元/科。     本次四六级报名收费方式为等级考试网报名后直接网上付费的方式，网上付费成功报名才视为有效，请各位同学相互转告（付费详细流程见等级考试网相关通知）。    注意事项： 1、 请学生务必于2015年9月24日--10月7日去院系教务办核对报名信息，并签字确认，','2015-09-10 08:53','2015-09-10 08:53','','','教学信息服务网','2015-09-10 08:53:00.0');
