insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(568585204,'通识教育核心课程选课手册（2015-2016-1）','工程训练中心集思空间入驻通知         为更好地服务院系，服务师生，实现工程训练中心的全面开放，为全校科技创新人才提供一个开放的，共享的，公共的创意、创新、实践平台。在整合了现有的学生科创工作室、实学创新工坊等资源基础上，筹建了一个规模较为集中的促进交流、体验分享、特色鲜明，立足于学生科技创新、实践能力培养的开放式活动空间--集思空间。         根据需要，空间内设置团队工作区、分享讨论区、加工区、展示区等功能区域，准工业级3D打印、激光切割、台式机床等设备、工具一应俱全。         空间将于暑假建成，新学期开学全面对外开放。即日起面向全校，招募学生科技创新团队入驻，申报表格下载见附件。         联系人：工程训练中心  李老师         表格发送邮箱：ljs@sjtu.edu.cn 工程训练中心 2015年7月8日 相关附件：   附件1、 集思空间申请表.docx','2015-07-08 10:04','2015-07-08 10:04','','','教学信息服务网','2015-07-08 10:04:00.0');
