insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1369359152,'第十期“上海交通大学大学生创新实践计划”学期检查暂缓通过项目二次检查答辩公告','                     关于发布2014-2015学年春季学期期末考试安排的通知 各位同学： 2014-2015学年春季学期期末考试已经发布，现将相关事项通知如下： 一、 期末考试查询网址： http://electsys.sjtu.edu.cn， 可登录查询相关考试安排。 二、 期末考试时间为：2015年6月23日-2015年7月3日，时间段细分如下： 1) 上午一     8:00―10:00 2) 上午二     10:30―12:30 3) 下午一     13:10―15:10 4) 下午二     15:40―17:40 5) 晚上       18:00―20:00 因期末考试期间天气炎热，学校在安排上尽可能避开下午一时间段，个别考试排在下午一，请同学们谅解。 三、 马克思主义基本原理、毛泽东思想和中国特色社会主义理论体系概论、思想道德修养与法律基础、中国近现代史纲要，这四门课程都是开卷考试，实际考试时间为一个半小时，请同学们注意具体考试时间。 四、 涉及体育班和国际教育学院的考试，本次未发布的课程考试，由学生所在院系另行通知。 五、 特别提醒： 1) 考生须持有带有本人清晰头像的校园卡或身份证原件等有效身份证件考试，无证件者不得参加考试； 2) 考生在考试过程中禁止使用手机等无线通讯设备。 3) 考生应提前10分钟进入考场，迟到15分钟及以上者不得参加考试。 4) 闭卷考试时，考位周围不得放置任何教材或参考资料；开卷考试时，不得使用电脑及同类电子设备，不得相互借阅纸质参考资料。 5) 考生在考试过程中如有特殊情况须暂离考场（如去洗手间）时，须经监考人员同意后，由一名监考人员陪同。 6) 考试结束铃响时，考生应立即停止答卷，并留在座位上等候，由监考人员统一收卷,监考人员对收回的试卷数核对无误后，方可离开考场。 请同学及时登录教学信息服务网（ http://electsys.sjtu.edu.cn），查询期末考试安排，合理安排时间进行备考。 有任何相关问题请及时反馈至：zhangminfang@sjtu.edu.cn,联系电话34206465－25。 预祝各位同学期末考试取得理想成绩！                                                                                   教务处教学运行管理办公室      ','2015-05-26 15:47','2015-05-26 15:47','','','教学信息服务网','2015-05-26 15:47:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2002237827,'创新与创业第五讲：用今天的知识去解决明天的问题 �C “创业”与“学力”','上海交通大学 2014、2015 “上海大学生创新活动计划”创新训练项目最新公告 教务处通知〔2015〕39号 根据上海市教委《关于实施2014年度上海大学生创新活动计划的通知》（沪教委高〔2014〕31号）、《关于实施2015年度上海大学生创新活动计划的通知》（沪教委高〔2014〕55号）：学校于2014年12月，从第十期“上海交通大学大学生创新实践计划”项目中升级50个项目作为2014年“上海大学生创新活动计划”创新训练项目；于2015年3月，升级45个项目作为2015年“上海大学生创新活动计划”创新训练项目。为保证上海市级项目的质量，学校在项目管理过程中实行动态管理，对表现不佳的已升级的“上海大学生创新活动计划”创新训练项目给予项目降级处理，直至中止；同时遴选优秀的项目作为2015年“国家级大学生创新创业训练计划”创新训练项目。 根据学校组织的第十期“上海交通大学大学生创新实践计划”项目学期检查结果，上述95个上海市级项目19项被学校予以降级处理（见教务处通知〔2015〕24号）。同时经择优选拔，推荐25个优秀上海市级项目为2015年“国家级大学生创新创业训练计划”创新训练项目。拟递补44个优秀第十期校级项目为2014、2015年“上海大学生创新活动计划”创新训练项目。 根据各院（系）组织的第十一期“上海交通大学大学生创新实践计划”立项评审排序，55个项目作为2015年“上海大学生创新活动计划”创新训练项目（见教务处通知〔2015〕17号），其中22项项目升级为2015年“国家级大学生创新创业训练计划”创新训练项目，拟递补22个第十一期优秀校级项目为2015年“上海大学生创新活动计划”创新训练项目。 特此公告。                                               教务处 2015年5月26日 附表1：2014年度“上海大学生创新活动计划”创新训练递补项目 上海市级项目编号 校级编号 立项人 项目名称 指导教师 立项人所在院系 IAP7151 IPP10029 蒋鹏 三模式复合运动机器人的研究 费燕琼 机械与动力工程学院 IAP7152 IPP10030 叶维熊 一项可压缩垃圾并可便携处理垃圾的垃圾桶 丁烨 机械与动力工程学院 IAP7153 IPP10033 陈刚 仿人机器人足球场地方向识别及校正 冷春涛 机械与动力工程学院 IAP7154 IPP10040 张钰鹏 ','2015-05-26 15:47','2015-05-26 15:47','','','教学信息服务网','2015-05-26 15:47:00.0');
