insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(344706611,'机械与动力工程学院转专业面试安排','关于第八届全国大学生创新创业年会、第四届上海大学生创新活动论坛参会项目校内选拔的通知 教务处通知〔2015〕37 号 根据教育部高教司和上海市教委相关通知，我校将开展第八届全国大学生创新创业年会、第四届上海大学生创新活动论坛参会项目和论文的校内选拔，现将有关事项通知如下： 一、报名类别 报名类别分为以下二类： 1、学术论文 学术论文应来源于校级大学生创新计划（参与全国大学生创新创业年会的选拔项目须为国家级大学生创新项目），如已在正式出版物上发表且标明了获得大学生创新计划项目资助的学术论文，请标明发表的刊物名称等相应信息。 学术论文内容主要反映学术研究情况（包括研究目的、方法、主要观点及结论等），由本科生为主完成（格式要求详见附件）。 2、项目展示 创新项目应来源于校级大学生创新计划（参与全国大学生创新创业年会的选拔项目须为国家级大学生创新项目），以展板展示为主，实物可同步展示。展板包含如下信息：项目名称、项目编号、项目简介、图片（含图表）、创新点描述（200字左右）、项目成员信息、项目指导教师信息等。如基于项目成果取得高水平竞赛成果，请注明竞赛名称、级别与获奖时间。 二、遴选安排 1、请于6月7日之前，将论文（格式参考附件2）或展板（格式参考附件3）电子版材料以邮件形式发送至PRP@sjtu.edu.cn，邮件标题格式为：项目编号-报名类别-联系人-项目名称（如：201210248001―论文―张三―关于XXXX的研究）,同时附上联系方式。 2、学校将于6月10日左右组织专家答辩评审，请做好准备。 3、创业项目的遴选安排由创业学院另行通知。 联系人：田夏 联系电话：34206429―8003 联系邮箱：tianxiabest@sjtu.edu.cn 联系地址：闵行校区新行政楼B325室 教务处 2015年5月21日 附件1：关于做好第八届全国大学生创新创业年会准备工作的通知 附件2：关于推荐第四届上海大学生创新活动论坛优秀项目的通知 附件3：论文格式要求 附件4：项目展板要求 相关附件：   附件1、 关于做好第八届全国大学生创新创业年会准备工作的通知【教高司函〔2015〕21号】.pdf 附件2、 关于推荐第四届上海大学生创新活动论坛优秀项目的通知.pdf 附件3、 论文格式要求.docx 附件4、 参展项目格式要求.docx','2015-05-21 14:54','2015-05-21 14:54','','','教学信息服务网','2015-05-21 14:54:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1275945154,'上海交通大学关于2015年节假日及课程调整安排的通知','上海交通大学大学生创新计划项目结题成绩公告 教务处通知 [2015] 036号 2015年5月13日，我校组织专家对第九期校级“大学生创新实践计划”（包括已升级为第七期“国家级大学生创新创业训练计划”创新训练项目、第七期“上海大学生创新活动计划”创新训练项目和前期未结题的校级创新项目）项目进行了结题答辩验收。经项目组申请，学院审核，全校共有155个项目取得学校答辩验收资格（不含医学院），其中“国家级大学生创新创业训练计划”创新训练项目23项，“上海大学生创新活动计划”项目49项，“上海交通大学大学生创新实践计划”项目83项，参加答辩学生共473名。 经专家评审，学校领导组终审，154项项目通过验收，其中46项项目成绩评定为“优秀”，项目优秀率为29.9%；473名学生取得大学生创新实践计划成绩和学分，其中149名学生成绩优秀，占获得成绩学生总数的31.5％。按照《上海交通大学大学生创新计划项目管理办法》有关规定，所有项目学生成绩和学分记入《上海交通大学学生学习成绩大表》，同时，学校将向通过项目验收的学生颁发项目结题证书。 教务处 2015年5月21日 附表：上海交通大学大学生创新计划项目结题成绩（2015年5月） 序号 项目编号 项目名称 立项人 指导教师 立项人院系 项目成绩 1 IAP7001 应用红外热成像技术进行流场量测的研究 杨帆 梁东方 船舶海洋与建筑工程学院 优秀 2 IPP9004 硅酸盐水泥基粘土固化剂的研究 万瑜 宋春雨 船舶海洋与建筑工程学院 通过 3 IPP9005 不同渗流条件下水土压力的试验与计算方法研究 周碧怡 陈锦剑 船舶海洋与建筑工程学院 通过 4 IPP9006 古塔建筑结构健康评估体系研究 孙赫 车爱兰 船舶海洋与建筑工程学院 通过 5 IAP7002 轻质泡沫土性能改善试验研究 道吉草 陈兵 船舶海洋与建筑工程学院 优秀 6 IPP9009 现代土木检测方法在古桥梁保护中的应用 黄赫 冯少孔 船舶海洋与建筑工程学院 通过 7 201410248003 深水浮式液化天然气生产平台水动力性能研究 王木 胡志强 船舶海洋与建筑工程学院 优秀 8 IAP7003 基于脑波分析技术的驾驶疲劳监测研究 郑昕泓 宋元斌 船舶海洋与建筑工程学院 通过 9 IAP7004 钢板混凝土中温度分布的试验研究 丁凯 宋晓冰 船舶海洋与建筑工程学院 通过 10 IAP7005 尾矿加筋路堤的稳定性和','2015-05-21 14:53','2015-05-21 14:53','','','教学信息服务网','2015-05-21 14:53:00.0');
