insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(713911659,'关于开展2015届本科生毕业设计（论文）中期检查工作的通知','                       上海交通大学 评选2014年度教务管理工作优秀奖的通知 教务处通知[2015]32号 各学院（系）： 为进一步提升我校本科教学管理工作水平，促进工作交流，表彰工作先进，学校决定结合年度考核，评选2014年度“上海交通大学教务管理工作优秀个人”和“上海交通大学教务管理工作优秀集体”。现将有关事项通知如下： 一、评选范围 “上海交通大学教务管理工作优秀个人”的评选范围是承担本科教学管理工作一年以上的人员。“上海交通大学教务管理工作优秀集体”的评选对象是学院（系）教务办公室（本科教务管理人员不少于2人）。 医学院2014年度教务管理工作优秀奖评选由医学院自行组织。如进行评选，须报教务处备案。 二、评选条件 “上海交通大学教务管理工作优秀个人”评选条件： 1. 爱岗敬业，工作责任感强，认真履行岗位职责。 2. 胜任本职工作，熟悉各项教务管理的常规工作。工作有计划，能及时、出色地完成学院和教务处交给的工作，且工作中没有明显失误或违规现象。 3. 工作踏实勤奋、积极主动，细致准确、认真负责、无差错，办事效率高。 4. 服务意识强，工作中任劳任怨，积极宣传学校有关教务管理工作规范，能为教师、学生做好有关教务管理和服务工作。 5. 创新精神强，能认真总结教学与教务管理工作规律，积极主动地思考和研究工作中出现的新情况，撰写教务管理方面的研究论文；积极向教务处等有关部门提出工作建议和意见，促进学校教学管理改革。 “上海交通大学教务管理工作优秀集体”评选条件： 1. 工作人员均爱岗敬业，工作积极主动，认真负责。 2. 工作人员均能够胜任本职工作，工作细致准确无差错，完成任务好，有较强的事业心和工作责任感。 3. 办公室各岗位职责明确，工作人员均有较强的集体荣誉感，合作能力强，配合默契，整体工作气氛融洽。 4. 同事间团结友爱，互相帮助，能开展批评与自我批评，自觉维护教务管理工作集体荣誉。 5. 工作人员服务意识强，宣传教务管理工作规定，主动为教师和学生服务。 6. 工作人员能够结合工作实际开展调查研究，积极向学院领导和教务处提出建议和意见；积极撰写教务管理工作的论文，或及时认真总结教务工作经验。 三、评选程序 凡申报2014年度“教务管理工作优秀个人”的人员必须填写《上海交通大学教务管理工作优秀个人申报表》，申报“上海交通大学教务管理工作优秀集体”的单位必须填写《上海交通大学教务管理工作优','2015-05-08 10:55','2015-05-08 10:55','','','教学信息服务网','2015-05-08 10:55:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-328801151,'生物医学工程学院2015年本科招生简介','时间：2015年5月13日（周三）下午， 3：00-4：30 地点：闵行校区东下院110 宣讲对象：理工类（除医科） 主讲人：Sylvain FERRARI（巴黎高科驻华主任）         Jean-François NAVINER（巴黎高科国立高等电信学校国际关系处主任）         Vincent BRENIER （巴黎高科国立高等矿业学校国际关系处主任） 巴黎高科项目简介： 自2000年以来，该项目在中国12所合作院校中选拔优秀本科生，前往法国巴黎高科攻读工程师或管理学的“Grande Ecole”（大学校）学位。录取学生在完成中国的本科学业之后，在法国进行为期两年或三年的学习，授课语言为法语（除HEC）。 录取学生均有申请法国和中国政府奖学金的机会。 项目具体介绍及申请页面：http://studywithus.paristech.fr/china-admission-programme50i 巴黎高科介绍： 巴黎高科（ParisTech）汇集了全法最具声望的11所工程师学校和1所商学院，其成员学校都在各自领域享誉盛名。 成员学校名单如下： 巴黎高科环境与生命科学工程学院(Agro ParisTech) 巴黎高科国立高等工程技术学校(Arts et Métiers ParisTech) 巴黎高科国立化学学校(Chimie ParisTech) 巴黎高科国立桥路学校(Ecole des Ponts ParisTech) 巴黎综合理工学院(Ecole Polytechnique) 巴黎高科国立统计与经济管理学校( ENSAE ParisTech) 巴黎高科高等先进技术学校(ENSTA ParisTech) 巴黎高科高等理工化工学校(ESPCI ParisTech) 巴黎高等商学院(HEC Paris) 高等光学学院(Institut d Optique Graduates School) 巴黎高科国立巴黎高等矿业学校(MINES ParisTech) 巴黎高科国立高等电信学校 (Télécom ParisTech) 外部链接： 巴黎高科官方网站www.paristech.fr 巴黎高科驻华办公室微博www.weibo.com/paristechchina 相关附件：  ','2015-05-08 10:29','2015-05-08 10:29','','','教学信息服务网','2015-05-08 10:29:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-555544048,'2014级自主调整专业报名通知','2015年校“优秀教师奖”及“烛光奖”评选工作通知 教务处[2015]31号 各院（系）、直属单位： 为了做好2015年校“优秀教师奖”及“烛光奖励计划”评选工作。现将有关事项通知如下： 一、校级“优秀教师奖”评选 1．评选对象 凡是担任本年度教学工作的教师（包括实验、实习指导老师、教学管理一线人员）都有资格向所在院（系）及有关部门申报优秀教师奖。 2．申报条件 （1）优秀教师奖 热爱教育事业，严谨治学，努力教书育人，教学效果好。 主动、积极承担教学任务，坚持在教学第一线，近三年每年至少为本科学生独立开设一门必修课或选修课（不包含第二学科课程的教学）。认真进行教学研究，积极开展教学改革，努力从事教学建设（含课程建设、教材和教学辅导材料编写、实验、电教、课程网站建设等），在教学改革、教学建设、教书育人和教学研究等方面都取得优秀成绩。 凡在本学年度中出现教学事故（如无故迟到、早退、缺课、擅自调课等）或在有关教学环节中违反教师工作规范或在学院（系）教学效果不佳者，不得参加本学年度的优秀教师奖的评审。 宝钢优秀教师奖的特殊要求： 近三年连续承担本科生教学任务，每学年完成本科公共基础课或专业基础课授课在64学时以上，且教学效果好，深受学生和同行好评。 （2）优秀教师（管理）奖 热爱教育事业，工作规范、高效，服务质量高，认真进行教学研究，为学校教学管理工作做出较大贡献。 凡在本学年度中出现教学事故或在工作中违反教学管理人员工作规范者，不得参加本学年度的优秀教师奖的评审。 3．名额测算与分配情况 各学院（系）推荐优秀教师奖候选人的名额按专任教师数进行测算，名额分配见附件1（注：推荐宝钢优秀教师奖的名额已含在内）。 “宝钢奖”、“优秀教师奖”特等、一等奖以及“烛光奖励计划”一等奖分配的是“参评名额”。“优秀教师奖”二、三等奖以及“烛光奖励计划”二等奖分配的是“获奖名额”。 今年宝钢特等奖的推荐人选（全校1个名额）从历年获得了宝钢优秀奖，但未获得宝钢特等奖的教师，及今年院系推荐参评宝钢优秀奖的教师中产生。 请学院（系）通知历年获得了宝钢优秀奖，但未获得宝钢特等奖的教师，自愿申报我校今年的宝钢特等奖推荐人选（全校1个名额）。 本次“优秀教师奖”包含医学院，具体评选工作由医学院自行组织，候选人报校教务处审核后公布。 4. 申请办法与评选程序 今年学校优秀教师奖评选仍分为两轮。第一轮评选由学院（系）组织。第二轮评选由学校组织。评选程序如','2015-05-07 16:21','2015-05-07 16:21','','','教学信息服务网','2015-05-07 16:21:00.0');
