insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1209858175,'关于2013级工商管理专业（BBA）招生报名的通知','上海交通大学大学生创新计划项目结题验收答辩公告 我校第九期校级“大学生创新实践计划”项目于2014年4月正式启动。按项目总体进程安排，已进入结题验收阶段（包括已升级为第七期国家级项目、第七期上海市级的项目和前期未结题的校级创新项目）。经项目组申请和院（系）评审，目前有155个项目（其中“国家级大学生创新创业训练计划”创新项目23项，“上海大学生创新活动计划”项目49项，“上海交通大学大学生创新实践计划”项目83项）获得学校答辩评审资格。2015年5月13日下午，教务处将组织全校性的项目结题验收答辩活动（医学院除外），具体答辩安排参见附件。 请各项目组所有成员提前30分钟到达答辩教室等候答辩（每个项目答辩时间约为20分钟：项目组汇报时间为12分钟，包含项目汇报时间及每名成员1-2分钟独立介绍自己工作的时间。专家提问8分钟，项目汇报时请重点阐述项目组所做的工作以及取得的成果，如有实物，请尽量将实物带至现场进行汇报演示）。各项目组PPT演示材料可以按照《项目结题报告》条目内容进行组织，也可以自己组织汇报内容，重点必须介绍项目组所做的工作、取得的成果及每个成员承担的工作（专家会为每名成员独立打分）,请注意掌控时间并请于5月13日12:30-12:50将PPT演示材料拷入答辩教室的电脑“桌面”。（项目组如遇预计答辩时间与上课时间冲突，持院（系）大学生创新工作组盖章的证明在12:50到答辩教室与答辩秘书协商适当调整答辩顺序）。项目成员务必全部到场参加答辩并做陈述，无故缺席者个人成绩以F计入成绩大表（如遇成员出国特殊情况，请各项目负责人务必在5月11日之前将院系盖章的相关情况说明纸质材料交到教务处实践教学办公室备案，并在答辩时由项目组其他成员介绍其工作内容和贡献，逾时将无法获得成绩和学分）。 欢迎全校广大教师和学生参与旁听。对于有意于参与或正在参与大学生创新计划的同学，这是一次难得的观摩和学习的机会。 教务处 2015年5月6日     上海交通大学大学生创新计划项目结题验收答辩安排表 答辩日期:2015年5月13日 组别 答辩顺序 答辩地点 预计答辩时间 项目编号 项目名称 立项人 其他成员 指导教师 立项人院系 1 1 东下院102 13:00-13:20 IPP9004 硅酸盐水泥基粘土固化剂的研究 万瑜 高亿文 古莉 王佳亮 宋春雨 船舶海洋与建筑工程学院 1 2 东下院102 13:20-13:40 IPP9005 ','2015-05-06 09:38','2015-05-06 09:38','','','教学信息服务网','2015-05-06 09:38:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1104297080,'2015年秋季本科交换生项目拟推荐学生名单公示（三）','第十期“上海交通大学大学生创新实践计划” 学期检查暂缓通过项目二次检查答辩公告 根据《上海交通大学第十期校级大学生创新实践计划项目学期检查情况通报》的要求，为加强项目过程控制，学校将于2015年5月13日下午组织专家对第十期“上海交通大学大学生创新实践计划”项目学期检查暂缓通过的项目进行二次答辩评审。 请参与二次答辩项目组的全体成员提前30分钟到达答辩教室等候答辩，项目成员务必全部到场参加答辩。答辩分为两部分，项目组PPT汇报10分钟，专家问答5分钟。答辩环节主要介绍项目整改情况（建议介绍整改方案、成员工作分工、投入保证等内容），并进一步明确研究目标和内容，制定科学、合理、可行的研究方案。 “上海交通大学大学生创新实践计划”采取“宽进严出”的管理方式，二次答辩中通过中期检查的项目，学校将下拨50%经费；未通过检查的项目学校予以中止，并收回全部下拨经费。请相关项目组予以重视。 教务处 2015年5月6日 学期检查暂缓通过项目二次检查答辩安排表 答辩教室 预计答辩时间 项目编号 立项人 项目名称 指导教师 立项人所在院系 东下院402 15:40-15:55 IPP10043 闫静雯 SA533钢力学行为的动态机械分析（DMA）研究 饶德林 机械与动力工程学院 东下院207 15:40-15:55 IPP10091 蔡思烨 基于DSP的无传感器BLDC控制的研究与实现 黄成军 电子信息与电气工程学院 东下院306 15:40-15:55 IPP10082 胡宇飞 视频图像智能校正算法与硬件实现 蒋剑飞 电子信息与电气工程学院 东下院405 15:20-15:35 IPP10122 代雨婷 半胱氨酸蛋白酶在植物绒毡层细胞凋亡中的功能研究 许杰 生命科学技术学院 相关附件：  ','2015-05-05 16:20','2015-05-05 16:20','','','教学信息服务网','2015-05-05 16:20:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1064438774,'上海交通大学关于2015年度夏季学期选课的通知','报告主题：用今天的知识去解决明天的问题 �C “创业”与“学力” 报告嘉宾：上海世瑞方华医院管理咨询公司创始人   陈宏勋 报告时间：2015年5月8日（周五）下午16:00-17:40 报告地点：东上院500号 嘉宾介绍： 上海交通大学安泰经济与管理学院2004级MBA，过去十年一直从事医院管理研究和咨询辅导工作，与国内外医院有学术交流合作，曾就任上海交通大学继续教育学院医院管理课程主任，离开学校后，创建上海世瑞方华医院管理咨询公司。 其主要研究方向为医院运营管理、医院经营与绩效管理，是《中医药经济报》、健康界的专栏作者，目前，为复旦大学附属儿科医院和深圳第九人民医院提供JCI认证辅导工作（医院流程和质量持续改善）。 请选上课的同学按时上课，不要迟到早退。 相关附件：  ','2015-05-04 09:29','2015-05-04 09:29','','','教学信息服务网','2015-05-04 09:29:00.0');
