insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2004397855,'【海外交流】法国巴黎高科9+9项目宣讲会通知','报告主题：物联天下・创业人生 报告嘉宾：深圳市安��信息技术有限公司 创始人、董事长、总经理   方先洋 报告时间：2015年4月24日（周五）下午16:00-17:40 报告地点：东上院500号 嘉宾介绍： 上海交通大学安泰经济与管理学院2001级MBA，先后在怡宝饮料、华润万家、爱施德等传统公司就职，2006年加入西安华讯，进入芯片高科技行业，成为中国最早从事民用导航芯片人员之一。 从2009年开始创业，先后创立了深圳市海威讯科技有限公司和深圳市安��信息技术有限公司。安��是中国物联网、车联网行业领先企业，也是中国移动、中国电信、中国联通的物联网和车联网项目核心合作伙伴；同时也是深圳前海自贸区深港创业梦工厂的典范公司。 方先洋还是中国人民解放军总装备部北斗办北斗导航芯片企业代表；深圳前海管理局智慧城市经营分析系统首席架构师；深圳前海自贸区深港创新梦工厂中国电信南方创新基地合作人；深圳软件产业基地智能硬件行业顾问；多家PE/VC/天使的北斗、物联网/智能硬件领域特聘尽调专家；多项PCT发明专利持有人。 请选上课的同学按时上课，不要迟到早退。 相关附件：  ','2015-04-17 15:12','2015-04-17 15:12','','','教学信息服务网','2015-04-17 15:12:00.0');
