insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-684954031,'【海外交流】日本京都大学、筑波大学、大阪大学、名古屋大学留学宣讲会','第十期“上海交通大学大学生创新实践计划”项目学期检查答辩公告 为加强项目过程控制，学校将于2015年4月1日下午组织专家对第十期“上海交通大学大学生创新实践计划”项目进行学期检查答辩。答辩分为两部分，项目组PPT汇报7分钟，专家提问5分钟,欢迎全校广大教师和学生参与旁听. 注意事项： 1. 各项目组PPT演示材料（可以按照《学期检查报告》条目内容进行组织，也可以自己组织汇报内容，重点必须介绍项目启动以来项目组所做的工作、取得的成果,请注意掌控时间，最好事先演练）请于4月1日12:20-12：50拷入答辩教室的电脑“桌面”。 2. 各项目组所有成员必须参加答辩，凡不能参加答辩的同学须履行请假手续，项目组成员无故缺席将取消项目组答辩资格。请各项目组根据“预计答辩时间”提前30分钟到达答辩教室。如果项目组答辩时间与课程（或考试）时间冲突，可以持院（系）大学生创新工作组盖章的证明在12:50到答辩教室与答辩秘书协商适当调整答辩顺序。 3. “上海交通大学大学生创新实践计划”采取“宽进严出”的管理方式，学校通过答辩等检查方式对各项目执行情况进行掌控，对执行不力或执行不认真的项目坚决给予淘汰；对进展缓慢、团队协作不佳等的项目暂缓通过学期检查，要求项目进行整改。已升级成为2014年或2015年“上海大学生创新活动计划”的校级项目若平均分处于本组后30%，给予降级处理，由其他优秀校级项目递补。所有项目组应对此次检查予以高度重视。 4.此次检查中表现优秀的项目将成为2015年“国家级大学生创新创业训练计划”创新训练项目的备选项目。 教务处 2015年3月24日 答辩安排表： 组别 答辩教室 答辩顺序 预计答辩时间 项目编号 立项人 项目名称 指导教师 立项人所在院系 1 东下102 1 13:00-13:12 IPP10016 马鹏飞 基于Scotch Yoke原理的高性能柴油发动机的创新研究 李铁 船舶海洋与建筑工程学院 1 东下102 2 13:12-13:24 IAP7011 曹星儿 新型SPS正交异性夹层钢桥面板的力学性能研究 程斌 船舶海洋与建筑工程学院 1 东下102 3 13:24-13:36 IAP7025 赵齐�� 多等分变形轮的设计及研究 郭为忠 机械与动力工程学院 1 东下102 4 13:46-13:48 IAP8064 傅其栋 RGB激光二极管合成白光光源 邓晓旭 物理与天文系 1 东下102 5 13:','2015-03-24 16:47','2015-03-24 16:47','','','教学信息服务网','2015-03-24 16:47:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1231792407,'关于推荐2015年上海普通高校优秀教材奖评选结果的公示','派遣过程中，如对方学校对合作项目的相关要求等有所更改，则以更改后的为准，如对方学校对合作项目的更改对交大学生利益有所损害，我校可能终止学生的派遣。上海交通大学不保证所有推荐学生都被录取，特此声明！ 各院（系）、各位同学， 根据我校与海外学校的学生交流交换协议，我校将继续选派优秀本科生至相关学校交流学习。本次选拔工作以学生自愿、择优推荐、公平公正为原则进行。现将选拔相关要求说明如下。 一、            报名基本要求： 1、  身体健康、品行端正,性格开朗，在校期间无违规违纪记录； 2、  成绩优秀； 3、  英语优秀，具体要求的请查看附件或对方网站查询； 4、  面向在校一二三年级本科生； 二、            申请材料： 1、  项目报名申请表（见附件） 2、  学院教务办开具的中文成绩单 3、  其他证明材料 三、            项目信息 项目时间：2015.7.5-2015.7.18 具体信息至对方学校网站：http://enterprise.nus.edu.sg/educate/summer-programme 可直接跟对方学校申请。 四、             特别说明： 1、  交流项目为直接向对方申请的项目，请根据对方网站的提醒，做好申请工作。与此同时，请务必办理我校校内备案（流程参考第三项）和校内审批手续（http://my.sjtu.edu.cn），未办理校内备案和出国审批的学生视为因私行为，不能视为学校公派学生。 2、  请有意申报的同学及早准备护照，确保买好交流期间的保险；不得提前前往参加项目，并在项目结束后立即回国。 3、  上海交通大学校历网址：http://www.jwc.sjtu.edu.cn/web/sjtu/198109.htm 4、  2015年春季学期于7月5日结束，夏季学期于8月2日结束，请各位同学确保自己申请的项目与学期安排无冲突。 5、  交流项目在推荐、派遣过程中，如对方学校有任何更改，以更改后的为准，上海交通大学不保证所有推荐学生都能推荐成功。 6、  后续若有新增项目，会发布在教务处主页（http://jwc.sjtu.edu.cn），敬请关注。 教务处 注册与学务中心 2015-3-24 相关附件：   附件1、 上海交通大学本科生海外交流项目申请审批表.doc','2015-03-23 16:48','2015-03-23 16:48','','','教学信息服务网','2015-03-23 16:48:00.0');
