insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2039586765,'2014-2015第二学期《计算机文化基础》考试相关事宜','上海交通大学 关于开展校级虚拟仿真实验教学中心评审工作的通知 教务处通知〔2015〕 020 号 各相关学院（系）、实验中心： 根据《教育部关于全面提高高等教育质量的若干意见》（教高〔2012〕4号）和《教育部办公厅关于开展2014年国家级虚拟仿真实验教学中心建设工作的通知》（教高厅函�z2014�{30号）的精神，决定开展上海交通大学虚拟仿真实验教学中心评审工作，并从中择优推荐国家级虚拟仿真实验教学中心。现将有关事项通知如下： 一、        申报范围 本次申报单位是我校相关的院（系）和直属中心，申报对象是我校国家级、上海市级和校级实验教学示范中心。校级虚拟仿真实验教学中心须依托上述示范中心而建设。 二、申报材料 申报单位须填写《上海交通大学虚拟仿真实验教学中心申请书》（以下简称《申请书》，见附件1）一式两份。 三、申报方式及时间 请各院（系）于4月24日之前将院系负责人签字并盖章的纸质《申请书》一式两份报送教务处实践教学办公室，同时将《申请书》电子版发送至zhangsm@sjtu.edu.cn，逾期不再受理。 四、评审方式 上海交通大学虚拟仿真实验教学中心的评审组织工作由教务处负责实施。 教务处组织专家采取材料审核、现场答辩和考察等方式进行评审。专家需网上审阅申报材料和访问实验教学中心网站。故各申报单位应保证实验教学中心网站正常开放。 联 系 人：张淑敏 联系电话：34206429 电子信箱：zhangsm@sjtu.edu.cn 联系地址：新行政楼B325室 附件1：上海交通大学虚拟仿真实验教学中心申请书 附件2：上海交通大学关于虚拟仿真实验教学中心建设和评审工作的 实施意见 附件3：上海交通大学虚拟仿真实验教学中心评估指标体系 附件4：上海交通大学各级实验教学示范中心一览表                                         教务处                                         2015年3月23日 相关附件：   附件1、 附件1 校级虚拟仿真实验教学中心申请书.docx 附件2、 附件2 关于虚拟仿真实验教学中心建设和评审工作的实施意见.docx 附件3、 附件3 上海交通大学虚拟仿真实验教学中心评估指标体系.docx 附件4、 附件4 上海交通大学各级实验教学示范中心一览表.doc','2015-03-23 11:16','2015-03-23 11:16','','','教学信息服务网','2015-03-23 11:16:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(587105300,'2014-2015学年春季学期重修班选课通知','宣讲会时间：3月27日下午16：00 宣讲会地点：闵行校区东中院1-200 宣讲人：巴黎高科代表团 项目背景： 巴黎高科（ParisTech）由法国十所最著名的工程研究生大学校联合组成，其各校都是享誉极高的百年老校。这十所大学校的名称是：国立桥路大学校（ENPC）、国立高等工程技术大学校（ENSAM）、国立高等先进技术大学校（ENSTA）、国立高等电信大学校（ENST）、巴黎高等理工化工大学校（ESPCI）、 巴黎综合理工大学校（Ecole Polytechnique）、国立林业、水和环境大学校（ENGREF）、 国立巴黎高等化学大学校（ENSCP）、国立巴黎高等矿业大学校（ENSMP）、国立农艺大学校（INA P-G）。从2000年起，巴黎高科每年给中国9所大学50个研究生名额（平均每校5�C6个名额），资助中国本科毕业生赴法国攻读工程硕士学位。这个项目俗称“9 + 9项目”，即法国巴黎高科辖下9所大学校与中国9所著名大学（清华大学、北京大学、中国农业大学、南京大学、东南大学、南京农业大学、复旦大学、同济大学、上海交通大学）对口交流，接收这些院校的学生。 相关附件：  ','2015-03-23 11:10','2015-03-23 11:10','','','教学信息服务网','2015-03-23 11:10:00.0');
