insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(321074786,'关于开展校级全英语教学课程建设项目申报工作的通知','关于组织第十期“上海交通大学大学生创新实践计划”项目 学期检查的通知 教务处通知〔2015〕 003号 根据《上海交通大学“大学生创新实践计划”项目管理办法》的规定，为加强项目过程管理，学校将对第十期“上海交通大学大学生创新实践计划”项目（包含第九期之前尚未结题的IPP项目）进展情况进行学期检查。现将有关事项通知如下： 一、 时间安排 1月12日-2月27日为项目组自查阶段，请各项目组充分利用寒假时间，积极准备，合理安排，做好项目自查工作，认真填写《上海交通大学IPP项目学期检查报告》，同时登录大学生创新实践网填写学期检查报告。各项目组须在3月6日前将经指导教师审核并签署意见的纸质报告交至所在院（系）工作组负责老师。3月9日-3月18日为院（系）组织学期检查时间，要求院（系）工作组对各项目执行情况认真评审并排序，并于3月20日之前将学期检查报告交至教务处实践教学办公室并完成网上评审工作。4月1日左右为学校组织统一学期检查答辩时间。 二、 检查内容与要求 1、 检查项目组是否认真填写《学期检查报告》，是否按要求在大学生创新实践网上填写项目日志，提交中期检查材料。（系统操作详见附件1） 2、 检查项目是否按计划进度组织实施，指导教师是否认真予以指导，项目是否取得初步成效等。 3、 要求项目组及时发现项目实施过程中存在的问题，在指导教师的帮助下分析原因，总结经验，提出解决思路，并进一步明确下一阶段的任务。 4、要求项目组及时总结在项目实施过程中的心得与体会，特别是在创新思维和创新实践方面的体验和收获。 5、项目组成员若发生变更，请及时填写项目变更申请表，经指导教师、院系工作组签署意见并盖章后，交至教务处实践教学办公室。学期检查后，不允许任何形式的项目成员添加。 三、 检查形式 1、各院（系）工作组可采用多种方式进行学期检查（为了保证项目成绩的公平公正，建议院系采取答辩或者多名专家材料评审的方式完成院系学期检查）。根据检查情况，在《学期检查报告》中“院（系）工作组意见”一栏签署审核意见和院（系）内排序建议，同时请登录大学生创新实践网录入评语，并于3月20日前将《学期检查报告》纸质版统一交至教务处实践教学办公室。 2、学校将于4月1日左右组织专家对各项目进行公开答辩评审，所有项目和全体成员均需认真准备，同时准备好7分钟的PPT演示材料。具体答辩安排另行通知。 四、检查电子材料 1、项目立项人请登录大学生创新实践网，于3月6','2015-01-12 15:49','2015-01-12 15:49','','','教学信息服务网','2015-01-12 15:49:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1686669897,'大三大四(2011级和2012级）学生有关大学英语的问卷调查','上海交通大学第25、26期PRP项目结题验收的通知 教务处通知〔2015〕002号 根据工作安排， 第25期（执行时间一年）和第26期（执行时间半年）PRP项目结题验收时间为3月2日―4月3日。具体工作由各部门PRP工作组统一组织实施。现将有关安排通知如下： 一、结题要求 1.参与学生 项目参与学生须对项目工作做全面总结，各自独立撰写研究论文（字数要求：不少于5000字），并将论文交由指导教师审阅。在3月11日前登录上海交通大学大学生创新实践网（http://202.120.35.20/ppa/）网上提交结题材料，认真填写“参与心得”，并将《PRP项目研究论文》最终版作为附件上传。研究论文格式按标准模板格式（http://www.jwc.sjtu.edu.cn/web/sjtu/198152-1980000000285.htm）撰写，文件大小不超过10M，页面不超过10页，文件格式为word。项目组全体成员必须参加结题答辩。凡不参加答辩者，成绩以“F”计。 2. 指导教师 指导教师须认真审阅学生提交的结题材料，于3月18日前在网上填写结题表，并点击 “提交结题申请”。 对完成较好且取得一定成果的项目可申请优秀PRP项目，请填写《PRP优秀项目评审表》，交部门PRP工作组。 3.部门PRP工作组 （1）验收准备工作 梳理本部门第25、26期应验收项目，对尚未进入“准备结项检查”的项目，需逐个完成其前期各个状态的工作，直至进入“准备结项检查”状态。 （2）答辩组织工作 各部门PRP工作组须组织专家对项目进行答辩验收。具体答辩安排必须于答辩前一周报教务处实践办，填写《PRP项目答辩安排表》并将电子版发送至tianxiabest@sjtu.edu.cn，院（系）PRP工作组应对答辩情况进行检查，学校将组织专家对各院（系）PRP结题答辩情况进行现场抽查。院（系）PRP工作组应对答辩情况进行检查，并填写《PRP结题答辩院系巡查情况记录表》并交学校备案。 （3）成绩评定 各部门应制定统一的答辩要求和评分标准，严把质量关。项目验收按优秀、合格和不合格三档评价，优秀项目不超过15%；学生成绩采用A、B、C、D、F（11级）记分方式，A级（含A+，A，A-）比例控制在25%以内。 （4）成绩录入 请各部门务必在3月23--27日完成结题验收审核，并在网上录入PRP成绩。对未进行中期检查或结题验收的项目，其最终的项目成绩记为“不合格”','2015-01-12 10:13','2015-01-12 10:13','','','教学信息服务网','2015-01-12 10:13:00.0');
