insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1246636515,'【海外交流】2015年加拿大阿尔伯塔大学科研实习项目推荐学生名单公示','关于2014-2015学年秋季学期期末开放自修教室和通宵教室的通知 各位同学：     本学期临近期末，为方便大家复习迎考，学校从2014年12月22日至2015年1月16日（除2014年12月26日和27日外），开放下列教室供同学们自修使用：     西区： 中院1-2层所有教室；     东区：东上院1-2层所有教室；     其中，以上两栋教学楼的1层所有教室为通宵教室。     2014年12月26日和27日，因研究生入学考试，自修教室调整为：     西区：中院1-2层所有教室；                下院1-2层所有教室；     其中，以上两栋教学楼的1层所有教室为通宵教室。     在保证考试和上课使用后，同学们可以在这些教室内自修，请同学们注意劳逸结合，提高复习效率。同时，请保持室内环境干净、整洁，请勿将复习资料、贵重物品放置教室内，以免遗失，保洁人员将每天清洁教室。                                          教务处教学运行管理办公室                                              2014年12月22日 相关附件：  ','2014-12-22 16:51','2014-12-22 16:51','','','教学信息服务网','2014-12-22 16:51:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-706096256,'2014年12月全国大学英语六级考试对比考补报名通知','派遣过程中，如对方学校对合作项目的相关要求等有所更改，则以更改后的为准，如对方学校对合作项目的更改对交大学生利益有所损害，我校可能终止学生的派遣。上海交通大学不保证所有申请学生都被录取，特此声明！ 各位同学： 你们好！ 根据我校与其他高校的学生交流协议，我校将继续选派本科生至该校交流学习。选拔以学生自愿、择优推荐、公平公正为原则。 报名基本要求： 1、身体健康、品行端正，性格开朗，在校期间无违规违纪记录，在校期间无放弃交流项目的记录； 2、成绩优秀； 3、英语优秀，有特定要求的请查看“项目其他信息及要求”； 4、面向在校一二三年级本科生。 项目其他信息及要求： 学校 项目日期 项目申请日期 项目推荐名额 项目外语能力具体要求及项目说明 日本明治大学 2015.7.20―8.6 2015.1.7―2.11 对方决定 查看对方网站 韩国梨花女子大学 Session II 2015.8.6―8.20 截止日期：2015.5.20或者2015.7.1（具体介绍见附件）要参加本项目的同学需在4月15日前首先向交大报名备案，得到交大确认之后方可继续申请。联系人：张冬玲老师，邮件：dlzhang@sjtu.edu.cn 对方决定 查看附件或对方网站   日本明治大学：http://www.meiji.ac.jp/cip/english/programs/mjlp/summer.html 韩国梨花女子大学：http://cms.ewha.ac.kr/user/indexMain.action?siteId=summer 递交申请的截止日期：以对方网站公布为准 申请流程： 1、学生在规定时间内向对方学校提交符合要求的申请材料。 2、对方学校确定录取名单。 3、对方确认录取后，凭寄给交流生进入许可材料（邀请信）办理校内申请审批手续和出国/境审批手续（http://my.sjtu.edu.cn），同时办理赴对方学校的手续（护照、签证（通行证）、机票、保险等）。 校内申请审批流程： 下载《上海交通大学本科生海外交流项目申请/审批表》，填写完成前三部分并经院（系）签署意见盖章，去学院教务办开具中文成绩单后交至新行政楼B112岗位五。 备注1：本次公布的项目为直接向对方学校申请的项目，未办理校内审批和出国审批的学生，视为个人因私行为，不能视为学校公派学生。 备注2：请有意申报的同学及早准备护照。确保买好交流期间的保险；不得提前前往参加项目，','2014-12-22 16:21','2014-12-22 16:21','','','教学信息服务网','2014-12-22 16:21:00.0');
