insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-387592114,'第九期“上海交通大学大学生创新实践计划”学期检查暂缓通过项目二次检查答辩公告','上海交通大学 2014年“国家级大学生创新创业训练计划”创新训练项目最新公告 教务处通知〔2014〕 74号 根据《关于报送2014年国家级大学生创新创业训练计划立项项目的通知》（教高司函〔2014〕24号），学校在2014年7月，从第九期“上海交通大学大学生创新实践计划”项目中升级30个项目作为2014年“国家级大学生创新创业训练计划”创新训练项目。为保证该期国家级项目的质量，学校在项目管理过程中实行动态管理，对表现不佳的已升级的“国家级大学生创新创业训练计划”创新训练项目给予项目降级处理，直至中止。 通过学校组织的项目学期检查，上述30个国家级项目中，5项被学校予以降级处理（见教务处通知〔2014〕63号）,1项项目中止。同时，经择优选拔，拟推荐6个优秀校级项目递补为该期“国家级大学生创新创业训练计划”创新训练项目。 选拔说明：2014年7月升级成为“国家级大学生创新创业训练计划”创新训练项目在学期检查答辩中评分处于该组后30%的，给予降级处理（降为校级项目），由本组其他优秀校级项目予以递补。 教务处 2014年12月15日 附表：2014年“国家级大学生创新创业训练计划” 创新训练项目递补项目 国家级项目编号 项目名称 立项人 指导教师 立项人所在院系 201410248101 承受多重荷载作用的桥梁模型设计和制作 李林家 宋晓冰 船舶海洋与建筑工程学院 201410248102 基于微纳光波导的触摸屏技术 洪�M力 叶志成 电子信息与电气工程学院 201410248103 脉冲电流制备稀土镁合金半固态浆料及流变成形组织与力学性能研究 罗桂钊 刘文才 材料科学与工程学院 201410248104 用于太阳能电池的钙钛矿纳米吸光材料的制备和表征 邹健 尹屹梅 化学化工学院 201410248105 上海城镇职工对延迟退休政策的态度研究 唐韵 张录法 国际与公共事务学院 201410248106 FSAE赛车侧箱空气动力学和散热性能分析优化及测试 吴嘉俊 王福新 航空航天学院 相关附件：  ','2014-12-15 10:26','2014-12-15 10:26','','','教学信息服务网','2014-12-15 10:26:00.0');
