insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1199974241,'创新与创业第三讲：上海创新发展战略与大学生创业机遇','上海交通大学大学生创新计划项目结题成绩公告 教务处通知 [2014]  073号 2014年11月26日，我校组织专家对第八期校级“大学生创新实践计划”（包括已升级为第七期“国家级大学生创新创业训练计划”创新训练项目、第六期“上海大学生创新活动计划”创新训练项目和前期未结题的校级创新项目）项目进行了结题答辩验收。经项目组申请，学院审核，全校共有151个项目取得学校答辩验收资格（不含医学院），其中“国家级大学生创新创业训练计划”创新训练项目27项，“上海大学生创新活动计划”项目45项，“上海交通大学大学生创新实践计划”项目79项，参加答辩学生共480名。 经专家评审，学校领导组终审，151项项目通过验收，其中48项项目成绩评定为“优秀”， 项目优秀率为31.7%；480名学生取得大学生创新实践计划成绩和学分，其中151名学生成绩优秀，占获得成绩学生总数的31.4％。按照《上海交通大学大学生创新计划项目管理办法》有关规定，所有通过项目验收的学生成绩和学分记入《上海交通大学学生学习成绩大表》。同时，学校将向通过项目验收的学生颁发项目结题证书。   教务处 2014年12月3日   附表：上海交通大学大学生创新计划项目结题成绩（2014年11月） 序号 项目编号 项目名称 立项人 指导教师 立项人院系 级别 项目成绩 1 IAP6006 民族乐器古筝结构特性和声学机理的研究 吴凯斌 余征跃 船舶海洋与建筑工程学院 市级 通过 2 IAP6007 基于后屈曲大变形复合材料机身蒙皮的优化设计 张婧琰 乔丕忠 船舶海洋与建筑工程学院 市级 通过 3 IPP8003 泡泡迷宫的可建性研究 郭冠男 刘士兴 船舶海洋与建筑工程学院 校级 通过 4 IPP8004 基于力反馈控制的立管涡激振动研究 刘天枫 付世晓 船舶海洋与建筑工程学院 校级 通过 5 IAP6151 基于涡激振动的斑海豹胡须水动力学特性理论与实验研究 易涵镇 付世晓 船舶海洋与建筑工程学院 市级 通过 6 IAP6152 一种水平轴海流发电机优化设计及制作 蒋甫政 余龙 船舶海洋与建筑工程学院 市级 优秀 7 IAP6008 用于高铁无砟轨道损伤快速修复磷酸镁水泥研究 程小宣 陈兵 船舶海洋与建筑工程学院 市级 通过 8 IPP8009 电流强度对人工珊瑚礁生长的影响 缪瑜 王磊 船舶海洋与建筑工程学院 校级 优秀 9 IAP6010 新型防溅射独立小便池的界面形状','2014-12-02 16:55','2014-12-02 16:55','','','教学信息服务网','2014-12-02 16:55:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1116556708,'关于做好2015届本科生毕业设计（论文）工作的通知','创新与创业第五讲 报告主题：创业是一种生活方式 报告嘉宾：饿了么网上订餐联合创始人兼CEO  张旭豪学长 报告时间：2014年12月4日（周四）下午16:00-17:30 报告地点：东上院500号 嘉宾介绍：     张旭豪是中国餐饮O2O行业最早的探索者之一。早在2008年上海交大研究生在读期间，他与同学一起创业，09年“饿了么（ele.me）”正式上线。经过5年的发展，“饿了么”已成为中国最大的餐饮O2O平台，占市场份额的70%。截至2014年11月，张旭豪所带领的“饿了么”已经覆盖全国230多个城市，用户量1000万以上，加盟餐厅超过18万家，日均订单超过120万单。作为中国餐饮业数字化领跑者，“饿了么”秉承极致、激情、创新之信仰，以建立全面完善的数字化餐饮生态系统为使命，为用户提供便捷服务极致体验，为餐厅提供一体化运营解决方案，推进整个餐饮行业的数字化发展进程。    请选上课的同学按时参加，不要迟到早退。 相关附件：  ','2014-12-02 15:08','2014-12-02 15:08','','','教学信息服务网','2014-12-02 15:08:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1805974432,'上海交通大学第26期PRP项目中期检查的通知','各位同学： 大家好！现开展以色列理工学院的关于价值创新―冬季课程，以色列理工是以色列最好的理工大学, 有以色列MIT之称，欢迎有兴趣的同学申报。 一．基本信息 课程日期：2015年2月1日―2月12日 地点：以色列,海法 �C 以色列理工学院 讲师：Shlomo Maital 教授，《创新的天梯》（The Imagination Elevator）一书作者 费用：$2500 （包括住宿、伙食以及课外活动）；除此之外，需要自费机票、签证、保险等费用（预计约9000元人人民币）。 二．招生对象： 1.  工科与商科在读的及创业学院的二、三、四年级本科生； 2.  身心健康，品行端正，在校期间无违规违纪记录； 3.  品学兼优，善于创新； 4.  课程全程以英文授课，申请人必须有较好的英文能力。如果英文不是第一语言，将在对方收到报名申请后一周内要求参加一个英文网络面试以测试英文水平。 三．提交材料 直接向对方申请，按要求于2014年12月12日前完成。 项目具体信息，链接如下： http://int.technion.ac.il/current-students/academics/%E4%BB%B7%E5%80%BC%E5%88%9B%E6%96%B0-%E5%86%AC%E5%AD%A3%E8%AF%BE%E7%A8%8B/ 同时学生需要完成校内申请手续，未完成校内申请审批手续者，视为个人因私行为。详见第四条。 四．校内申请流程： 请有意向的同学下载附件1《上海交通大学本科生海外交流项目申请/审批表》，填写完成前三部分并经院（系）签署意见盖章，去学院教务办开具中文成绩单后于2014年12月12日下午16:30前交至新行政楼B112岗位五。 备注： 1、 请有意申报的同学及早准备护照。 2、 按照《上海交通大学本科生学分转换管理办法实施细则》（http://www.jwc.sjtu.edu.cn/web/sjtu/198150-1980000000215.htm），本项目不受理学分转换、学分豁免申请。 教务处  注册与学务中心 2014-12-2 相关附件：   附件1、 上海交通大学本科生海外交流项目申请审批表.doc','2014-12-02 14:32','2014-12-02 14:32','','','教学信息服务网','2014-12-02 14:32:00.0');
