insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1698132132,'【海外交流】2015年春季香港大学交换生项目推荐学生名单公示','创新与创业第二讲 报告主题：创业是一道证明题 报告嘉宾：格瓦拉生活网创始人、CEO  刘勇先生 报告时间：2014年10月23日（周四）下午16:00-17:30 报告地点：东上院500号 嘉宾介绍： 　　刘勇是中国电影在线选座购票领域早期探索者之一，在2009年创办了集城市娱乐资讯、消费、互动一体化的网络平台-格瓦拉生活网（简称“格瓦拉”）。依托用户体验为前提、服务至上的产品理念，格瓦拉创造性地建立了一站式消费服务平台，基于互联网、移动客户端和线下终端的多载体沟通，为广大消费者提供了在线选座购票、短信提醒、影院/剧场现场自助取票、运动场馆预订等服务。目前，格瓦拉已覆盖上海、北京、广州、深圳等近50个城市，为广大消费者提供精准、权威的本地化娱乐消费资讯。刘勇正带领团队着力将格瓦拉建设成国内城市泛娱乐生活网络服务平台的领导者。 “格瓦拉”，这座由刘勇一手创办起来的娱乐生活服务一站式交易平台，不仅深刻影响着上海乃至全国年轻一代娱乐消费习惯，并且也正以迅如疾风的速度，为传统影业阵地带来一阵革新之风。正如它的名字，“格瓦拉”象征着生活娱乐服务的“革命者”。 请选上课的同学准时参加，不要迟到早退。 相关附件：  ','2014-10-20 09:45','2014-10-20 09:45','','','教学信息服务网','2014-10-20 09:45:00.0');
