insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(150204397,'上海交通大学关于西安交通大学暑期学校报名的通知','                            关于开放2013-2014学年夏季学期及暑假自修教室的通知 各位同学：     为满足夏季学期和暑假在校学生的自习需要，同时为保障学校的正常教学秩序，经与教室管理组协商决定，在教学优先使用的前提下，开放以下教室供同学们自习使用：     夏季学期     西区： 中院 100、101、102、103、104、105、106、107、108、109、110、200、201、202、203、204、205、207、209；     东区：东下院101、102、103、104、105、106、100、110、200、210；     开放时间：以上教室在保障正常教学的前提下，2014年6月30日至7月25日，每天7:30-22:30开放。     暑假     西区：中院100、200；     东区：东下院110、100；     开放时间：2014年7月26日至9月12日，每天8:00-22:00；     暑期教室自修使用情况，学校会及时关注，如遇自修教室资源紧张情况，会再行调整，届时，请同学们留意相关通知。同时，提醒同学们夏季学期的课程安排集中于西区教室，请及时关注教室使用情况。     请同学们劳逸结合，注意安全，自觉保持室内卫生 。                                                         教务处教学运行管理办公室                                                               教室管理组                                                              2014年6月25日 相关附件：  ','2014-06-24 10:17','2014-06-24 10:17','','','教学信息服务网','2014-06-24 10:17:00.0');
