insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1117241755,'关于申报第九期“上海交通大学大学生创新实践计划”项目的通知','项目介绍： TOP CHINA交流项目由西班牙桑坦德银行全额资助，项目师生来自巴西顶尖的十所高校、上海交通大学以及北京大学。通过举办为期三周的夏季课程和一系列参观访问活动，促进中巴师生在专业、文化等方面的交流。 项目主题：Sustainability and Natural Resources Management 项目地点：上海交通大学徐汇校区、北京大学 项目日期：2014年8月16日至9月5日 项目交流语言：英语 项目人员构成：巴西高校师生、上海交通大学师生以及北京大学师生 项目费用：由桑坦德银行赞助日程中所列项目费用（含国内旅游费，火车票费，徐汇校区住宿费等） 项目招募人数：30人 本次项目包含48学时专业课程及讲座活动，将作为一门课程计入夏季学期，参与此次项目的同学视为选修3学分通识课程（需选上课）。 课程代码及名称：EV038，人与环境：可持续发展 课程考核方式：出勤（学生登记）（20%）+一篇读书报告（30%）+期末口试（50%） 项目行程： 日期（2014年） 日程 8月16日 巴西师生团到达上海交通大学 8月17日上午 北京大学师生团到达上海交通大学 8月17日下午及晚上 参观博物馆、城市规划馆和夜游浦江 8月18日 上海一日游（游览陆家嘴、上海环球金融中心、世博园、豫园、城隍庙、上海老街） 8月19日上午 项目开幕式 8月19日下午 上海（巴西）师生团素质拓展训练 8月20日上午 北京大学师生团返回北京； 8月20日-8月22日 专业课、文化课、语言课以及公司参观 8月23日-8月24日 杭州、乌镇二日游 8月25日-8月30日 专业课、文化课、语言课以及结业汇报 8月31号下午 参观田子坊 9月1号 上海（巴西）师生团赴北京 9月1日-9月4日 参观北京、师生交流 9月4日 上海交通大学学生返回上海 学生选拔要求： 1、 英语优秀，专业课成绩优秀； 2、 具有较强的责任心、组织纪律性及优秀的团队协作能力； 3、 性格开朗，无违规违纪记录； 4、 能全程参与项目； 5、 2011级或2012级本科学生，及有一定个人才艺的学生优先考虑。 报名方式：提交《上海交通大学本科生TOP CHINA 项目申请/审批表》（见附件），完成前三部分后，交至教务处注册与学务中心（新行政楼B112）岗位4或岗位5 报名截止日期： 2014年3月31号下午3点 备注： 1、学校会统一安排闵行校区直达徐汇校区的班车。 2','2014-03-24 12:53','2014-03-24 12:53','','','教学信息服务网','2014-03-24 12:53:00.0');
