insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-697530326,'2014年SAF本科生寒假交流项目录取名单公示','各位同学： “2014年全国大学生英语竞赛”分为A，B，C，D四个类别，其中A类适用于研究生参加，B类适用于英语专业本科学生参加，C类适用于非英语专业本科生参加，D类适用于体育类和艺术类本科生和非英语专业高职高专类学生参加。以下是报名及竞赛时间安排： 特别提示：参加上海交大C类英语竞赛的考生，考试当天须自带收音机和耳机！！！  A、B类考生无需自带。    1. 报名时间：2014年1月6日到2014年1月10日（上午8：30-11:30；下午12:30-15:30） 2. 报名地点：闵行东区外国语学院209办公室：蒋老师处 3. 初赛时间及形式：2014年4月13日（星期日）上午9：00-11：00，笔试（总分150分，包括听力30分）； 4. 决赛时间及形式：2014年5月11日（星期日）上午9：00-11：00，形式有两种：只参加笔试（总分150分，包括听力30分）或者 笔试（包括听力）+口试（满分为50分）； 5. 参考书：2014年新版大学《英语奥林匹克丛书》，2014年1月上市； 6. 奖项：分为特等奖、一等奖、二等奖和三等奖（具体参阅组委会竞赛通知奖励方式相关内容）                                                         上海交通大学外国语学院                                                      2013年12月 相关附件：   附件1、 2014年全国大学生英语竞赛通知.doc','2013-12-26 13:14','2013-12-26 13:14','','','教学信息服务网','2013-12-26 13:14:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(39732457,'2013年12月全国大学英语四六级考试对比考报名通知','派遣过程中，如对方学校对合作项目的相关要求等有所更改，则以更改后的为准，如对方学校对合作项目的更改对交大学生利益有所损害，我校可能终止学生的派遣。上海交通大学不保证所有推荐学生都被录取，特此声明！ 各位同学： 你们好！ 根据我校与台湾交通大学的学生交流协议，我校将继续选派本科生至该校交流学习。选拔以学生自愿、择优推荐、公平公正为原则。 项目时间： 2014年7月3日至7月31日 项目费用： 课程费用：一组课程3200新台币，学生可选1组或2组课程，课程不能有冲突； 杂费：9000新台币； 住宿费：6000新台币； 往返机票费用约3000-4000元人民币（估计）； 餐费约1000-1500元人民币（估计）。 报名基本要求： 1、身体健康、品行端正,性格开朗，在校期间无违规违纪记录，在校期间无放弃交流项目的记录； 2、成绩优秀； 3、英语优秀（由交大本校确认）； 4、面向在校一二三年级本科生。 拟选派学生人数：6 名 申请截止日期：2014年3月3号下午3点 申请材料：（1）附件申请表，表格须完成前四部分           （2）学院教务办开具的中文成绩单           （3）其他证明材料 申请材料收集办公室：教务处注册与学务中心，闵行校区新行政楼B112室岗位4或岗位5 申请流程： 1、学生在规定时间内提交符合要求的申请材料。 2、学校（或对方）根据报名情况进行材料审核或面试，确定推荐名单。 3、学生办理护照（或通行证），准备对方学校的相关材料，对方确认录取后，凭寄给交流生进入许可材料（邀请信）至国际交流处出国出境服务中心（新行政楼B楼2F东侧B200号）办出国/境审批手续，同时办理赴对方学校的手续（签证（通行证）、机票、保险等）。 特别说明： 1、上海交通大学校历网址：http://www.jwc.sjtu.edu.cn/web/sjtu/198109.htm，2014春季学期于6月27日结束，夏季学期于7月25日结束，请各位同学确保自己的申请项目与学期安排无冲突。 2、报名阶段仅需提交本校交流申请表和学院教务办开具的中文成绩单，交流名单确定后，教务处会通知推荐学生准备对方申请材料，并告知推荐学生申请步骤或流程。 3、后续若有新增项目，会发布在教务处主页（www.sjtu.edu.cn）或教务处海外游学网（http://youxue.jwc.sjtu.edu.cn），敬请关注。 4、请学院协助做好学校统','2013-12-26 09:30','2013-12-26 09:30','','','教学信息服务网','2013-12-26 09:30:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(225534247,'2013-2014学年第二学期早稻田大学交换生项目拟推荐学生名单公示','关于上海交通大学2010年校教改立项 第一批验收通过项目的公示 教务处通知[2013]152号 为落实学校人才培养目标，深化与新一轮人才培养方案相结合的课程建设，学校于2010年启动了教学改革立项工作。根据《2010年上海交通大学本科教学改革立项研究项目指南》，学校于今年12月组织专家对第一批申报的教改项目进行了验收，验收通过的项目清单见附件，排序按院系名称拼音和专家评分高低顺排。 此公示有效期自公示之日起一周。如对公示结果有异议者，请与教务处教学改革与质量办公室联系。 联系方式：zhaoliu@sjtu.edu.cn。 联系电话：34206426-805。                                     教务处                                        二�一三年十二月二十六日 附件：     2010年校教改立项第一批验收通过的项目 序号 院系 教改项目名称 项目 负责人 1 工程训练中心 “机器人基础课程”远程互动课程建设 吕恰生 2 化工学院 综合实验训练二的建设与完善 章烨 3 环境科学与工程学院 环境工程实验课程改革方案的制定与课程实施 李春杰 4 机械与动力工程学院 以学生为中心的工程热力学I课程改革初探 童钧耕 5 农业与生物学院 《植物育种学》课程改革方案的制定与实施 陈火英 6 生命科学技术学院 通识教育中生命科学实践课程的研究与探索 曹阳 7 生命科学技术学院 通识教育核心课程《生命科学导论》的建设与改革 林志新 8 生命科学技术学院 “解剖与生理”课程教学大纲和方法的改革与探索 王莲芸 9 生命科学技术学院 通过国际合作创新“微生物学”教学模式的研究 赵立平、 陈峰 10 药学院 基于多元激发兴趣学习的“生药学”教学改革 李晓波 11 安泰经济与管理学院 公司金融系列课程实施标准制定 费一文 12 船舶海洋与建筑工程学院 建筑设计及原理（1）课程改革研究 范文兵 13 船舶海洋与建筑工程学院 工程流体力学课程问题导向型教学的改革探索 胡文蓉 14 电子信息与电气工程学院 《嵌入式系统原理与实践》课程实验教学体系建设 殳国华 15 电子信息与电气工程学院 工科电类基础课程教学实施标准 张峰 16 工程训练中心 任务驱动型电工电子基础实践教学模式研究 王留芳 17 国际与公共事务学院 《国际政治经济学导论》课堂实验与分组讨论 黄琪轩 ','2013-12-25 15:29','2013-12-25 15:29','','','教学信息服务网','2013-12-25 15:29:00.0');
