insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-191928669,'关于组织2013年度特色实验项目验收答辩的通知','各位同学： 你们好！ 经过审核和制作，2013级本科新生的学生证（副证）及外地生源学生的火车票优惠卡已经制作好，将于2013年12月10日前发放至各学院教务办。 请2013级本科新生于12月11日至12月18日间根据学院的通知至本学院领取学生副证，外地生源新生同时领取火车票优惠卡。 相关说明： 1、有部分新生因招生及学籍数据正在审核中，故暂不发放学生证（副证），相关学生可至学信网（网址：http://www.chsi.com.cn/）查询数据，能查到本人信息后，凭身份证至教务处注册与学务中心（新行政楼B112）领取。 2、以下两类型新生可于12月20号前至教务处注册与学务中心（新行政楼B112）现场补办火车票优惠卡： （1）未按时、按要求填写火车区间的学生。教务处无法采集这部分学生的数据，请先在教学信息服务网上登记火车区间再进行现场办理； （2）火车区间不同于本人生源地或家庭住址有变动的学生。请提交当地居委会住址证明或户口本复印件，至教务处现场审核更改。 火车票优惠卡充值及管理办法见教务处主页：http://www.jwc.sjtu.edu.cn/web/sjtu/198150-1980000000146.htm   教务处                                          2013-12-06 相关附件：  ','2013-12-06 17:05','2013-12-06 17:05','','','教学信息服务网','2013-12-06 17:05:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(947936249,'第八期“上海交通大学大学生创新实践计划”项目公示','有关院系：     国家留学基金管理委员会2014年“艺术类人才培养特别项目”选派工作已启动，现就选派工作有关事宜通知如下。     1、2014年“艺术类人才培养特别项目”选派范围、选派类别等请查阅附件《2014年艺术类人才培养特别项目本科插班生选派工作手册》（以下简称“手册”）（附件一）中的《2014年艺术类人才培养特别项目选派办法》（以下简称“选派办法”）。     2、选派专业领域：上海交通大学艺术类专业为艺术设计和广播电视编导两个专业。请媒体与设计学院做好本项目的通知、咨询和管理工作。请符合选派办法条件的上述两专业的二年级和三年级学生进行申请。     3、外语要求：学生申请人申请时外语须达到合格标准，具体外语要求也请查询手册中选派办法的相关内容。     4、时间安排：2014年项目网上报名及申请受理时间为2014年3月20日-4月5日，具体的网上报名方法请登陆留学基金委网站查询（www.csc.edu.cn）。学生申请人完成网上报名后应将纸质申请材料交给媒体与设计学院项目负责人。媒体与设计学院应当在4月6日前将推荐人选名单及学生申请材料交至教务处注册与学务中心。申请人应提交的申请材料以及媒体与设计学院项目负责人的联系方式请查询手册。     5、留学基金委审核后将公布被录取人员名单。被录取人员应及时办理相关派出手续。派出手续办理事宜请查询教育部出国人员上海集训部网站（http://jxb.shisu.edu.cn/）。请下载附件二《国家留学基金管理委员会出国留学人员须知》，了解国家公派出国留学人员须知事项。     6、有关本项目的其他信息，请务必登陆留学基金委网站（www.csc.edu.cn）查询。 教务处 注册与学务中心 2013年12月6日 相关附件：   附件1、 2014年艺术类人才培养特别项目本科插班生选派工作手册.pdf 附件2、 《国家留学基金管理委员会 出国留学人员须知》2013年版.pdf','2013-12-05 17:00','2013-12-05 17:00','','','教学信息服务网','2013-12-05 17:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-8141776,'转发：国际合作与交流处关于慕尼黑工业大学亚洲（TUM Asia）招生宣讲会的通知','关于第七期“上海交通大学大学生创新实践计划” 学期检查暂缓通过项目二次检查答辩情况通报                                                                           教务处通知〔2013〕147 号 根据《上海交通大学第七期校级大学生创新实践计划项目学期检查情况通报》的要求，为加强项目过程控制，学校于2013年11月27日下午组织专家对第七期“上海交通大学大学生创新实践计划”项目学期检查暂缓通过的项目进行二次答辩评审。 机械与动力工程学院李琛炜、张亚辉项目组，材料科学与工程学院田然项目组、化学化工学院赵倩在二次答辩中通过中期检查，学校将下拨第二批50%经费。船舶海洋与建筑工程学院杨智森、李卉项目组，化学化工学院罗卫剑项目组申请中止项目，学校将收回全部已下拨的经费。 教务处 2013年12月5日 附表：上海交通大学大学生创新实践计划项目学期检查暂缓通过项目二次检查情况通报 项目编号 项目名称 立项人 指导教师 立项人院系 检查结果 IPP7023 上海市车牌拍卖制度研究与优化 杨智森 胡昊 船舶海洋与建筑工程学院 中止 IPP7024 船东船旗选择行为的研究 李卉 尹静波 船舶海洋与建筑工程学院 中止 IPP7051 基于轻量化的发动机冷却系统优化 李琛炜 张勇 机械与动力工程学院 通过 IPP7044 多自由度蔬果采摘器 张亚辉 袁建军 机械与动力工程学院 通过 IPP7084 后处理参数对烧结钕铁硼永磁体性能影响规律的研究 田然 王福新 材料科学与工程学院 通过 IPP7113 金配合物催化烯烃氢硅烷化反应机理的理论研究 赵倩 仇毅翔 化学化工学院 通过 IPP7114 紫外光诱导改性SBS复合材料 罗卫剑 史子兴 化学化工学院 中止 相关附件：  ','2013-12-05 16:59','2013-12-05 16:59','','','教学信息服务网','2013-12-05 16:59:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1499709205,'转发:人文学院关于比利时荷语布鲁塞尔自由大学招生宣讲的通知','上海交通大学大学生创新计划项目结题成绩公告 教务处通知 [2013] 146 号 2013年11月27日，我校组织专家对第六期校级“大学生创新实践计划”（包括已升级为第六期“国家级大学生创新创业训练计划”创新训练项目、第五期上海市级的项目和前期未结题的校级创新项目）项目进行了结题答辩验收。经项目组申请，学院初审，全校共有161个项目取得学校答辩验收资格（不含医学院），其中“国家级大学生创新创业训练计划”创新训练项目36项，“上海大学生创新活动计划”项目54项，“上海交通大学大学生创新实践计划”项目71项，参加答辩学生共499名。 经专家评审，学校领导组终审，此次申请结题验收的161个项目中，131项项目成绩评定为“通过”，48项项目成绩评定为“优秀”， 项目优秀率为29.8%；船舶海洋与建筑工程学院1项目组未参加答辩；机械与动力工程学院1项目组未在规定时间提交齐答辩材料，学校决定以上2个项目做验收不通过处理，并收回全部已下拨经费。499名学生取得大学生创新实践计划成绩和学分，其中163名学生成绩优秀，占获得成绩学生总数的32.6％。按照《上海交通大学大学生创新计划项目管理办法》有关规定，所有通过项目验收的学生成绩和学分记入《上海交通大学学生学习成绩大表》。同时，学校将向通过项目验收的学生颁发项目结题证书。                                                                                                                                     教务处 2013年12月5日 附表：上海交通大学大学生创新计划项目结题成绩（2013年11月） 序号 项目编号 项目名称 立项人 指导教师 立项人院系 级别 项目成绩 1 IAP5181 内转塔单点定位FPSO水动力性能在实测、数值分析和模型试验条件下的准确性比较研究 邓凝 胡志强 船舶海洋与建筑工程学院 上海 通过 2 IAP5010 通过扭转获取玻璃钢蜂窝夹层材料剪切刚度性能的研究 都晓慧 乔丕忠 船舶海洋与建筑工程学院 上海 通过 3 IPP6003 复新传统木构建筑的技术研究 聂瑶 王增春 船舶海洋与建筑工程学院 校级 通过 4 IPP6004 利用动态智能压电材料对二维复合材料板破坏的高效识别方法研究 李施宏 乔丕忠 船舶海洋与建筑工程学院 ','2013-12-05 09:17','2013-12-05 09:17','','','教学信息服务网','2013-12-05 09:17:00.0');
