insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(364403193,'教务处关于增开24小时温馨通宵自习教室的通知','上海交通大学2012年教学成果奖评选结果公示                 教务处通知[2013]145号 我校2012年校级教学成果奖于2012年7月启动，至今已完成院系申报和评选程序。本届成果奖共收到31个院（系）、部处申报成果119项。学校组织专家对申报成果进行了小组初评和大组终评，现拟确定84个项目获2012年校级教学成果奖，其中特等奖20项，一等奖38项，二等奖奖26项。名单详见附件。 本公示有效期为自公示之日起一周。凡对以下名单中获得校级教学成果奖资格有异议者，请与教务处教学改革与质量办公室联系。 联系人：丁洁     电话：34206426-802 e-mail: djie@sjtu.edu.cn 附件：上海交通大学2012年校级教学成果奖获奖公示名单                                  上海交通大学教务处                                       二�一三年十二月三日           上海交通大学2012年校级教学成果奖获奖公示名单 序号 单位 成果名称 成果完成人 奖次 1 体育系 “体教结合”高校高水平运动员创新培养模式的探索与实践 毛丽娟、孙麒麟、刘庆广、刘瑾、王坤、董扬、蔡皿、刘冬华、徐持忍、朱凤军  特等奖 2 教务处、学指委 研究型大学本科实践教学体系的构建与实践 江志斌、郑益慧、朱健、付宇卓、张淑敏、吴益锋、吴静怡、张焰、王敏、杨启 特等奖 3 物理与天文系 课堂教学方法改进与课后教学拓展的研究与实践 胡其图、高景、张小灵、邓晓 特等奖 4 材料科学与工程学院 依托国家重点实验室科研基地，构筑“三融合”型教学平台，培养复合材料创新人才 张荻、范同祥、顾佳俊、吕维洁、苏慧兰、张帆 特等奖 5 医学院 基于PRICE理念以学生综合能力提升为核心的医学教育模式改革 黄钢、陆斌杰、张艳萍、梅文瀚、富冀枫、李鸣燕、马骏、张美娇、李佳静、夏蕙 特等奖 6 医学院 模拟平台结合教学病区，提高临床教学质量的探索与实践 张艳萍、段宝华、张浩、李春红、沈理、黄钢、邵洁、邵莉、李小波、杨云、沈昱仁、李佳静 特等奖 7 上海交通大学 融“知识”、“能力”、“素养”于一体人才培养体系的构建与实践 张杰、江志斌、朱健、杜朝辉、高捷、郑益慧 特等奖 8 研究生院 创新机制更新举措，提高博士生教育质量 黄震、杜朝辉、张国栋、苏剑波、樊琳、张勇、习','2013-12-02 12:45','2013-12-02 12:45','','','教学信息服务网','2013-12-02 12:45:00.0');
