insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1768835092,'2014年本科生UCLA暑期科研项目招募通知','2013年12月全国大学英语六级考试对比考补报名通知 教务处通知〔2013〕144号 各位同学： 2013年12月全国大学英语四六级对比考前期报名于2013年11月18日至20日下午16:00结束，但鉴于本次四级对比考报名名额未满，经学校向全国大学英语四六级考试委员会争取，考委会同意在我校增加六级对比考名额60人，现将六级对比考补报名通知如下，请本校已经报名参加12月六级考试的本科生踊跃报名，先到先得，报完截止。 报名网址：http://ks.sjtu.edu.cn 考试对象：  本校已经报名参加2013年12月六级考试的本科生，六级增加名额60名（具有2013年12月六级准考证）。 考试科目： CET6C 考试时间：2013年12月4日（周三）18：00--19：35 考试地点：中院（闵行校区） 补报名时间：2013年11月21日（周四）中午12：30  ―  2013年11月22日（周五）中午12：00，报完截止。 注： 1、考试请务必带好准考证、学生证、身份证（均原件），缺一不可。 2、由于名额有限，报名同学请务必参加对比考试，谢谢配合。 3、本考试为对比考，需对比参考正式考试成绩，故成绩单发放时间较正式考试顺延（一般在下一次对比考试前期），故无法作为下次正式考试报名成绩依据，特此申明。                        教务处运行管理办公室                           2013年11月20日 相关附件：  ','2013-11-19 15:48','2013-11-19 15:48','','','教学信息服务网','2013-11-19 15:48:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-98672077,'转发人文学院关于比利时鲁汶大学招生宣讲会的通知','上海交通大学大学生创新计划项目结题验收答辩公告 我校第六期校级“大学生创新实践计划”项目于2012年11月正式启动。按项目总体进程安排，已进入结题验收阶段（包括已升级为第六期国家级项目、第五期上海市级的项目和前期未结题的校级创新项目）。经项目组申请和院（系）评审，目前有162个项目（其中“国家级大学生创新创业训练计划”创新项目36项，“上海大学生创新活动计划”项目55项，“上海交通大学大学生创新实践计划”项目71项）获得学校答辩评审资格。2013年11月27日下午，教务处将组织全校性的项目结题验收答辩活动（医学院除外），具体答辩安排参见附件。 请各项目组所有成员提前30分钟到达答辩教室等候答辩（每个项目答辩时间约为17分钟：项目组汇报时间为12分钟，包含项目汇报时间及每名成员1-2分钟独立介绍自己工作的时间。专家提问5分钟，项目汇报时请重点阐述项目组所做的工作以及取得的成果，如有实物，请尽量将实物带至现场进行汇报演示）。各项目组PPT演示材料可以按照《项目结题报告》条目内容进行组织，也可以自己组织汇报内容，重点必须介绍项目组所做的工作、取得的成果及每个成员承担的工作（专家会为每名成员独立打分）,请注意掌控时间并请于11月27日12:00-12：30将PPT演示材料拷入答辩教室的电脑“桌面”。（项目组如遇预计答辩时间与上课时间冲突，持院（系）大学生创新工作组盖章的证明在12:50到答辩教室与答辩秘书协商适当调整答辩顺序）。项目成员务必全部到场参加答辩并做陈述，无故缺席者个人成绩以F计入成绩大表（如遇成员出国特殊情况，请各项目负责人务必在11月22日之前将院系盖章的相关情况说明纸质材料交到教务处实践教学办公室备案，并在答辩时由项目组其他成员介绍其工作内容和贡献，逾时将无法获得成绩和学分）。 欢迎全校广大教师和学生参与旁听。对于有意于参与或正在参与大学生创新计划的同学，这是一次难得的观摩和学习的机会。 教务处 2013年11月19日 上海交通大学大学生创新计划项目结题验收答辩安排表 答辩日期:2013年11月27日 组别 答辩顺序 答辩地点 预计答辩时间 项目编号 项目名称 立项人 其他成员 指导教师 立项人院系 1 1 东下院102 13:00-13:17 IPP6033 应用于公共场所的高效臭氧曝气洗手机 吴许祺晨 陆一凡 盛承敏 冯晋田 宋立博 机械与动力工程学院 1 2 东下院102 13:17-13:34 I','2013-11-19 13:56','2013-11-19 13:56','','','教学信息服务网','2013-11-19 13:56:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-982378212,'2014年SAF名校官方寒假短期国际交流项目','关于上海交通大学2012-2013学年第二学期 “985”三期优质课程和全英语课程验收结果的公示 教务处通知[2013]143号 为深化教育教学改革，提升课程建设水平，将“三位一体”人才培养理念贯彻落实到本科教学环节中，结合“985工程”三期本科创新人才培养体系建设，学校于2012-2013学年第二学期继续开展本科优质课程验收和全英语教学课程认定工作。 根据《上海交通大学“985工程”三期优质课程建设验收标准》和《上海交通大学全英语教学课程实施要求》，学校组织专家，通过检查课程教学资料、随堂听课、参考学生评教结果、会议终评等环节，评定出“优质课程”92门、“全英语教学课程”31门。名单请详见附件。 现予以公示，公示有效期为自公示之日起一周。对公示名单有异议者请与教务处教学改革与质量办公室联系。 联系人：刘昭    电话：30206424-805  E-mail: zhaoliu @sjtu.edu.cn        丁洁    电话：34206426-802 E-mail:djie@sjtu.edu.cn 附件1：上海交通大学2012-2013-2学期优质课程验收通过清单 附件2： 上海交通大学2012-2013-2学期全英语教学课程认定结果清单                                                           上海交通大学教务处                                                                     二�一三年十一月十八日 相关附件：   附件1、 上海交通大学2012-2013-2学期优质课程验收通过清单.docx 附件2、 上海交通大学2012-2013-2学期全英语教学课程认定结果清单.docx','2013-11-14 14:52','2013-11-14 14:52','','','教学信息服务网','2013-11-14 14:52:00.0');
