insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1732643078,'安泰经济与管理学院接收2014年校内推免直升研究生申请的通知','2013-2014学年秋季学期二专缴费通知 教务处通知（2013）127号 根据《第二学科学士学位学费缴纳暂行规定》，学生须自行缴纳二专学费，逾期未缴学费者将取消其该学期二专所选课程。在校学生缴纳方式为网上付款，离校或无法进行网上支付的学生可至财务处现场缴纳。 二专学费缴纳时间 2013年10月23日至11月1日 二专学费缴纳方式 在校学生： 登陆http://www.jdcw.sjtu.edu.cn/payment/ 通过网上支付方式完成全额缴费。 （网上缴费用户名为学号，初始密码为本人身份证后六位，无身份证或身份证无效者初始密码为学号。） 离校或无法进行网上支付的学生： 正常上班时间至闵行财务处（新行政楼B125）现场缴费。 特别提醒： 1、          若逾期未缴费被取消课程，原课表该学期二专课程将不再出现，且成绩以无效计。 2、          二专学费以该学期所选课程总学分计，每学分为100元。其中英语、德语、法语、日语、动画专业为每学分120元。 3、          为方便缴费建议同学自行至银行开通网上支付功能。 4、          逾期将不再接受补缴或恢复课程，请各位同学在时间节点内完成学费缴纳。 教务处 2013年10月17日 相关附件：  ','2013-10-17 14:18','2013-10-17 14:18','','','教学信息服务网','2013-10-17 14:18:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-928454861,'2013-2014学年秋季学期2013级选修新生研讨课与通识核心课程通知','关于本学期因大型考试调整自修教室的通知 教务处通知（2013）126号 各位同学： 为方便同学们了解教室使用情况，便于同学们提前安排自己的自修时间和地点，现将本学期一些大型考试教室使用情况通知如下： 考试时间 考试项目 考场地点 封楼时间 10月27日 （8:30-17:30） 在职硕士联考 东上中下院 10月27日 7:00-18:00 11月9日 （9:00-17:30） 学位考试 上中下院 11月9日 7:00-18:00 12月14日 （9:00-17:25） 大学英语四、六级考试 上中下院； 东上中院五栋教学楼 12月14日 7:00-18:00 届时，考试当天的自修教室调整为以下教室： 10月27日，西区：中院1-4楼所有教室； 11月  9日，东区：东上院1-3楼所有教室； 12月14日，东区：东下院1-2楼所有教室； 考试当天以上自修教室开放时间为：7:30-22:30，其他教室均不再开放。请同学们留意以上变化。同时，提醒各位同学自修结束后勿将书籍等物品放置室内，以免遗失。                                                                               教务处教学运行管理办公室                                                                                    2013年10月17日 相关附件：  ','2013-10-17 09:18','2013-10-17 09:18','','','教学信息服务网','2013-10-17 09:18:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1992758174,'上海市西南片联合办学高校学生跨校辅修我校二专开学注册通知','宣讲会时间: 10月21日下午13:45-15:30 宣讲会地点: 闵行校区东中院4-301 欢迎大一理工科专业同学参加！ 中央理工集团4+4项目 1996年，由法国五所最著名的工程研究生大学组成学校联合体，这五所大学校的名称是：里昂中央理工大学、南特中央理工大学、里尔中央理工大学、巴黎中央理工大学、马赛中央理工大学，他们与四所中国合作伙伴大学联合签署了“4+4”强强合作协议，这四所名牌大学分别为：清华大学，上海交通大学，西安交通大学，西南交通大学。 中央理工学院联合体每年从中国这四所大学的大学二年级学生中录取共约40名优秀学生。在法国期间，可以去法国一些著名的企业集团实习，如：阿尔司通公司、法国电信公司和斯纳克玛等都支持参与法国中央理工学院与中国著名大学的合作。 学科设置 里昂中央理工大学：电力工程、力学和能量学、材料科学、经济科学和人文科学、科学计算语言 南特中央理工大学：自动化技术、计算机信息、工业系统与产品开发、材料、力学工程模拟、海洋工程、数字流体力学 里尔中央理工大学：电力工程、力学和能量学、材料科学、经济科学、人文科学、科学计算语言 巴黎中央理工大学：机械、信息、经济 马赛中央理工大学：自动化技术、计算机信息、工业系统与产品开发、材料、力学工程模拟、海洋工程、数字流体力学 教务处 海外事务中心 2013年10月17日 相关附件：  ','2013-10-16 12:49','2013-10-16 12:49','','','教学信息服务网','2013-10-16 12:49:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1695687209,'通识教育核心课程选课手册（2013-2014-1，2013级）','关于开展MOOCs立项研究工作的通知 教务处通知（2013）125号 各院（系）、部（处）： 近年来，高等教育在线教学与学习取得了长足进展。当前，大规模在线开放课程（Massive Open Online Courses，MOOCs）已蔚然成势，众多高校和各种机构展开了对MOOCs的深入研究与实践，相关联盟也纷纷成立。 为迎接大规模在线开放课程的国际挑战，提高我校的教学水平与质量，更好发挥上海交大服务社会的功能，加强我校在学习化社会构建中的地位，经研究决定开展大规模在线开放课程（MOOCs）的立项研究。现将有关事宜通知如下： 一、申报条件 1．凡我校在编人员皆可申报大规模在线开放课程（MOOCs）研究项目，但已立项本科教学改革研究项目却未完成者不得申报。 2．申报者应严格遵守学术规范，不弄虚作假；知悉国内外该领域的前沿和动态，有鲜明的问题意识和创新意识，在研究思路、框架、内容、方法、观点等方面有所突破。 二、申报事项 1．选题应以《MOOCs立项研究课题方向指南》（附件1）为主要依据，可由申报者自己确定研究题目进行申报。 2．申报者须填写《MOOCs立项研究课题申报书》（附件2），并经所在单位负责人审核、签字盖章后，于11月20日前提交教务处教学研究办公室（闵行校区新行政楼B321室），同时将电子表格发送至联系人信箱。逾期不再受理。 3．学校将组织专家对申报项目进行评审，确定立项项目，并公示。 三、项目实施 1．立项资助经费一般为2万元，以“985三期”项目的方式先期下拨一半经费，其余经费结题后下拨。 2.课题研究时间原则上以半年为限，须于2014年7月前提交结题报告。学校将组织专家对结题项目进行评审，评审合格者下拨剩余经费。 如因特殊原因不能按时完成，须提交说明，学校同意后可延期2个月。否则，作未完成处理，不再下拨其余经费。 3．联系人：杨西强。电话：34206481*25；信箱yangxiqiang@sjtu.edu.cn                            上海交通大学教务处                             2013年10月16日 附件： 1．MOOCs立项研究课题方向指南 2．MOOCs立项研究课题申报书 相关附件：   附件1、 附件1 MOOCs立项研究课题方向指南.docx 附件2、 附件2 MOOCs立项研究课题申报书.docx 附件3、 部分相关资料.r','2013-10-16 10:26','2013-10-16 10:26','','','教学信息服务网','2013-10-16 10:26:00.0');
