insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(982749014,'2014年春季香港大学本科生交换项目拟推荐学生名单公示',' 第七期“上海交通大学大学生创新实践计划”项目学期检查答辩公告 为加强项目过程控制，学校将于2013年10月16日下午组织专家对第七期“上海交通大学大学生创新实践计划”项目进行学期检查答辩。答辩分为两部分，项目组PPT汇报7分钟，专家提问5分钟。欢迎全校广大教师和学生参与旁听，对于有意参与大学生创新计划项目的同学（尤其是正在申报第八期“上海交通大学大学生创新实践计划”的项目组），这是一次难得的了解和学习的机会！ 注意事项： 1. 各项目组PPT演示材料（可以按照《学期检查报告》条目内容进行组织，也可以自己组织汇报内容，重点必须介绍项目启动以来项目组所做的工作、取得的成果,请注意掌控时间，最好事先演练）请于10月16日12:00-12：30拷入答辩教室的电脑“桌面”。 2. 各项目组所有成员必须参加答辩，凡不能参加答辩的同学须履行请假手续，项目组成员无故缺席将取消项目组答辩资格。请各项目组根据“预计答辩时间”提前30分钟到达答辩教室。如果项目组答辩时间与课程（或考试）时间冲突，可以持院（系）大学生创新工作组盖章的证明在12:50到答辩教室与答辩秘书协商适当调整答辩顺序。 3. “上海交通大学大学生创新实践计划”采取“宽进严出”的管理方式，学校通过答辩等检查方式对各项目执行情况进行掌控，对执行不力或执行不认真的项目坚决给予淘汰；对进展缓慢、团队协作不佳等的项目暂缓通过学期检查，要求项目进行整改。已升级成为2013年“国家级大学生创新创业训练计划”创新训练项目的校级项目若平均分处于本组后30%，给予降级处理，由其他优秀校级项目递补。所有项目组应对此次检查予以高度重视。 4.此次检查中表现优秀的校级项目将成为2013年“上海大学生创新活动计划”的备选项目。 教务处 2013年10月9日 答辩安排表： 组别 答辩教室 答辩顺序 预计答辩时间 项目编号 立项人 项目名称 指导教师 立项人所在院系 1 东下院103 1 13:00-13:12 201310248008 宋晶 用于磷尾矿闭库的生态材料试验研究 刘宁 船舶海洋与建筑工程学院 1 东下院103 2 13:12-13:24 IPP7099 陈欢 飞秒激光3D微加工技术研究 陈玉萍 物理系 1 东下院103 3 13:24-13:36 IPP7037 王贺 冰箱辅助节能散热系统 李勇 机械与动力工程学院 1 东下院103 4 13:36-13:48 IPP7100 秦大','2013-10-09 11:26','2013-10-09 11:26','','','教学信息服务网','2013-10-09 11:26:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1165349260,'2014年巴黎高科9+9毕业班项目拟推荐学生名单公示','关于讲解《量子力学与统计力学》的通知 为方便通识核心课程《量子力学（A）》的修课同学和其他非物理专业的本科生和研究生自学《量子力学与统计力学》，《量子力学（A）》课程主讲老师将在本学期开始详细讲解教材《量子力学与统计力学》（已付印）。本学期计划将详细讲解前五章即量子力学基本原理和量子体系基础，具体讲授时间地点如下： 学    期：2013-2014学年第1学期（即本学期） 起始周次：第6周至第16周，共11周     讲授时间：星期一 上午8：00---12：00                星期四 上午8:30---12:30     讲授地点：中院512 教室 主讲老师欢迎有兴趣者有空时自由前去听课！上述时间安排偶有变动，请登陆学校课程中心网站后，访问站名为“理工科量子力学”的课程网站，点击“教学通知”，然后在“2、2013年下半年”的“（3）通知”栏目中点击相应周次进行查核。              物理与天文系量子力学课程组  通识核心课程《量子力学（A）》教学小组                                        2013.10.09 相关附件：  ','2013-10-08 13:19','2013-10-08 13:19','','','教学信息服务网','2013-10-08 13:19:00.0');
