insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2039025082,'2013年上海交通大学本科生创新成果展“我最喜欢的大学生创新项目”和“优秀论文”评选结果公告','如何确保2013年12月全国大学英语四六级考试报名成功 各位同学： 2013年12月全国大学英语四六级考试报名正在进行中，本次报名的缴费系统进行了改版，为使同学们报名成功，请大家在网上支付报名费后，等待付费页面跳转至商户后，再关闭当前支付连接。 一旦有同学发现支付系统显示付费成功信息，但报名系统还没有付费成功记录时，请同学再次点击选定的考试类型，付费成功信息就会同步显示。同时，请同学们在系统开放的报名时间内及时报名，以免错过参加本次考试的机会。                                                                        教务处教学运行管理办公室                                                                            2013年9月13日 相关附件：  ','2013-09-13 09:30','2013-09-13 09:30','','','教学信息服务网','2013-09-13 09:30:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1075956846,'通识教育核心课程立项建设项目公示','为了培养人才、服务教学，增加大学生学习数学的兴趣，中国数学会决定从2009 年开始每年举办一次全国大学生数学竞赛。根据此决定，中国数学会普及工作委员会拟定了2013年数学竞赛的活动方案。竞赛主办单位为中国数学会，全国大学生竞赛委员会负责实施，第五届竞赛由中国科技大学数学学院承办。竞赛分初赛和决赛两个阶段，上海市的参赛者初赛在上海赛区进行。上海赛区组织工作由上海市数学会负责。上海市数学会决定，上海赛区的初赛同时作为第五届上海市大学生数学竞赛,竞赛由上海大学承办。 现将第五届全国大学生数学竞赛初赛暨上海市大学生数学竞赛的有关事宜通知如下： 一、竞赛时间地点和报名方式截止日期 1、竞赛时间： 第五届全国大学生数学竞赛初赛既上海市大学生数学竞赛在2013年10月26日（星期六）上午9：00―11：30举行，决赛于2014年3月份的第三周周六上午在中国科技大学举行。 2、竞赛地点： 上海大学与华东理工大学（具体赛场安排待报名结束后通知）。 3、我校报名方式截止日期： 报名同学请填写附件中竞赛报名表，并于9月21日晚24点之前发到mcmsjtu@163.com。过期不接受报名，个人自行向组委会报名。 二、参赛对象及分组 大学本科二年级或二年级以上的在校大学生。竞赛分为非数学专业组和数学专业组(含数学与应用数学、信息与计算科学专业的学生)。数学专业学生不得参加非数学专业组的竞赛。 三、竞赛知识范围 数学组竞赛初赛知识范围：数学分析（占50%）、高等代数（占35%）、解析几何（占15%）。 非数学组竞赛初赛知识范围：高等数学（以理工科本科教学大纲规定的教学内容为准）。 四、竞赛辅导与培训 辅导讲座有数学分析、高代与解几，高等数学，从下周末开始，具体时间待定，辅导讲座报名同学请填写附件中辅导讲座报名表，并于9月18日晚24点之前发到mcmsjtu@163.com。名额有限。 五、收费事项 每个参赛者须交报名费60 元，由院校汇总后统一交上海赛区组织委员会。其中10 元交全国竞赛委员会，50 元用于分赛区的组织工作。仅参加辅导讲座不需缴费。 我校收费由上海交通大学数学协会负责，只收费一次，定于9月22日晚上5:00-7:00数学系1207室。缴费负责同学：朱旭 13636575985，望各位同学按时缴费，过期不候。 六、奖项 全国竞赛设赛区奖和全国决赛奖。上海赛区的赛区奖又作为上海市大学生数学竞赛优胜奖。 全国赛区的赛区奖（即上海市','2013-09-13 09:02','2013-09-13 09:02','','','教学信息服务网','2013-09-13 09:02:00.0');
