insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(506913763,'关于申报“2013年度市教委重点课程项目”的通知','教务处通知[2013] 93 号 各位同学： 考虑密西根学院学生毕业时间较晚及其他个别学生的特殊需求，教务处拟增加以下出国成绩单、在读证明和学历学位认证办理时间，其他时间不予受理： 时间：2013年8月16日，上午8:30-11:00，下午1:30-4:00 地点：闵行校区新行政楼B112室 受理内容：出国成绩单、在读证明及学历学位认证 详细办理要求和受理范围参见：http://www.jwc.sjtu.edu.cn/web/sjtu/198150-1980000000208.htm（办理出国成绩单及学历学位认证）；http://www.jwc.sjtu.edu.cn/web/sjtu/198150-1980000000202.htm（办理在读证明，限统招在校生） 请有需要的同学根据以上时间前往办理，考虑可能办理的人数会比较多，还请考量好自己的实际需求前往办理。 特别提醒1：如别人代替办理出国成绩单、在读证明和学历学位认证，请出具委托书，以备查； 特别提醒2：下学期办理出国成绩单、在读证明和学历学位认证的时间从9月16号开始。 教务处 注册与学务中心 2013-8-7 相关附件：  ','2013-08-01 08:53','2013-08-01 08:53','','','教学信息服务网','2013-08-01 08:53:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(910215268,'2013年上海交通大学大学生英语竞赛获奖名单','关于取消2013级及以后年级《计算机文化基础》课程的通知 教务处通知[2013] 92 号 各院（系）： 为了秉承我校“起点高、基础厚、要求严、重实践、求创新”的办学传统，顺应信息技术发展形势和我校本科人才培养新需要，学校决定从2013级开始，取消本科培养方案中的《计算机文化基础》零学分课程。 随着信息技术的不断发展，我校本科生入学时的计算机水平已有了明显提高，且在入校后的各类课程学习过程中，学生参与课堂讨论、汇报等的机会增多，在日常课程学习过程中已有足够机会完成计算机基础知识与基本技能的训练。同时，学校本科各专业已将必修修读的计算机基础类课程纳入培养计划当中；学校还加强了通识核心课程的建设，在自然科学与工程技术模块设置了计算机类通识课程供全校学生选修。原有本科生培养方案中设置的《计算机文化基础》零学分课程的考试内容及水平已不符合我校人才培养需要。 在广泛征求院（系）及学生意见基础上，经提请校教学改革与建设指导委员会审议通过，取消本科生培养方案中的《计算机文化基础》零学分课程，即不再安排相关考试，学生毕业不再与是否通过该课程的考试相关联。该决议从2013级开始执行，请各院（系）教学主管院长及教学秘书通知专业负责人，并做好培养方案调整后的相关工作。 特此通知。                                              教务处                                          2013年7月30日 相关附件：  ','2013-07-31 16:13','2013-07-31 16:13','','','教学信息服务网','2013-07-31 16:13:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1854384862,'关于2013届“上海交通大学优异学士学位论文”评选的通知','关于2013年上海交通大学优秀教师奖、烛光奖 获奖人选的公示 教务处通知[2013]91号 根据《上海交通大学优秀教师奖评选办法》及《2013年校级“优秀教师奖”及“烛光奖”评选工作通知》，学校于2013年4月28日下发通知，启动了2013年优秀教师奖及烛光奖的评选工作。 学校组织评审专家进行了网上评审和终评会评审，其中宝钢优秀教师奖共有11名候选人上终评会答辩。 根据评审结果，拟推荐吴静怡、李志勇、徐向华、毛丽娟四位教师为我校申报宝钢优秀教师奖候选人，其中吴静怡老师为宝钢优秀教师奖特等奖候选人；拟推荐宋晓冰、李铸国等102名教师获校优秀教师奖（含医学院单独组织评选出的10名教师）；拟推荐黄永华、闫晓梅等30名教师获烛光奖。具体获奖名单见附件。 特此公示。如有异议者，请与教务处教学改革与质量办公室联系，公示期自公示之日起一周。 联系方式：zhaoliu@sjtu.edu.cn 教务处 二�一三年七月三十一日 相关附件：   附件1、 2013年上海交通大学优秀教师奖获奖名单.doc 附件2、 2013年上海交通大学烛光奖获奖名单.doc','2013-07-29 10:58','2013-07-29 10:58','','','教学信息服务网','2013-07-29 10:58:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1494320440,'创新与创业第六讲: 创业感悟','关于调整2013级及以后年级个性化教育模块学分的通知 教务处通知[2013] 090 号 各院（系）： 我校自2009级本科生开始，实施新版培养方案，方案彰显了加强通识、突出个性化培养的特色，但因总学分仍较高，学生课业修读压力过大，个性化教育课程未真正起到个性化培养的功能，难以真正给予学生自由发展空间。鉴于此，根据我校本科培养方案整体结构情况，在广泛征求院（系）及学生意见基础上，经提请校教学改革与建设指导委员会审议通过，从2013级开始，将含有20学分个性化教育模块本科专业的个性化学分由20学分调整至10学分。请各院（系）教学主管院长及教学秘书告知专业负责人，并做好培养方案调整后的相关工作。 特此通知。                                              教务处                                          2013年7月22日 相关附件：  ','2013-07-24 10:25','2013-07-24 10:25','','','教学信息服务网','2013-07-24 10:25:00.0');
