insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1852723300,'第24期PRP选项报名通知','2013年上海交通大学本科生创新成果展 “我最喜欢的大学生创新项目”和“优秀论文”评选结果公告 教务处通知〔2013〕 89号 根据上海交通大学2013 本科生创新成果展活动安排，学校开展了“我最喜欢的大学生创新项目”和 “优秀论文”评选活动。通过广大师生的积极参与和热情推选，评选出船舶海洋与建筑工程学院“柔性膜穹拱的快速搭建”（立项人：刘德扣）等10个项目为2013本科生创新成果展“我最喜欢的大学生创新项目”；机械与动力工程学院“微型复合抛物面聚光平板型集热器的研究”（作者：劳麒翰）等10篇论文为2013本科生创新成果展“优秀论文”。特此公告。 附表1：2013本科生创新成果展“我最喜欢的大学生创新项目” 附表2：2013本科生创新成果展“优秀论文”                                      上海交通大学教务处 共青团上海交通大学委员会 上海交通大学材料科学与工程学院 2013年7月22日 附表1：2013年本科生创新成果展“我最喜欢的大学生创新项目” 序号 项目名称 立项人 指导教师 所在学院 1 柔性膜穹拱的快速搭建 刘德扣 宋晓冰 船舶海洋与建筑工程学院 2 双齿轮机构的可重复使用高分辨率磁性白板---绿色白板 闫畅迪 邵华 机械与动力工程学院 3 基于语音头像识别、位置服务及社交网络的移动身份验证应用的开发 范雨晨 俞凯 电子信息与电气工程学院 4 电沉积制备铂纳米催化剂的支持电解质改良 杜欣童 钟澄 材料科学与工程学院 5 电化学方法生长半导体氧化物的研究 陈徐 钟澄 材料科学与工程学院 6 基于光致等离子体特征分析的高功率激光焊接过程稳定性监测系统 洪蓝青 蔡艳 材料科学与工程学院 7 功能性纳米凝胶的制备及其细胞标记与成像中的应用 吴越 窦红静 材料科学与工程学院 8 微波热解制备生物炭堆肥中污染物的治理 王瑞 沈国清 农业与生物学院 9 创意都市背景下设计端和消费者端对文化商品的认知研究 龙嘉裕 韩挺 媒体与设计学院 10 培训级模拟飞行驾驶舱 张琦 傅山 航空航天学院 附表2：2013年本科生创新成果展“优秀论文” 序号 论文名称 论文作者 指导教师 所在学院 1 微型复合抛物面聚光平板型集热器的研究 劳麒翰 李勇 机械与动力工程学院 2 回收洗澡水冲厕装置设计 杨霁芳 裴景玉 机械与动力工程学院 3 温差发电用于投影仪散热的理论研究、实验模拟及模型设计 李帮俊','2013-07-22 12:36','2013-07-22 12:36','','','教学信息服务网','2013-07-22 12:36:00.0');
