insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1630248920,'关于2012-2013学年春季学期节假日安排的通知','关于组织第七期“上海交通大学大学生创新实践计划” 项目学期检查的通知 教务处通知〔2013〕79号     根据《上海交通大学“大学生创新实践计划”项目管理办法》的规定，为加强项目过程管理，学校将对第七期“上海交通大学大学生创新实践计划”项目进展情况进行学期检查。现将有关事项通知如下： 一、 时间安排 7月1日-9月6日为项目组自查阶段。请各项目组充分利用夏季学期和暑假时间，积极准备，合理安排，做好项目自查工作，认真填写《上海交通大学IPP项目学期检查报告》，同时登录大学生创新实践网填写学期检查报告，经指导教师审核并签署意见后，于9月13日前交至所在院（系）工作组负责老师。9月23日-9月27日为院（系）组织学期检查时间，要求院（系）工作组对各项目执行情况进行认真评审并排序，并于10月9日之前将学期检查报告交至教务处教学实践办公室并完成网上评审工作。10月16日左右为学校组织统一学期检查答辩时间。 二、 检查内容与要求 1、 检查项目组是否认真填写《学期检查报告》，是否按要求在大学生创新实践网上填写项目日志，提交中期检查材料。 （系统操作详见附件1） 2、 检查项目是否按计划进度组织实施，指导教师是否认真予以指导，项目是否取得初步成效等。 3、 要求项目组及时发现项目实施过程中存在的问题，在指导教师的帮助下分析原因，总结经验，提出解决思路，并进一步明确下一阶段的任务。 4、要求项目组及时总结在项目实施过程中的心得与体会，特别是在创新思维和创新实践方面的体验和收获。 5、项目组成员若发生变更（或项目需要延期），请及时填写项目变更申请表（或项目延期申请表），经指导教师、院系工作组签署意见并盖章后，交至教务处实践教学办公室。学期检查后，不允许项目成员添加。 三、 检查形式 1、各院（系）工作组可采用多种方式进行学期检查（建议采取答辩评审方式），并将检查方式及时报教务处实践办备案。根据检查情况，在《学期检查报告》中“院（系）工作组意见”一栏签署审核意见和院（系）内排序建议，同时请登录大学生创新实践网录入评语，并请于10月9日前将《学期检查报告》纸质版统一交至教务处实践教学办公室。 2、学校将于10月16日左右组织专家对各项目进行公开答辩评审，所有项目全体成员均需认真准备，同时准备好10分钟的PPT演示材料。具体答辩安排另行通知。 四、检查电子材料 1、项目立项人请登录大学生创新实践网，网上填写学期检查表，同时将《学期检查','2013-06-27 17:09','2013-06-27 17:09','','','教学信息服务网','2013-06-27 17:09:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(964990796,'上海交通大学2013-2014学年秋季学期选课通知','上海交通大学2013-2014学年秋季学期本科生注册/报到通知 教务处通知2013 [078]号 各学院（系）：      上海交通大学本科学生注册日定于2013年9月8日（星期日），请各学院（系）通知学生按时到校注册、报到，不允许他人代为报到、注册。具体安排如下： 报到/注册时间： 2013年9月8日（星期日） 上午 8：00―下午17：00 报到/注册地点： 各学院注册报到点（具体地点见附件） 注意事项： 1、学生本人凭校园卡或学生证注册、报到。 2、因病或其它原因不能按时返校的同学，应提前与学院（系）联系请假事宜。请假情况由学院（系）汇总后，于9月8日下午16：30前将请假学生名单导入报到注册系统中。 3、未缴纳2013-2014学年学费的同学，可至财务处（新行政楼B楼一楼125室）缴费。因经济条件等原因不能按时缴费的同学，须先办理报到手续，否则自9月9日起按旷课计。 4、申请复学的学生，先办理复学手续，补缴学费后再凭校园卡办理注册手续。 5、2013-2014学年秋季学期出国交流的学生不需要办理注册、报到手续。 6、非闵行校区的医学院学生请至重庆南路校区注册、报到。 7、闵行校区的留学生请至留学生办公室注册、报到。 8、已缴纳学费的学生在各学院注册报到现场领取注册条，未缴学费的学生在完成交缴费后凭缴费凭证和学生证至学院教务办补领注册条。 注册、报到日学院（系）教务办须安排教师值班 ，处理学生诸如请假等特殊事宜，并安排班主任到学生宿舍了解学生假期思想动态。 二�一三年六月二十七日                                    教务处 附 2013-2014秋季学期各院系报道注册地点 院系 校区 年级 报道注册地点 材料科学与工程学院 闵行 12级-10级 材料学院A楼4003 生命科学与技术学院 闵行 12级-10级 生命药学楼2-119 信息安全学院 闵行 12级-10级 电院1号楼3楼东325信安学院办公室 媒体与设计学院 闵行 12级-10级 媒设学院A103 安泰经济与管理学院 （体育系校外班除外） 闵行 11级、12级 闵行校区老行政楼416 徐汇 10级 法华校区安泰楼303B 体育系校外班 徐汇 12级-10级 徐汇校区体育系体育馆一楼 船舶海洋与建筑工程学院 闵行 12级-10级 木兰船建楼A201 电子信息与电气工程学院 闵行 12级-10级 电信楼群3#楼113，','2013-06-26 15:28','2013-06-26 15:28','','','教学信息服务网','2013-06-26 15:28:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1787371238,'安泰经济与管理学院2014年研究生招生宣讲会','关于2013年夏季学期《研究人员应具备的交流技巧》课程取消通知 学院通知＜2013＞[009] 各位同学： 2013年夏季学期致远学院特聘匹兹堡大学Beth A. Fischer教授开设《研究人员应具备的交流技巧》课程（学分：1；课号：BI047），但因Beth教授近期脚扭伤且需要手术，7月份无法来授课；同时，因为课程的特殊性，目前没有找到合适的老师来代替Beth教授教授《研究人员应具备的交流技巧》课程。为避免造成教学事故，现将2013年夏季学期开设的《研究人员应具备的交流技巧》课程取消。 特此通知！ 致远学院 2013年6月25日 附：课程信息 开课院系 教师姓名 课程 课号 学时 学分 致远学院 Beth A.Fischer 研究人员应具备的交流技巧 BI047 16 1 相关附件：  ','2013-06-26 08:26','2013-06-26 08:26','','','教学信息服务网','2013-06-26 08:26:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1902128616,'上海交通大学关于开展2013年“最受学生欢迎的教师”奖评选工作的通知','上海交通大学关于举办2013年本科生创新成果展的通知 教务处通知2013 [077]号 为丰富夏季小学期大学生科技创新活动，给大学生创新计划项目团队搭建成果展示和交流的平台，我校将于2013年7月20日举办“2013年本科生创新成果展”。现将有关事项通知如下： 一、举办时间，地点 举办时间为：2013年7月20日； 举办地点为：闵行校区新图书馆 二、参与人员 所有参加各级大学生创新计划和本科生研究计划（PRP）项目的师生。本次活动由材料科学与工程学院承办。 三、系列活动与要求 （一） 举办大学生创新计划成果展 1、本届成果展将以展板结合实物+作品+演示等方式集中展示我校各级大学生创新计划项目典型案例和成果（具体要求见附件1）。 2、通过现场投票的方式评选出10个“我最喜爱的大学生创新项目”。 凡获得 “我最喜爱的大学生创新项目”且未结题的项目，项目结题所有成员考核成绩提高一个等第。 3、请参与成果展的项目负责人于7月7日之前将制作完毕的展板材料上传至邮箱：2942008438@qq.com（具体要求见附件1），学校将组织专家对展示材料评选，择优选取部分项目进行展示。 学校要求所有第六期“上海交通大学大学生创新实践计划”项目组必须提交项目展示材料并作为各项目结题考核材料之一。鼓励第七期“上海交通大学大学生创新实践计划”项目组积极参与。 （二） 召开大学生创新计划学术交流会议 1、本届成果展将举办项目论文学术汇报会，入选论文按学科类别分小组进行论文学术交流，同时聘请专家对学术论文进行点评（具体要求见附件2）。 2、由专家评选10篇“本科生创新成果展”优秀论文。 凡获得 “本科生创新成果展”优秀论文且未结题的项目，论文署名成员个人考核成绩提高一个等第。 3、请参与学术交流会议的学生于7月7日之前将相关论文发送到邮箱：2942008438@qq.com。鼓励所有参加各级大学生创新计划和本科生研究计划（PRP）的学生积极参与。 为了在本次成果展上充分交流我校大学生创新计划项目成果，请所有参加各级大学生创新计划和本科生研究计划（PRP）的师生踊跃参与，以展现我校大学生的创新思维和创新能力。请各院（系）大学生创新计划工作组精心组织、认真实施，确保活动各环节正常进行，共同确保创新成果展圆满成功。 联系人：叶定剑、田夏 电  话：54747664 、34206429―8003 邮  箱：2942008438@qq.com  prp','2013-06-25 17:09','2013-06-25 17:09','','','教学信息服务网','2013-06-25 17:09:00.0');
