insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1697495100,'2012级电院自主调整专业面试安排','上海交通大学大学生创新计划项目结题成绩公告 教务处通知〔2013〕 41号 2013年4月24日，我校组织专家对第五期校级“大学生创新实践计划”（包括已升级为第五期“国家级大学生创新创业训练计划”创新训练项目、第五期上海市级的项目和前期未结题的校级创新项目）项目进行了结题答辩验收。经项目组申请，学院初审，全校共有197个项目取得学校答辩验收资格（不含医学院），其中“国家级大学生创新创业训练计划”创新训练项目65项，“上海大学生创新活动计划”项目71项，“上海交通大学大学生创新实践计划”项目61项，参加答辩学生共648名。 经专家评审，学校领导组终审，此次申请结题验收的197个项目中，134项项目成绩评定为“通过”，56项项目成绩评定为“优秀”， 项目优秀率为29.3%；密西根学院1项项目评定为验收答辩“不通过”，电子信息与电气工程学院1项目组未参加答辩；材料科学与工程学院、国际与公共事务学院、媒体与设计学院、密西根学院、致远学院各1项目组未在规定时间提交齐答辩材料，学校决定以上7个项目做验收不通过处理，并收回全部已下拨经费。其中，643名学生取得大学生创新实践计划成绩和学分，其中203名学生成绩优秀，占获得成绩学生总数的31.6％。按照《上海交通大学大学生创新计划项目管理办法》有关规定，所有项目通过验收学生成绩和学分记入《上海交通大学学生学习成绩大表》。同时，学校将向通过项目验收的学生颁发项目结题证书。                                                                                                                           教务处 2013年5月7日 附表：上海交通大学大学生创新计划项目结题成绩（2013年5月） 序号 项目编号 项目名称 立项人 指导教师 院系 级别 项目成绩 1 IPP5001 上海软土小应变试验 陈超斌 叶冠林 船舶海洋与建筑工程学院 校级 通过 2 201210248001 堆积体室内土柱渗流实验 黎瀚文 张璐璐 船舶海洋与建筑工程学院 国家 通过 3 201210248002 利用水生植物同时实现人工湖水体净化和景观美学设计 石晋豪 袁涛 船舶海洋与建筑工程学院 国家 通过 4 201210248003 一种新型风扇增升式风帆 覃鑫生 张裕芳 船舶海洋与建筑工程学院 ','2013-05-07 10:54','2013-05-07 10:54','','','教学信息服务网','2013-05-07 10:54:00.0');
