insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1872924120,'关于“冷战与中国”课程上课教室变更的告知','2013年第十届OAPS学生优秀学术论文征集工作接近尾声，最终44篇优秀论文脱颖而出。为促进同学间的交流、师生间的分享，进一步提高大家的论文写作能力，同时为下一届OAPS论文征集打好基础，日前，学校图书馆联合教务处特举办交流沙龙。图书馆馆长陈进、教务处副处长郑益慧，评审代表国际与公共事务学院副院长章晓懿、电子信息与电气工程学院副教授陈彩莲，与到场的30多位同学畅谈学术论文撰写心得和技巧。 陈进首先介绍了OAPS活动的背景及上海交大加入OAPS项目的历程以及在其中发挥的重要作用，他从我校“三位一体”的育人理念出发，指出OAPS项目在促进本科生走近科研、总结和凝练科研成果、提升写作水平、营造积极活跃的创新学术氛围等方面的重要意义。 郑益慧从自身的求学经历讲起，追忆探索创新的艰苦历程，感叹当今大学生优越的学习环境。他指出，学校的大学生创新计划（“大创”）和本科生研究计划（“PRP”）等项目都是支撑学生不断成长的优秀平台，希望大家好好利用，在未来的科研中发扬主动学习的精神。他还表示OAPS项目能够积极引导同学们总结成果，促使其发现论文撰写并不神秘，科研成果就在眼前。 来自机械与动力工程学院的杨元辉和材料科学与工程学院的杜欣童，与大家分享了参与OAPS项目的亲身感受和经验。杨元辉结合自身特长，介绍了Latex软件的排版特点和图片排版技巧，同时表示论文撰写要有积极向上的态度。杜欣童详细介绍了科研论文撰写的几大步骤，特别指出图书馆文献资源和信息素养教育课程的重要作用，全方位多角度的阐述了对待科研要有严谨的态度。此外，两位同学均表示团队合作在促进互补、能量富集等方面具有重要意义。 章晓懿肯定了同学们在论文写作方面的实力，指出不管是自然科学还是社会科学都是发现问题、解决问题的过程，研究中要善于找到感兴趣且较容易的切入点，她还从“题目、方法论、逻辑关系、知识面、规范性”等五大方面阐述了论文评审的要点，助力同学撰写优秀论文。陈彩莲讲述了自己多年带领同学完成“大创”、“PRP”等项目的经验，分享了多次评审OAPS论文的感受，强调了“团队合作、反复讨论、多次修改”的重要性，并鼓励大家将参加OAPS的优秀成果进一步提升和凝练，投稿国际期刊。 OAPS项目所倡导的鼓励本科生走进实验室、积极参与研究和实践、撰写学术论文、拓展学术视野、增强实践创新能力，无疑会对实现个人价值和梦想，培养各领域具有创新精神和领导能力的人才起到重要的支持作用。 OAP','2013-04-22 09:15','2013-04-22 09:15','','','教学信息服务网','2013-04-22 09:15:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-481949759,'2013暑期课程手册','   2013年4月15日晚，上海交通大学第三届智能车竞赛在电院IEEE试点班实验室举行。本次比赛由校智能车创新工作室承办。自动化系杨明老师、王冰老师、电院团委宋续明老师以及校智能车创新工作室的主要成员出席了竞赛并担任评委。     智能车比赛是以迅猛发展的汽车电子为背景，涵盖了控制、模式识别、传感技术、电子、电气、计算机、机械等多个交叉学科的科技创新比赛。此次竞赛是面向上海交通大学全体学生的综合性科技实践活动。该活动致力于激发同学们的创新精神，培养同学们的创新实践能力，丰富校园文化生活，同时也为同学们提供一个实现自己梦想的机会。本次比赛分为光电组、电磁组和摄像头组。基于不同原理设计出来的赛车在接下来的比赛中进行了激烈的角逐。此次比赛从2012年5月开始组织报名，吸引了来自电信、机动、船建、密西根等学院的60多支队伍报名，其中近150名同学参加了相关课程的学习。 在本次比赛中，比赛跑道分为顺时针和逆时针两个方向，选手们每个方向有3次机会。经过了3个小时的精彩比拼，3支队伍获得一等奖，3支队伍获得二等奖，6支队伍获得三等奖，另有5支队伍获得优胜奖，详细获奖名单见附件。本次竞赛获得一等奖和二等奖的6支队伍将有机会代表我校参加全国大学生智能车竞赛华东区比赛。 这次智能车竞赛的圆满举行不仅使参赛学生自主创新能力得到了提高，而且在比赛中展现了交大学子不怕吃苦、乐于钻研、顽强拼搏的精神和强烈的团队合作意识。目前，此项赛事己经成为交大展示科研成果和学生实践能力的重要途径，同时也为社会选拔优秀的创新人才提供了重要平台。 第四届智能车竞赛组织工作近期即将启动，比赛预计将于今年12月份举行。欢迎有兴趣的全校各院系同学选修今年秋季的《工程实践与科技创新Ⅳ-D》（EI229）课程。 作者：电院科协 校智能车创新工作室 2013年4月22日 附：获奖名单 奖项 组号 队名 队员 学号 一等奖 M09 Peano 刘成志 5110309716 一等奖 M09 Peano 钱起扬 5120309158 一等奖 M09 Peano 楼畅 5120309080 一等奖 O10 CyberDark 吕哲鸣 5100309285 一等奖 O10 CyberDark 赵翔 5100309263 一等奖 O10 CyberDark 袁进强 5100209222 一等奖 C01 老师说名字长显得霸气队 冯倩玉 5110309723 一等奖 C01 老师说名','2013-04-20 10:30','2013-04-20 10:30','','','教学信息服务网','2013-04-20 10:30:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1894194042,'创新与创业第二讲：创新创业与成功','各位同学： 以色列理工学院summer school于2013年7月31日到8月30日在海法举行，对中国学生提供全额奖学金，欢迎上海交通大学学生申请。 招募对象：优秀的本科生和研究生（BSc，B-TECH, MSc， PHD） 奖学金额度：获得奖学金的学生可免学费、住宿费，获得学校食堂的免费午餐（一周五天）和一个月$250的生活费。 课程安排：电子和计算机工程，机械工程，航天航空学院和数学领域。     详细信息和要求请见附件。 项目申请方式：根据项目说明自行申请。 最终被录取的学生，请务必办理好学校的审批手续，相关提醒见：http://youxue.jwc.sjtu.edu.cn/index.php/archives/2269。 教务处 注册与学务中心 2013年4月20日 相关附件：   附件1、 Technion Summer School - China.pdf','2013-04-19 10:44','2013-04-19 10:44','','','教学信息服务网','2013-04-19 10:44:00.0');
