insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(958714559,'关于启动2012-2013学年第二学期全英语教学课程认定的通知','上海交通大学关于组织申报2013年上海高校本科重点教学改革项目的通知 教务处通知〔2013〕29号 各学院（系） 为贯彻落实国家和上海市中长期教育改革和发展规划纲要的精神，深化本科教育教学改革，以科学的研究成果指导改革实践，今年上海市教育委员会决定继续组织开展上海高校本科重点教学改革项目的申报工作。为此，学校将组织开展上海高校本科重点教学改革项目申报和推荐工作。现将有关事项通知如下： 1.     申报内容和条件详见上海市教委通知（附件1）。 2.     请各院（系）组织教师积极申报，于4月28前日前将各项目申请表（附件2）和汇总表（附件3）的电子文档发送至hmli@sjtu.edu.cn，申报表纸质文档（一式三份）交教务处教学改革与质量办公室。联系人：李红梅；联系电话：34206426-801；联系地址：闵行校区新行政楼B323。 3.     学校定于5月2日组织专家对申报项目进行评审遴选，选定项目将于5月6日报上海市教育委员会。 上海交通大学教务处 教学改革与质量办公室二�一三年四月十五日 相关附件：   附件1、 附件1-上海市教育委员会关于申报教改项目的通知.doc 附件2、 附件2-申报表.doc 附件3、 附件3-院系申报汇总表.doc','2013-04-15 16:46','2013-04-15 16:46','','','教学信息服务网','2013-04-15 16:46:00.0');
