insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(391340521,'2012-2013学年春季学期重修免修考考试安排发布通知','2013年第十届OAPS学生会议论文征集工作接近尾声，感谢来自全校各院系同学的参与和广大评审老师的积极配合。为促进同学间的交流、师生间的分享，进一步提高大家的论文写作能力，同时为下一届OAPS论文征集活动打好基础，图书馆联合教务处将举办关于此次论文征集活动的交流沙龙，具体安排如下： 活动名称 《第十届OAPS学生会议论文》交流沙龙 活动日期 2013年04月19日 上午 活动地点 上海交通大学闵行校区 图书馆主馆E401会议室 活动议程 时    间 具体条目 内容说明 9:30-10:00 领导发言 教务处郑益慧副处长 图书馆陈进馆长 10:00-10:20 学生代表发言 机械与动力工程学院 杨元辉 材料科学与工程学院 杜欣童 （分享论文撰写经验、论文修改的心路历程 参与OAPS活动的心得体会） 10:20-11:00 评审老师代表发言 电子信息与电气工程学院 陈彩莲 国际与公共事务学院 章晓懿 （评审老师分别从“如何撰写好的论文尤其是英文文章”、  “OAPS论文评审感想”等方面进行现场指导） 11:00-11:15 互动交流 学生自由提问，评审老师现场答疑 11:15-11:25 颁发证书 为所有文章被录用的同学颁发证书 11:25-11:30 合影 在场全体师生 【友情提醒】活动中将为所有文章被录用的同学颁发证书，请相关同学务必出席！ 此次交流沙龙有利于同学们对参与大学生创新计划项目的研究成果汇总，形成优秀原创性论文，同时利于英文文章撰写能力的提升，故为一次难得的交流和学习机会，欢迎广大同学积极参加！ 2013年4月10日 图书馆、教务处 相关附件：  ','2013-04-09 19:37','2013-04-09 19:37','','','教学信息服务网','2013-04-09 19:37:00.0');
