insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1648459362,'关于下发2012-2013学年第一学期本科教学研究生助教（岗）额度的通知','2011年度上海交通大学特色实验项目验收成绩公告 教务处通知〔2012〕076号 “2011年度上海交通大学特色实验项目”于2011年9月立项申报，经专家评审和学校审核，共立项建设37项。2012年9月26日，学校组织专家对该期全部建设项目和尚未通过验收的1个“2009年度上海交通大学特色实验项目”进行了集中答辩验收。 参与本次结题验收答辩的项目中，共有34个项目通过结题验收，其中7个项目被评为优秀。另有4个项目，因实验内涵建设不明确，实验特色不明显，不符合本科生实验要求或无实验开设计划等原因被专家评为暂缓通过验收。对于暂缓通过验收的项目，学校要求各项目负责人加大投入，并根据专家意见积极整改，按时参加学校后续安排的补答辩验收。 附件：2011年度上海交通大学特色实验项目验收成绩 教务处                                              2012年10月12日 相关附件：   附件1、 2011年度特色实验项目验收成绩.xls','2012-10-11 17:41','2012-10-11 17:41','','','教学信息服务网','2012-10-11 17:41:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(705410077,'创新与创业第一讲：商业银行与中小（微）企业授信','重点提醒： 1、获得推免直升资格的直硕生（包含所有直升校内外的学生）注意：后续直升工作流程包括（1）凭校验码网上（http://yz.chsi.com.cn）报名；（2）填写交回登记表；（3）网上确认并缴费（直升校外的学生根据对方学校的提醒进行网上确认或现场确认）三个环节，缺一不可，否则报名无效。 2、校内直博生后续工作流程包括1、填写交回登记表2、据研究生院通知至本校研究生院网站进行网上报名确认并缴费（详见yzb.sjtu.edu.cn）。 3、直升校外的直博生根据录取学校的通知办理相关手续。 一、   领取教育部推免生登记表和网报校验码 直升本校各学院（含跨学院、临床4＋4、直硕直博）学生（针对录取工作已经结束的学院）                           各学院请于2012年10月17日前到教务处注册与学务中心领取直升校内学生的校验码（限直硕生）和教育部推免生登记表（表格如填错、毁坏，后果自负）。     学生请于10月17号之后根据所在学院的通知领取表格和校验码（限直硕生），办理网上报名、网上确认等手续，表格填写完整后按照学院规定的时间交回学院教务办。 学院请将学生填写完整的表格汇总后于11月6日前交至教务处注册与学务中心。 备注：直博生不需要领取校验码进行网上报名，填写完整的教育部推免生登记表交至录取学院，不需交回教务处。 直升外校学生（含校外直硕、校外直博） 学生凭对方学校的录取函（或拟录取信函）原件和证件（学生证或校园卡）于10月12号下午（计划内名额部分）、10月17号全天（计划内名额部分）、10月19号全天（其他）或10月26号下午到教务处注册与学务中心领取校验码（限直硕生）和教育部推免生登记表（含直硕和直博生，表格如填错、毁坏，后果自负），进行网上报名。 网上报名后，将填写完整的教育部推免生登记表根据对方的要求寄给对方学校，成绩单因为申请时已经寄过，可以询问对方学校后确定随登记表寄或不寄。 二、    推荐免试研究生网上报名须知（适用于直升校内和校外所有直硕生）    所有获得推免直升资格的学生必须持教务处下发的校验码在10月31日前登陆http://yz.chsi.com.cn 或 http://yz.chsi.cn网站进行网上报名。网上报名时报考点选择： a)   被我校接收的学生报考点选择上海交通大学（3105）； b)   被上海其它招生单位接收的学生按照上海市教育考试院','2012-10-11 16:57','2012-10-11 16:57','','','教学信息服务网','2012-10-11 16:57:00.0');
